INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `SoLanXem`, `NoiBat`, `AnHien`, `tags`, `Duyet`, `NguoiDang`) VALUES
(1, 'vi', 'SẮC ĐẸP LÀ VŨ KHÍ QUYỀN LỰC CỦA PHỤ NỮ HIỆN ĐẠI', 'Một nhà văn đã nói “Đàn bà đẹp luôn có quà” như một minh chứng về người phụ nữ có nhan sắc sẽ hạnh phúc hơn, thành công hơn trong cuộc sống. Đã qua rồi cái thời chị em đầu tắt mặt tối, lo chuyện bếp núc mà chả màng quan tâm đến vẻ ngoài của mình. Thời nay phụ nữ ra ngoài làm việc, giao lưu tiếp xúc và tự khẳng định giá trị của mình cho nên PHỤ NỮ LÀ PHẢI ĐẸP', 'uploads/images/pexels-jasmin-chew-6121448.jpg', '2021-06-02', 3, '<h1>SẮC ĐẸP L&Agrave; VŨ KH&Iacute; QUYỀN LỰC CỦA PHỤ NỮ HIỆN ĐẠI</h1>\r\n\r\n<p><img alt=\"Sắc đẹp là vũ khí quyền lực của phụ nữ hiện đại\" src=\"https://vienthammyoracle.vn/data/news/2023/h1-sac-dep-la-vu-khi.jpg\" style=\"height:419px; width:650px\" /></p>\r\n\r\n<p>Một nh&agrave; văn đ&atilde; n&oacute;i&nbsp;<em>&ldquo;Đ&agrave;n b&agrave; đẹp lu&ocirc;n c&oacute; qu&agrave;&rdquo;</em>&nbsp;như một minh chứng về người phụ nữ c&oacute; nhan sắc sẽ hạnh ph&uacute;c hơn, th&agrave;nh c&ocirc;ng hơn trong cuộc sống. Đ&atilde; qua rồi c&aacute;i thời chị em đầu tắt mặt tối, lo chuyện bếp n&uacute;c&nbsp;m&agrave; chả m&agrave;ng quan t&acirc;m đến vẻ ngo&agrave;i của m&igrave;nh. Thời nay phụ nữ ra ngo&agrave;i l&agrave;m việc, giao lưu tiếp x&uacute;c v&agrave; tự khẳng định gi&aacute; trị của m&igrave;nh cho n&ecirc;n PHỤ NỮ L&Agrave; PHẢI ĐẸP.</p>\r\n\r\n<p>V&igrave; phụ nữ đẹp lu&ocirc;n thu h&uacute;t v&agrave; nhận được sự ch&uacute; &yacute;, tr&acirc;n trọng, ưu &aacute;i của mọi người xung quanh. Bất kỳ chị em n&agrave;o cũng c&oacute; n&eacute;t duy&ecirc;n ri&ecirc;ng, v&igrave; vậy m&agrave; h&atilde;y biết c&aacute;ch gi&uacute;p m&igrave;nh đẹp hơn, tự tin hơn. Đ&acirc;y cũng một yếu tố gi&uacute;p bạn trở th&agrave;nh nam ch&acirc;m thu h&uacute;t nhiều cơ hội, bao gồm cả cơ hội trong sự nghiệp cũng như trong t&igrave;nh y&ecirc;u. Thậm ch&iacute;, việc vợ l&agrave;m đẹp c&ograve;n l&agrave; niềm tự h&agrave;o cho c&aacute;c đấng lang qu&acirc;n v&igrave; &ldquo;đ&agrave;n &ocirc;ng gi&agrave;u v&igrave; bạn sang v&igrave; vợ&rdquo;</p>\r\n\r\n<h2><strong>Trong c&ocirc;ng việc</strong></h2>\r\n\r\n<p>Trong c&ocirc;ng việc bạn thường gặp phải những trường hợp người c&oacute; kỹ năng k&eacute;m hơn bạn nhưng vẫn được ưu &aacute;i v&agrave; được trả lương cao hơn bạn. Bạn đ&atilde; bao giờ tự hỏi rằng, tại sao? Rất nhiều trường hợp như thế xảy ra trong cuộc sống, v&agrave; đa số nguy&ecirc;n nh&acirc;n l&agrave; do vẻ ngo&agrave;i. Điều n&agrave;y kh&ocirc;ng c&oacute; nghĩa phụ nữ xấu sẽ kh&ocirc;ng bao giờ c&oacute; cơ hội, tuy nhi&ecirc;n, chắc chắn một điều rằng phụ nữ kh&ocirc;ng biết l&agrave;m đẹp cho bản th&acirc;n sẽ lu&ocirc;n gặp kh&oacute; khăn hơn.</p>\r\n\r\n<p><img alt=\"Sắc đẹp là vũ khí quyền lực của phụ nữ hiện đại\" src=\"https://vienthammyoracle.vn/data/news/2023/h2-sac-dep-la-vu-khi.jpg\" style=\"height:566px; width:850px\" /></p>\r\n\r\n<h2><strong>Trong cuộc sống</strong></h2>\r\n\r\n<p>Người ta vẫn thường n&oacute;i&nbsp;<em>&ldquo;Tốt gỗ hơn tốt nước sơn&rdquo;</em>, tuy nhi&ecirc;n c&oacute; một thực tế rằng rất &iacute;t người hoặc thậm ch&iacute; l&agrave; chẳng ai muốn để &yacute; đến một m&oacute;n đồ mang vẻ ngo&agrave;i k&eacute;m bắt mắt d&ugrave; thật sự chất lượng của n&oacute; rất tốt. V&agrave; một c&ocirc; g&aacute;i c&oacute; vẻ ngo&agrave;i xinh đẹp rạng rỡ bao giờ cũng thu h&uacute;t v&agrave; dễ d&agrave;ng g&acirc;y được ấn tượng, thiện cảm với người tiếp x&uacute;c, đặc biệt l&agrave; trong mắt nam giới. Đấy ch&iacute;nh l&agrave; l&yacute; do tại sao phụ nữ kh&ocirc;ng n&ecirc;n xem thường vũ kh&iacute; sắc đẹp của m&igrave;nh.</p>\r\n\r\n<p>Kh&ocirc;ng phải c&ocirc; g&aacute;i n&agrave;o khi sinh ra cũng sở hữu nhan sắc trời ph&uacute; v&agrave; gặp nhiều thuận lợi trong c&ocirc;ng vi&ecirc;c lẫn trong cuộc sống. Do vậy, h&atilde;y t&iacute;ch cực chăm s&oacute;c cho vẻ ngo&agrave;i nhiều hơn v&agrave; bạn sẽ nhận ra quyền năng từ vũ kh&iacute; sắc đẹp của ch&iacute;nh bạn.</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Sắc đẹp là vũ khí quyền lực của phụ nữ hiện đại\" src=\"https://vienthammyoracle.vn/data/news/2023/h3-ha-kieu-anh-1.jpg\" style=\"width:600px\" /></td>\r\n			<td><strong>Hoa hậu Việt Nam H&agrave; Kiều Anh</strong>&nbsp;t&acirc;m sự:&nbsp;<em>&ldquo;Đối với phụ nữ th&igrave; l&agrave;n da, v&oacute;c d&aacute;ng l&agrave; một t&agrave;i sản qu&yacute; gi&aacute;. Với vai tr&ograve; l&agrave; một cựu Hoa h&acirc;u, l&agrave; doanh nh&acirc;n, t&ocirc;i lại c&agrave;ng kh&ocirc;ng thể lơ l&agrave; việc giữ g&igrave;n nhan sắc của m&igrave;nh&rdquo;</em>.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td><strong>B&aacute;c sĩ phẫu thuật thẩm mỹ Phan Minh Ho&agrave;ng (Bệnh viện quận 2)</strong>&nbsp;cho biết:&nbsp;<em>&ldquo;T&ocirc;i đ&atilde; gặp nhiều phụ nữ th&ocirc;ng minh, th&agrave;nh c&ocirc;ng trong sự nghiệp. Tuy nhi&ecirc;n, nhiều người trong số họ mải m&ecirc; sự nghiệp, chăm s&oacute;c gia đ&igrave;nh m&agrave; qu&ecirc;n chăm ch&uacute;t bản th&acirc;n n&ecirc;n tự l&agrave;m m&igrave;nh trở n&ecirc;n k&eacute;m hấp dẫn. Đ&acirc;y cũng l&agrave; một trong số nguy&ecirc;n nh&acirc;n l&agrave;m t&igrave;nh cảm giữa những cặp đ&ocirc;i kh&ocirc;ng c&ograve;n nồng n&agrave;n nữa. T&ocirc;i lu&ocirc;n ủng hộ chuyện phụ nữ đi phẫu thuật thẩm mỹ để khắc phục khiếm khuyết v&igrave; điều đ&oacute; sẽ gi&uacute;p chị em đẹp hơn, tự tin hơn v&agrave; hạnh ph&uacute;c hơn. Quan trọng l&agrave; c&aacute;c bạn phải biết t&igrave;m nơi uy t&iacute;n để &ldquo;chọn mặt gửi v&agrave;ng&rdquo;</em>.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td><em><strong>Dr.Wang Jea Kwon &ndash; Trưởng khoa Phẫu thuật thẩm mỹ Oracle H&agrave;n Quốc</strong>&nbsp;chia sẻ:&nbsp; &ldquo;Sắc đẹp l&agrave; ch&igrave;a kh&oacute;a th&agrave;nh c&ocirc;ng của phụ nữ. C&oacute; rất nhiều c&aacute;ch để gi&uacute;p chị em xinh đẹp hơn, duy&ecirc;n d&aacute;ng hơn. Đừng để thời gian lấy đi thanh xu&acirc;n của bạn. Một người phụ nữ th&agrave;nh c&ocirc;ng khi hội tụ c&aacute;c yếu tố: sự nghiệp vững v&agrave;ng, gia đ&igrave;nh đầm ấm, cuộc sống hạnh ph&uacute;c... v&agrave; yếu tố kh&ocirc;ng thể thiếu đ&oacute; l&agrave; sắc đẹp, biết l&agrave;m đẹp&rdquo;.</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y xưa, phụ nữ chỉ cần &ldquo;c&ocirc;ng dung ng&ocirc;n hạnh&rdquo; l&agrave; ho&agrave;n hảo. Nhưng b&acirc;y giờ, cấp độ &ldquo;c&ocirc;ng dung ng&ocirc;n hạnh&rdquo; cao hơn nhiều. V&igrave; thế, người phụ nữ cũng phải biết tự &quot;n&acirc;ng cấp&quot; bản th&acirc;n m&igrave;nh về vẻ đẹp. Đẹp từ t&acirc;m hồn, tr&iacute; tuệ đến vẻ ngo&agrave;i rạng rỡ. L&agrave;m đẹp sẽ gi&uacute;p chị em lu&ocirc;n tỏa s&aacute;ng, tự tin khẳng định gi&aacute; trị bản th&acirc;n v&agrave; đ&oacute; cũng thể hiện sự t&ocirc;n trọng người kh&aacute;c trong c&aacute;c cuộc gặp mặt, gi&uacute;p g&acirc;y ấn tượng tốt hơn.</p>\r\n', 1, 0, 0, 0, NULL, 0, 'user'),
(2, 'vi', 'Giá Trị Dinh Dưỡng Của Ức Gà Không Da', 'Ức gà không da là một thực phẩm tốt và là một món ăn được dân gymer ưa chuộng nhất, Trong ức gà chứa rất một nguồn protein rất cao và ít chất béo bão hòa. Bên cạnh đó còn chứa nhiều phốt pho, Niacin , Vitamin B6 và Selen. Nhưng ức gà có một khuyết điểm nhỏ đó là cao Cholesterol.', 'uploads/images/100g-ức-gà-chứa-bao-nhiêu-protein-và-thực-đơn-cho-người-giảm-cân.jpg', '2021-06-02', 3, '<p><strong>Ức g&agrave; kh&ocirc;ng da l&agrave; một thực phẩm tốt v&agrave; l&agrave; một m&oacute;n ăn được d&acirc;n gymer ưa chuộng nhất, Trong ức g&agrave; chứa rất một nguồn protein rất cao v&agrave; &iacute;t chất b&eacute;o b&atilde;o h&ograve;a. B&ecirc;n cạnh đ&oacute; c&ograve;n chứa nhiều phốt pho, Niacin , Vitamin B6 v&agrave; Selen. Nhưng ức g&agrave; c&oacute; một khuyết điểm nhỏ đ&oacute; l&agrave; cao Cholesterol.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<thead>\r\n		<tr>\r\n			<th colspan=\"2\">\r\n			<h4>TH&Agrave;NH PHẦN DINH DƯỠNG</h4>\r\n			</th>\r\n		</tr>\r\n		<tr>\r\n			<th>Khối lượng liều d&ugrave;ng</th>\r\n			<th>100g</th>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Th&agrave;nh phần dinh dưỡng trong mỗi liều</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Năng lượng 110</td>\r\n			<td>Từ fat 11,2</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Nhu cầu hằng ng&agrave;y</th>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất b&eacute;o</strong>&nbsp;1,2g</td>\r\n			<td>2%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- B&eacute;o b&atilde;o h&ograve;a&nbsp;</em>0,3g</td>\r\n			<td>2%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Trans fat</em></td>\r\n			<td>0g</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cholesterol 58mg</td>\r\n			<td>19%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất bột đường 0g</strong></td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Chất xơ 0&nbsp;g</em></td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Đường 0g</em></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Protein 23,1g</td>\r\n			<td>46%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vitamin A0%</td>\r\n			<td>Vitamin C2%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Canxi1%</td>\r\n			<td>Sắt4%</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><small>Ch&uacute; &yacute;: nhu cầu hằng ng&agrave;y dựa tr&ecirc;n t&iacute;nh to&aacute;n 1 người cần 2000 calories 1 ng&agrave;y, nhu cầu hằng ng&agrave;y của bạn c&oacute; thể cao hoặc thấp hơn t&ugrave;y thuộc v&agrave;o lượng calories bạn cần.</small></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4>C&Acirc;N BẰNG NGUỒN NĂNG LƯỢNG</h4>\r\n\r\n<p>CALORIES ĐẾN TỪ PROTEIN&nbsp;<strong>90%</strong></p>\r\n\r\n<p>CALORIES ĐẾN TỪ CARB&nbsp;<strong>0%</strong></p>\r\n\r\n<p>CALORIES ĐẾN TỪ FAT&nbsp;<strong>10%</strong></p>\r\n\r\n<p><strong>Ch&uacute; &yacute;:&nbsp;</strong><small>Những số liệu tr&ecirc;n c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o những sản phẩm cụ thể, c&aacute;ch chế biến, bảo quản. C&aacute;ch chỉ số, chỉ ti&ecirc;u được ch&uacute;ng t&ocirc;i tổng hợp ph&acirc;n t&iacute;ch từ c&aacute;c tổ chức c&oacute; uy t&iacute;n lớn của Hoa Kỳ, phần lớn dựa theo số liệu của FDA (U.S. Food and Drug Administration). Ch&uacute;ng t&ocirc;i giữ bản quyền nội dung b&agrave;i viết tr&ecirc;n website n&agrave;y. Mọi sự sao ch&eacute;p về website của c&aacute;c bạn kh&ocirc;ng được sự đồng &yacute; bằng văn bản từ THOL l&agrave; bất hợp ph&aacute;p v&agrave; đơn vị thu thập nội dung chịu mọi tr&aacute;ch nhiệm trước ph&aacute;p luật. Ch&uacute;ng t&ocirc;i kh&ocirc;ng chịu bất cứ tr&aacute;ch nhiệm g&igrave; nếu c&aacute;c bạn sử dụng c&aacute;c số liệu tr&ecirc;n website n&agrave;y v&agrave;o c&aacute;c mục đ&iacute;ch ri&ecirc;ng của c&aacute;c bạn.</small></p>\r\n', 3, 0, 0, 0, NULL, 0, 'user'),
(3, 'vi', 'Giá Trị Dinh Dưỡng Của Trái Chuối', 'Chuối là một loại trái cây thân thuộc trong giới thể hình và thể thao. Chuối chứa nhiều chất dinh dưỡng như xơ cao, vitamin C, kali và mangan dồi dào và một nguồn rất tốt của Vitamin B6. Bên cạnh đó chuối chứa ít Chất béo bão hòa , cholesterol và natri tốt cho tim mạch. Nhưng chuối lại chứa nhiều đường có calo cao', 'uploads/images/320-chuoi-chien.jpg', '2021-06-02', 3, '<p><strong>Chuối l&agrave; một loại tr&aacute;i c&acirc;y th&acirc;n thuộc trong giới thể h&igrave;nh v&agrave; thể thao. Chuối chứa nhiều chất dinh dưỡng như xơ cao, vitamin C, kali v&agrave; mangan dồi d&agrave;o v&agrave; một nguồn rất tốt của Vitamin B6. B&ecirc;n cạnh đ&oacute; chuối chứa &iacute;t Chất b&eacute;o b&atilde;o h&ograve;a , cholesterol v&agrave; natri tốt cho tim mạch. Nhưng chuối lại chứa nhiều đường c&oacute; calo cao</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<thead>\r\n		<tr>\r\n			<th colspan=\"2\">\r\n			<h4>TH&Agrave;NH PHẦN DINH DƯỠNG</h4>\r\n			</th>\r\n		</tr>\r\n		<tr>\r\n			<th>Khối lượng liều d&ugrave;ng</th>\r\n			<th>100g</th>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Th&agrave;nh phần dinh dưỡng trong mỗi liều</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Năng lượng 89</td>\r\n			<td>Từ fat 2,8</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Nhu cầu hằng ng&agrave;y</th>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất b&eacute;o</strong>&nbsp;0,3g</td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- B&eacute;o b&atilde;o h&ograve;a&nbsp;</em>0,1g</td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Trans fat</em></td>\r\n			<td>~g</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cholesterol 0mg</td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất bột đường 22,8g</strong></td>\r\n			<td>8%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Chất xơ 2,6&nbsp;g</em></td>\r\n			<td>10%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Đường 12g</em></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Protein 1,1g</td>\r\n			<td>2%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vitamin A1%</td>\r\n			<td>Vitamin C14%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Canxi0%</td>\r\n			<td>Sắt2%</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><small>Ch&uacute; &yacute;: nhu cầu hằng ng&agrave;y dựa tr&ecirc;n t&iacute;nh to&aacute;n 1 người cần 2000 calories 1 ng&agrave;y, nhu cầu hằng ng&agrave;y của bạn c&oacute; thể cao hoặc thấp hơn t&ugrave;y thuộc v&agrave;o lượng calories bạn cần.</small></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 3, 0, 0, 0, NULL, 0, 'user'),
(4, 'vi', 'Thịt Lợn Tươi Sống ( Thịt Thăn, Nạc Thăn)', 'Thịt nạc thăn heo rất thấp Natri, chứa nhiều của Riboflavin, Kali và kẽm, protein, Thiamin, Niacin, VitaminB6, Phốt Pho và Selenium, nhưng lại chứa nhiều cholesterol', 'uploads/images/cach-phan-biet-cac-loai-thit-heo-ma-ban-co-the-chua-biet_800x500.jpg', '2021-06-02', 3, '<p><strong>Thịt nạc thăn heo rất thấp Natri, chứa nhiều của Riboflavin, Kali v&agrave; kẽm, protein, Thiamin, Niacin, VitaminB6, Phốt Pho v&agrave; Selenium, nhưng lại chứa nhiều cholesterol</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<thead>\r\n		<tr>\r\n			<th colspan=\"2\">\r\n			<h4>TH&Agrave;NH PHẦN DINH DƯỠNG</h4>\r\n			</th>\r\n		</tr>\r\n		<tr>\r\n			<th>Khối lượng liều d&ugrave;ng</th>\r\n			<th>100g</th>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Th&agrave;nh phần dinh dưỡng trong mỗi liều</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Năng lượng 109</td>\r\n			<td>Từ fat 19,6</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Nhu cầu hằng ng&agrave;y</th>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất b&eacute;o</strong>&nbsp;2,2g</td>\r\n			<td>3%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- B&eacute;o b&atilde;o h&ograve;a&nbsp;</em>0,7g</td>\r\n			<td>3%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Trans fat</em></td>\r\n			<td>0g</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cholesterol 65mg</td>\r\n			<td>22%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất bột đường 0g</strong></td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Chất xơ 0&nbsp;g</em></td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Đường 0g</em></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Protein 20,9g</td>\r\n			<td>42%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vitamin A0%</td>\r\n			<td>Vitamin C0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Canxi0%</td>\r\n			<td>Sắt6%</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><small>Ch&uacute; &yacute;: nhu cầu hằng ng&agrave;y dựa tr&ecirc;n t&iacute;nh to&aacute;n 1 người cần 2000 calories 1 ng&agrave;y, nhu cầu hằng ng&agrave;y của bạn c&oacute; thể cao hoặc thấp hơn t&ugrave;y thuộc v&agrave;o lượng calories bạn cần.</small></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Chất Lượng Của Protein Từ Thịt Nạc Heo</h2>\r\n\r\n<p>20,9g protein quả l&agrave; con số kh&ocirc;ng hề nhỏ nhưng liệu chất lượng của n&oacute; c&oacute; tốt hay kh&ocirc;ng?</p>\r\n\r\n<p>Trước ti&ecirc;n, ch&uacute;ng ta phải đ&aacute;nh gi&aacute; Chất Lượng Protein qua 3 yếu tố, trong 100g Thịt Nạc Heo tươi sống c&oacute;:</p>\r\n\r\n<p><strong><em>&nbsp;Chỉ số PDCAAS: 1</em></strong>. Với kết quả n&agrave;y, cho ch&uacute;ng ta thấy được trong 100g Thịt Nạc Heo chứa rất nhiều nguồn Acid Amin thiết yếu m&agrave; cơ thể cần v&agrave; khả năng hấp thu của cơ thể đối với nguồn thực phẩm n&agrave;y.</p>\r\n\r\n<p><strong><em>&nbsp;Chỉ số về sự C&acirc;n Bằng v&agrave; Đầy Đủ của c&aacute;c Acid Amin Thiết Yếu: 150</em></strong>. Điểm số n&agrave;y mang lại cho ch&uacute;ng ta sự y&ecirc;n t&acirc;m về mặt c&acirc;n bằng v&agrave; đầy đủ của c&aacute;c Acid Amin.</p>\r\n\r\n<p><strong><em>&nbsp;Chỉ số BV: 78</em></strong>. Đ&acirc;y l&agrave; chỉ tương đối cao, acid amin tương đối nhiều v&agrave; khả năng giữ Nitrogen trong cơ thể c&agrave;ng cao gi&uacute;p tổng hợp Protein v&agrave; ph&aacute;t triển cơ bắp.</p>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<h2>Thịt Nạc Heo Nh&igrave;n Ngon V&agrave; Bổ Nhưng Gymer H&atilde;y Cẩn Thận!!!</h2>\r\n\r\n<p>C&aacute;c chỉ số tr&ecirc;n cho ch&uacute;ng ta thấy rằng nguồn protein từ Thịt Nạc rất l&agrave; tốt để bổ sung v&agrave;o chế độ dinh dưỡng tăng cơ hằng ng&agrave;y. Tuy nhi&ecirc;n, khi c&aacute;c bạn mua thịt nạc heo về, khi c&aacute;c bạn cắt thịt ra để chi&ecirc;n hoặc luộc, x&agrave;o th&igrave; đều thấy xen kẽ những sợi mỡ động vật ở b&ecirc;n trong thịt. V&agrave;&nbsp;<strong>Fat từ 100g Thịt Nạc Heo l&agrave; 2,2g</strong>.</p>\r\n\r\n<p>Thật đ&aacute;ng tiếc, 2,2g Fat n&agrave;y l&agrave; mỡ động vật, m&agrave; đ&atilde; l&agrave; mỡ động vật th&igrave; chủ yếu chứa&nbsp;<em>Chất B&eacute;o B&atilde;o H&ograve;a</em>.&nbsp;<strong>Đ&acirc;y l&agrave; loại&nbsp;<a href=\"http://www.thehinhonline.com.vn/baiviet/chitiet/fat-secret\" target=\"_blank\">Chất B&eacute;o</a>&nbsp;xấu, kh&ocirc;ng tốt cho sức khỏe v&agrave; khi v&agrave;o cơ thể sẽ l&agrave;m tăng lượng Cholesterol trong m&aacute;u l&ecirc;n, về l&acirc;u d&agrave;i sẽ mắc bệnh về huyết &aacute;p, tim mạch&nbsp;</strong>.</p>\r\n\r\n<p>&nbsp;Đ&aacute;ng sợ hơn nữa l&agrave; h&agrave;m lượng Cholesterol của 100g Thịt Nạc Heo l&agrave;&nbsp;<strong><em>65mg</em></strong>. Đ&acirc;y l&agrave; chỉ số rất cao v&agrave; cũng ch&iacute;nh l&agrave; nhược điểm của Thịt Nạc Heo. Cho n&ecirc;n về mặt gi&aacute; trị về sức khỏe th&igrave; kh&ocirc;ng n&ecirc;n ăn nhiều.</p>\r\n\r\n<p><em>&nbsp;B&ecirc;n cạnh đ&oacute;, Thịt Nạc Heo cũng c&oacute; chứa Omega 3 v&agrave; Omega 6, tuy nhi&ecirc;n tỉ lệ Omega n&agrave;y vẫn chưa l&iacute; tưởng</em>.</p>\r\n\r\n<h2>Thực Phẩm Bổ Sung Thay Thế, &Iacute;t Cholesterol</h2>\r\n\r\n<p>T&oacute;m lại, Thịt Nạc Heo tr&ecirc;n kh&iacute;a cạnh h&agrave;m lượng Protein c&oacute; thể gi&uacute;p c&aacute;c gymer ph&aacute;t triển cơ bắp, kh&ocirc;ng may thay về kh&iacute;a cạnh cholesterol, chất b&eacute;o b&atilde;o h&ograve;a c&oacute; trong Thịt Nạc Heo th&igrave; kh&ocirc;ng tốt cho sức khỏe. Nếu c&aacute;c bạn muốn bổ sung một lượng protein cao để tăng cơ từ c&aacute;c thực phẩm tự nhi&ecirc;n b&ecirc;n ngo&agrave;i m&agrave; kh&ocirc;ng d&ugrave;ng thực phẩm bổ sung th&igrave; phải chấp nhận h&agrave;m lượng cholesterol. Trong khi đ&oacute; c&aacute;c bạn n&ecirc;n tham khảo những loại thực phẩm bổ sung thay thế như&nbsp;<strong><a href=\"http://www.thehinhonline.com.vn/sanpham/chitiet/iso-100-23\" target=\"_blank\">ISO 100</a></strong>&nbsp;mang lại cho bạn 25g protein, trong 25g n&agrave;y chỉ c&oacute; 5mg cholesterol th&ocirc;i hoặc&nbsp;<strong><a href=\"http://www.thehinhonline.com.vn/sanpham/chitiet/elite-casein-4\" target=\"_blank\">Elite Casein</a></strong>&nbsp;cũng mang lại 25g protein v&agrave; k&egrave;m theo 10mg cholesterol.</p>\r\n\r\n<p>Một ng&agrave;y ở TheHinhOnline c&oacute; rất nhiều bạn tới đo m&aacute;y InBody v&agrave; hầu hết c&aacute;c bạn đ&oacute; cần 120g protein để tăng cơ. Giả sử như với lượng protein đ&oacute;, bạn ăn Thịt Nạc Heo th&igrave; phải cần tới 600g Thịt Nạc Heo mới cho ra 120g protein v&agrave; đồng thời l&agrave;&nbsp;<strong>390mg cholesterol</strong>, đ&acirc;y l&agrave; lượng cholesterol rất cao so với khuyến nghị của tổ chức Y Tế Thế Giới. Cho n&ecirc;n bạn cũng n&ecirc;n c&acirc;n nhắc tới việc sử dụng Thực Phẩm Bổ Sung để vừa đảm bảo được gi&aacute; trị tăng cơ bắp v&agrave; vừa đảm bảo lu&ocirc;n cả sức khỏe.</p>\r\n\r\n<h2>Vitamin Kho&aacute;ng Chất C&oacute; Trong Thịt Nạc Heo</h2>\r\n\r\n<p>Ngo&agrave;i ra, trong Thịt Nạc Heo cũng chứa nhiều vitamin nh&oacute;m B tương đối cao. Nh&oacute;m Vitamin B n&agrave;y gi&uacute;p ch&uacute;ng ta tập trung hơn trong khi tập v&agrave; khi bổ sung đầy đủ th&igrave; n&oacute; sẽ th&uacute;c đẩy chuyển h&oacute;a Carb, Fat, Protein th&agrave;nh Glucose để cho ch&uacute;ng ta hoạt động cơ bắp. V&agrave;&nbsp;<strong>chỉ số c&acirc;n bằng về vitamin, kho&aacute;ng chất trong Thịt Nạc Heo l&agrave; 53</strong>, đ&acirc;y cũng kh&ocirc;ng phải l&agrave; kết quả cao, tuy nhi&ecirc;n nếu muốn ăn đầy đủ lượng vitamin kho&aacute;ng chất th&igrave; với thực phẩm tự nhi&ecirc;n, c&aacute;c bạn ăn kh&ocirc;ng bao giờ l&agrave; đủ cho n&ecirc;n c&aacute;c bạn cũng c&oacute; thể sử dụng th&ecirc;m Thực Phẩm Bổ Sung Vitamin, Kho&aacute;ng Chất để cung cấp đủ cho ch&uacute;ng ta.</p>\r\n', 3, 0, 0, 0, NULL, 0, 'user');
INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `SoLanXem`, `NoiBat`, `AnHien`, `tags`, `Duyet`, `NguoiDang`) VALUES
(5, 'vi', '12+ BÀI TẬP MÔNG CHO NỮ TẠI NHÀ, PHÒNG GYM CHO VÒNG 3 CĂNG TRÒN CHUẨN SỐ ĐO', 'Muốn mông nhỏ lép nở căng tròn đầy đặn nhanh chóng chắc chắn không thể bỏ qua các bài tập squat mông cho nữ tại nhà. Chỉ trong khoảng 30 ngày tập luyện chăm chỉ, vòng 3 của bạn sẽ cải thiện rõ rệt.', 'uploads/images/upl_12_bai_tap_mong_cho_nu_cho_vong_3_cang_tron_day_quyen_ru_1602558223_image_1602558223.jpg', '2021-06-02', 3, '<h2><strong>I. C&aacute;c b&agrave;i tập m&ocirc;ng cho nữ tại nh&agrave;</strong></h2>\r\n\r\n<p>T&ugrave;y thuộc v&agrave;o t&igrave;nh trạng v&ograve;ng 3 của bạn: nhỏ l&eacute;p, chảy xệ hay ph&igrave; đại qu&aacute; mức m&agrave; lựa chọn một trong số những b&agrave;i tập m&ocirc;ng cho nữ tại nh&agrave; dưới đ&acirc;y để &aacute;p dụng.</p>\r\n\r\n<h3>1.1 B&agrave;i tập squat cho h&ocirc;ng quả t&aacute;o sau 30 ng&agrave;y</h3>\r\n\r\n<p>Muốn m&ocirc;ng nhỏ l&eacute;p nở căng tr&ograve;n đầy đặn nhanh ch&oacute;ng chắc chắn kh&ocirc;ng thể bỏ qua c&aacute;c b&agrave;i tập squat m&ocirc;ng cho nữ tại nh&agrave;. Chỉ trong khoảng 30 ng&agrave;y tập luyện chăm chỉ, v&ograve;ng 3 của bạn sẽ cải thiện r&otilde; rệt.</p>\r\n\r\n<p>Hệ thống b&agrave;i tập squat v&agrave;o m&ocirc;ng rất đa dạng, dưới đ&acirc;y l&agrave; tổng hợp tất cả những động t&aacute;c gi&uacute;p m&ocirc;ng to hiệu quả nhất đ&atilde; được nhiều chị em thử nghiệm v&agrave; th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p><strong>[1] Squat cơ bản</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tần suất:&nbsp;</strong>15 &ndash; 20 lần/ 1 buổi tập</li>\r\n	<li><strong>Lịch tập:&nbsp;</strong>6 ng&agrave;y đầu ti&ecirc;n &ndash; Cuối tuần nghỉ</li>\r\n	<li><strong>Hướng dẫn:&nbsp;</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"squat vào mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/squat-vao-mong.jpg\" style=\"height:408px; width:600px\" title=\"Squat mông tại nhà đơn giản không cần dụng cụ \" /></p>\r\n\r\n<p><em>Squat m&ocirc;ng tại nh&agrave; đơn giản kh&ocirc;ng cần dụng cụ&nbsp;</em></p>\r\n\r\n<ul>\r\n	<li>Đứng thẳng: lưng thẳng, cổ thẳng, hai ch&acirc;n đứng rộng bằng vai, mắt nh&igrave;n thẳng về ph&iacute;a trước.</li>\r\n	<li>Đan xen c&aacute;c ng&oacute;n tay v&agrave;o nhau v&agrave; vươn thẳng c&aacute;nh tay ra trước ngực.</li>\r\n	<li>Từ từ hạ thấp phần m&ocirc;ng xuống theo hướng s&agrave;n cho tới khi đ&ugrave;i v&agrave;o mặt s&agrave;n tạo hai đường thẳng song song.</li>\r\n	<li>L&uacute;c n&agrave;y, cần đảm bảo lưng thẳng, cổ thẳng, g&oacute;t ch&acirc;n kh&ocirc;ng bị dịch chuyển đồng thời h&iacute;t v&agrave;o (giữ khoảng 2s)</li>\r\n	<li>Thực hiện song song n&acirc;ng cơ thể v&agrave; thở ra nhẹ nh&agrave;ng.</li>\r\n	<li>Nghỉ tại chỗ 1 gi&acirc;y trước khi thực hiện lần tiếp theo. Nghỉ giữa c&aacute;c b&agrave;i tập l&agrave; 1 ph&uacute;t.</li>\r\n</ul>\r\n\r\n<p><strong>[2] Squat đ&aacute; ch&acirc;n sau</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tần suất:&nbsp;</strong>15 &ndash; 20 lần/ 1 buổi tập</li>\r\n	<li><strong>Lịch tập:&nbsp;</strong>Từ ng&agrave;y 2 &ndash; ng&agrave;y 5 (nghỉ ng&agrave;y 6)</li>\r\n	<li><strong>Hướng dẫn:&nbsp;</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"tập squat mông cho nữ tại nhà\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-squat-mong-cho-nu-tai-nha.jpg\" style=\"height:389px; width:600px\" title=\"Squat đá chân sau - bài tập squat mông cho nữ tại nhà\" /></p>\r\n\r\n<p><em>Squat đ&aacute; ch&acirc;n sau &ndash; b&agrave;i tập squat m&ocirc;ng cho nữ tại nh&agrave;</em></p>\r\n\r\n<ul>\r\n	<li>Tạo tư thế hạ thấp người: hai ch&acirc;n dang rộng bằng vai, đ&ugrave;i song song với mặt s&agrave;n, 2 b&agrave;n tay nắm v&agrave;o nhau đưa ra trước ngực.</li>\r\n	<li>Đứng l&ecirc;n, ch&acirc;n phải dựng thẳng, ch&acirc;n tr&aacute;i đ&aacute; ra ph&iacute;a sau.</li>\r\n	<li>Giữ trong 2 &ndash; 3 gi&acirc;y trở về tư thế ban đầu v&agrave; tương tự với b&ecirc;n ch&acirc;n phải.</li>\r\n	<li>Thực hiện xong 2 b&ecirc;n ch&acirc;n mới được t&iacute;nh l&agrave; một lần.</li>\r\n</ul>\r\n\r\n<p><strong>[3] Squat sumo</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tần suất:&nbsp;</strong>15 &ndash; 20 lần/ 1 buổi tập</li>\r\n	<li><strong>Lịch tập:&nbsp;</strong>Từ ng&agrave;y 7 &ndash; ng&agrave;y 11 (nghỉ ng&agrave;y 12)</li>\r\n	<li><strong>Hướng dẫn:&nbsp;</strong></li>\r\n</ul>\r\n\r\n<p>Chuẩn bị: 2 b&agrave;n ch&acirc;n dang rộng ra hơn vai một ch&uacute;t, mũi ch&acirc;n chếch ra ph&iacute;a ngo&agrave;i. Hai tay đặt l&ecirc;n h&ocirc;ng, lưng thẳng, cổ thẳng.</p>\r\n\r\n<ul>\r\n	<li>Từ từ ngồi thấp xuống sao cho phần m&ocirc;ng được đẩy l&ugrave;i ra sau, đầu gối hướng ra ngo&agrave;i thực hiện h&iacute;t v&agrave;o 2 &ndash; 3 gi&acirc;y.</li>\r\n	<li>Trở lại tư thế ban đầu đồng thời thở ra.</li>\r\n	<li>Kết th&uacute;c 1 lần thực hiện.</li>\r\n</ul>\r\n\r\n<p><img alt=\"squat mông tại nhà\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/squat-mong-tai-nha.jpg\" style=\"height:403px; width:600px\" title=\"Squat sumo tại nhà vừa giúp mông nở ra lại săn chắc nhanh chóng\" /></p>\r\n\r\n<p><em>Squat sumo tại nh&agrave; vừa gi&uacute;p m&ocirc;ng nở ra lại săn chắc nhanh ch&oacute;ng</em></p>\r\n\r\n<p><strong>[4] Squat hai tay chạm đất</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tần suất:&nbsp;</strong>15 lần cho 3 ng&agrave;y đầu, 20 lần cho c&aacute;c ng&agrave;y tiếp theo.</li>\r\n	<li><strong>Lịch tập:&nbsp;</strong>Từ ng&agrave;y 13 &ndash; ng&agrave;y 18 (nghỉ ng&agrave;y 19)</li>\r\n	<li><strong>Hướng dẫn:&nbsp;</strong></li>\r\n</ul>\r\n\r\n<p>Chuẩn bị: hai ch&acirc;n đứng h&igrave;nh chữ V ngang bằng vai, đầu thẳng, mắt nh&igrave;n thẳng về trước, hai tay thẳng theo th&acirc;n người.</p>\r\n\r\n<p>Từ từ hạ thấp th&acirc;n người (lưng vẫn thẳng) hai b&ecirc;n đầu gối hướng ra ngo&agrave;i cho khoảng c&aacute;ch m&ocirc;ng v&agrave; s&agrave;n ngắn nhất, hai tay nằm trong hai ch&acirc;n v&agrave; mũi b&agrave;n tay chạm s&agrave;n.</p>\r\n\r\n<p>D&ugrave;ng lực ở b&agrave;n ch&acirc;n đẩy mạnh th&acirc;n người thẳng l&ecirc;n đồng thời đưa hai tay l&ecirc;n cao qua đầu, hai ch&acirc;n kiễng g&oacute;t trụ bằng mũi b&agrave;n ch&acirc;n.</p>\r\n\r\n<p><img alt=\"bài tập squat mông cho nữ \" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-squat-mong-cho-nu.jpg\" style=\"height:333px; width:500px\" title=\"Trước khi tập Squat tư thế liền sườn, bạn phải có thời gian ít nhất 3 - 5 ngày làm quen với tư thế sumo\" /></p>\r\n\r\n<p><em>Trước khi tập Squat tư thế liền sườn, bạn phải c&oacute; thời gian &iacute;t nhất 3 &ndash; 5 ng&agrave;y l&agrave;m quen với tư thế sumo</em></p>\r\n\r\n<p><strong>[5] Squat cơ liền sườn</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tần suất:&nbsp;</strong>15 &ndash; 20 lần/ 1 buổi tập</li>\r\n	<li><strong>Lịch squat m&ocirc;ng:&nbsp;</strong>Từ ng&agrave;y 20 &ndash; ng&agrave;y 25 (nghỉ ng&agrave;y 26)</li>\r\n	<li><strong>Hướng dẫn:&nbsp;</strong></li>\r\n</ul>\r\n\r\n<p>Chuẩn bị: Người đứng thẳng, hai ch&acirc;n mở rộng, hai tay đưa qua sau đầu, l&ograve;ng b&agrave;n tay chạm t&oacute;c.</p>\r\n\r\n<ul>\r\n	<li>Hạ thấp gối xuống sao cho m&ocirc;ng thấp hơn đầu gối, nửa th&acirc;n người tr&ecirc;n v&agrave; cẳng ch&acirc;n giữ nguy&ecirc;n tư thế.</li>\r\n	<li>Đứng thẳng người l&ecirc;n, nhấc ch&acirc;n phải l&ecirc;n cao sao cho cẳng ch&acirc;n tạo g&oacute;c 90 độ với mặt s&agrave;n.</li>\r\n	<li>Nghi&ecirc;ng th&acirc;n người về ph&iacute;a b&ecirc;n phải sao cho c&ugrave;i chỏ chạm đầu gối phải.</li>\r\n	<li>Giữ 2 gi&acirc;y v&agrave; hạ ch&acirc;n về tư thế ban đầu.</li>\r\n	<li>Thực hiện với ch&acirc;n tr&aacute;i t&iacute;nh 1 lượt tập.</li>\r\n</ul>\r\n\r\n<p><img alt=\"tập squat mông đúng cách\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-squat-mong-dung-cach.jpg\" style=\"height:383px; width:500px\" title=\"Squat cơ liền sườn - Những bài tập dành cho mông đơn giản tại nhà\" /></p>\r\n\r\n<p><em>Squat cơ liền sườn &ndash; Những b&agrave;i tập d&agrave;nh cho m&ocirc;ng đơn giản tại nh&agrave;</em></p>\r\n\r\n<p><strong>[6] Squat bật nhảy</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tần suất:&nbsp;</strong>15 lần/ 1 buổi tập</li>\r\n	<li><strong>Lịch tập:&nbsp;</strong>Từ Ng&agrave;y 27 &ndash; ng&agrave;y 30</li>\r\n	<li><strong>Hướng dẫn:&nbsp;</strong></li>\r\n</ul>\r\n\r\n<p>Chuẩn bị: d&aacute;ng người thẳng, ch&acirc;n mở rộng, hai tay đan v&agrave;o nhau đưa ra trước mặt.</p>\r\n\r\n<ul>\r\n	<li>Hạ thấp người, gối khụy dần tới khi đ&ugrave;i tạo với mặt s&agrave;n hai đường thẳng song song.</li>\r\n	<li>Dồn lực v&agrave;o b&agrave;n ch&acirc;n, bật nhảy cao hết sức, hai tay thẳng vung ra sau lưng, mũi b&agrave;n ch&acirc;n hướng về mặt s&agrave;n.</li>\r\n	<li>Về tư thế ban đầu v&agrave; thực hiện c&aacute;c lần bật nhảy tiếp theo.</li>\r\n</ul>\r\n\r\n<p><img alt=\"các bài tập squat mông cho nữ tại nhà\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/cac-bai-tap-squat-mong-cho-nu-tai-nha.jpg\" style=\"height:333px; width:500px\" title=\"Squat cho mông đẹp chỉ sau 1 tháng tập luyện\" /></p>\r\n\r\n<p><em>Squat cho m&ocirc;ng đẹp chỉ sau 1 th&aacute;ng tập luyện</em></p>\r\n\r\n<p><strong>!!! Lưu &yacute; khi tập Squat cho m&ocirc;ng nở eo thon</strong></p>\r\n\r\n<p>Muốn Squat m&ocirc;ng tại nh&agrave; hiệu quả nhanh, bạn cần thự hiện đ&uacute;ng từng tư thế trong mỗi động t&aacute;c. Đặc biệt, lưu &yacute; một số điểm sau:</p>\r\n\r\n<ul>\r\n	<li>Lưng lu&ocirc;n thẳng tự nhi&ecirc;n trong suốt qu&aacute; tr&igrave;nh tập luyện.</li>\r\n	<li>Khi hạ người h&iacute;t s&acirc;u v&agrave;o bằng mũi v&agrave; thở ra bằng miệng nhẹ nh&agrave;ng</li>\r\n	<li>Kết hợp thực đơn dinh dưỡng tăng v&ograve;ng 3</li>\r\n</ul>\r\n\r\n<h3>1.2 C&aacute;c b&agrave;i tập giảm mỡ m&ocirc;ng hiệu quả nhất</h3>\r\n\r\n<p>C&aacute;c b&agrave;i tập gi&uacute;p m&ocirc;ng nhỏ hơn, đốt ch&aacute;y lượng lớn calo trong mỗi buổi tập bạn ho&agrave;n to&agrave;n c&oacute; thể thực hiện dễ d&agrave;ng tại nh&agrave;. Mỗi ng&agrave;y, chỉ với 20 ph&uacute;t tập luyện, chắc chắn bạn sẽ nhận thấy v&ugrave;ng m&ocirc;ng giảm mỡ đ&aacute;ng kể.</p>\r\n\r\n<p>Đặc biệt với chị em sau sinh m&ocirc;ng chảy xệ nhưng kh&ocirc;ng c&oacute; thời gian tới ph&ograve;ng tập c&oacute; thể thử phương ph&aacute;p n&agrave;y.</p>\r\n\r\n<p>[1] Side Hop Lunge</p>\r\n\r\n<p>Side Hop lunge được nhiều chị em lựa chọn bởi hai t&aacute;c động: giảm mỡ v&agrave; đ&ugrave;i săn chắc. V&igrave; vậy, trong danh s&aacute;ch những b&agrave;i tập giảm mỡ m&ocirc;ng cho nữ tại nh&agrave; chắc chắn kh&ocirc;ng thể bỏ qua Side Hop Lunge.</p>\r\n\r\n<ul>\r\n	<li><strong>Chuẩn bị</strong></li>\r\n</ul>\r\n\r\n<p>Người ở tư thế đứng thẳng, hai ch&acirc;n thẳng đặt cạnh nhau.</p>\r\n\r\n<ul>\r\n	<li><strong>Thực hiện</strong></li>\r\n	<li>Đưa ch&acirc;n tr&aacute;i l&ecirc;n trước, ch&ugrave;ng xuống sao cho đ&ugrave;i ch&acirc;n tr&aacute;i vu&ocirc;ng g&oacute;c, song song với s&agrave;n</li>\r\n	<li>Thu ch&acirc;n tr&aacute;i về vị tr&iacute; ban đầu v&agrave; bật nhảy cao cả 2 ch&acirc;n về ph&iacute;a b&ecirc;n phải.</li>\r\n	<li>Tiến h&agrave;nh tương tự với ch&acirc;n phải v&agrave; t&iacute;nh hết 1 lần.</li>\r\n</ul>\r\n\r\n<p><img alt=\"những bài tập mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/nhung-bai-tap-mong.jpg\" style=\"height:354px; width:600px\" title=\"Tập luyện kiên trì sẽ giúp mông nhỏ lại trông thấy\" /></p>\r\n\r\n<p><em>Tập luyện ki&ecirc;n tr&igrave; sẽ gi&uacute;p m&ocirc;ng nhỏ lại tr&ocirc;ng thấy</em></p>\r\n\r\n<ul>\r\n	<li><strong>Thời gian thực hiện</strong></li>\r\n</ul>\r\n\r\n<p>Mỗi ng&agrave;y tập tối thiểu 15 lần cho b&agrave;i tập l&agrave;m m&ocirc;ng nhỏ lại Side Hop Lunge. Để hiệu quả cao hơn, c&oacute; thể kết hợp nhiều động t&aacute;c c&ugrave;ng thực hiện trong 1 buổi tập.</p>\r\n\r\n<p>[2] One &ndash; Leg deadlift</p>\r\n\r\n<p>L&agrave; một trong những b&agrave;i tập giảm mỡ m&ocirc;ng cho nữ hiệu quả nhanh, One &ndash; Leg deadlift với c&aacute;c động t&aacute;c đơn giản nhưng t&aacute;c động nhiều tới cơ đ&ugrave;i sau, m&ocirc;ng v&agrave; cả ch&acirc;n. H&ocirc;ng quả t&aacute;o eo thon kh&ocirc;ng c&ograve;n xa vời với chị em nữa.</p>\r\n\r\n<p><strong>&ndash; Chuẩn bị</strong></p>\r\n\r\n<p>Th&acirc;n người đứng thẳng, hai ch&acirc;n dang ra rộng bằng h&ocirc;ng, 2 tay để thẳng thoải m&aacute;i theo th&acirc;n người.</p>\r\n\r\n<p><img alt=\"bài tập giảm mỡ mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-giam-mo-mong.jpg\" style=\"height:460px; width:600px\" /></p>\r\n\r\n<p><strong>&ndash; Thực hiện</strong></p>\r\n\r\n<ul>\r\n	<li>Bắt đầu đưa tay tr&aacute;i l&ecirc;n trước một g&oacute;c 30 độ, l&ograve;ng b&agrave;n tay nắm hờ như đang cầm tạ.</li>\r\n	<li>Tay phải hơi gập về ph&iacute;a sau, b&agrave;n tay đặt tại vị tr&iacute; h&ocirc;ng phải.</li>\r\n	<li>Hạ thấp người xuống, lấy ch&acirc;n phải l&agrave;m trụ, đẩy ch&acirc;n tr&aacute;i ra ph&iacute;a sau sao cho đầu, lưng, ch&acirc;n tr&aacute;i tạo th&agrave;nh một đường thẳng.</li>\r\n	<li>L&uacute;c n&agrave;y, tay tr&aacute;i thẳng vu&ocirc;ng g&oacute;c với mặt s&agrave;n v&agrave; h&iacute;t s&acirc;u bằng mũi khoảng 3 gi&acirc;y.</li>\r\n	<li>Thở ra từ từ v&agrave; d&ugrave;ng ch&acirc;n phải l&agrave;m trụ n&acirc;ng người l&ecirc;n về ban đầu.</li>\r\n	<li>Đổi b&ecirc;n phải v&agrave; tiến h&agrave;nh tương tự.</li>\r\n</ul>\r\n\r\n<p><strong>&ndash; Thời gian thực hiện:&nbsp;</strong>Mỗi ng&agrave;y tập tối đa 3 hiệp mỗi hiệp 20 lần</p>\r\n\r\n<p>[3] Plank with a raised leg</p>\r\n\r\n<p>B&agrave;i tập Plank t&aacute;c động l&ecirc;n nhiều v&ugrave;ng cơ c&ugrave;ng một l&uacute;c: cơ ngang, cơ bụng dọc, cơ li&ecirc;n sườn, cơ m&ocirc;ng, &hellip;n&ecirc;n kh&ocirc;ng chỉ gi&uacute;p giảm mỡ m&ocirc;ng m&agrave; cơ bụng cũng được săn chắc hơn.</p>\r\n\r\n<ul>\r\n	<li><strong>Chuẩn bị</strong></li>\r\n</ul>\r\n\r\n<p>Nằm &uacute;p mặt song song với mặt s&agrave;n nhưng kh&ocirc;ng chạm s&agrave;n: hai khuỷu tay v&agrave; hai mũi ch&acirc;n chạm s&agrave;n l&agrave;m trụ n&acirc;ng đỡ th&acirc;n người sao cho đầu, lương, m&ocirc;ng tạo th&agrave;nh một đường thẳng</p>\r\n\r\n<p><strong>&ndash; Thực hiện</strong></p>\r\n\r\n<ul>\r\n	<li>Lấy tay phải l&agrave;m trụ, n&acirc;ng tay tr&aacute;i l&ecirc;n tạo với vai một đường thẳng, l&ograve;ng b&agrave;n tay hướng xuống s&agrave;n.</li>\r\n	<li>Đồng thời thực hiện n&acirc;ng cao ch&acirc;n phải.</li>\r\n	<li>Giữ nguy&ecirc;n tư thế trong khoảng 2 gi&acirc;y rồi hạ xuống về vị tr&iacute; ban đầu.</li>\r\n	<li>Thực hiện tương tự với b&ecirc;n tay phải v&agrave; ch&acirc;n tr&aacute;i.</li>\r\n</ul>\r\n\r\n<p><strong>&ndash; Thời gian thực hiện:&nbsp;</strong>3 hiệp trong 1 buổi tập với số lần lặp 20 lần</p>\r\n\r\n<p><img alt=\"bài tập giúp mông nhỏ hơn\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-giup-mong-nho-hon.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<h3>1.3 Top b&agrave;i tập Yoga cho eo thon m&ocirc;ng to, săn chắc</h3>\r\n\r\n<p>Tập yoga c&oacute; n&acirc;ng m&ocirc;ng kh&ocirc;ng? Về nguy&ecirc;n tắc, những động t&aacute;c yoga khi thực hiện t&aacute;c động tới c&aacute;c cơ v&ugrave;ng m&ocirc;ng một c&aacute;ch t&iacute;ch cực gi&uacute;p m&ocirc;ng săn chắc hơn, cải thiện k&iacute;ch thước đ&aacute;ng kể khi tập luyện đều đặn.</p>\r\n\r\n<p>C&aacute;c động t&aacute;c Yoga th&iacute;ch hợp với t&igrave;nh trạng người m&ocirc;ng xệ,&nbsp;<a href=\"https://benhvienthammykangnam.vn/tham-my-vong-3/mong-lep/\"><strong>m&ocirc;ng l&eacute;p</strong></a>&nbsp;bởi sau đ&acirc;y đều l&agrave; những b&agrave;i tập săn m&ocirc;ng đồng thời n&acirc;ng cao v&ugrave;ng m&ocirc;ng bị xệ cho kết quả v&ograve;ng 3 căng đẹp.</p>\r\n\r\n<p><strong>[1] Rose</strong></p>\r\n\r\n<p>Thời gian thực hiện cho mỗi động t&aacute;c l&agrave; 30 gi&acirc;y v&agrave; tăng dần số gi&acirc;y trong c&aacute;c lần tiếp theo. Một hiệp gồm 6 động t&aacute;c thực hiện li&ecirc;n tiếp nhau trong 3 ph&uacute;t. Hết hiệp nghỉ tại chỗ 1 ph&uacute;t trước khi lặp lại hiệp tiếp theo.</p>\r\n\r\n<ul>\r\n	<li><strong>Boat Pose</strong></li>\r\n</ul>\r\n\r\n<p>Khởi động bằng b&agrave;i tập t&aacute;c động trực tiếp tới cơ bụng, cơ m&ocirc;ng cho hiệu quả eo thon m&ocirc;ng cong. Động t&aacute;c n&agrave;y đ&ograve;i hỏi tay tẳng, ch&acirc;n thẳng, lưng thẳng mắt hướng về trước, h&iacute;t s&acirc;u thở đều.</p>\r\n\r\n<p><img alt=\"bài tập yoga cho mông to\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-yoga-cho-mong-to.jpg\" style=\"height:330px; width:600px\" title=\"bài tập yoga cho mông to\" /></p>\r\n\r\n<ul>\r\n	<li><strong>Camel Pose</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập yoga nâng mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-yoga-nang-mong.jpg\" style=\"height:337px; width:600px\" title=\"bài tập yoga nâng mông\" /></p>\r\n\r\n<ul>\r\n	<li><strong>Dancer Pose</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập mông yoga\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-yoga.jpg\" style=\"height:337px; width:600px\" title=\"bài tập mông yoga\" /></p>\r\n\r\n<ul>\r\n	<li><strong>Bridge Pose</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập yoga nở mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-yoga-no-mong.jpg\" style=\"height:311px; width:600px\" title=\"bài tập yoga nở mông\" /></p>\r\n\r\n<ul>\r\n	<li><strong>Happy baby Pose</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập mông cong eo thon\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-cong-eo-thon.jpg\" style=\"height:333px; width:600px\" title=\"bài tập mông cong eo thon\" /></p>\r\n\r\n<p><strong>[2] Yoga chiến binh</strong></p>\r\n\r\n<p>Nhắc tới b&agrave;i tập m&ocirc;ng cong eo thon bằng phương ph&aacute;p yoga kh&ocirc;ng thể thiếu tư thế chiến binh. Với hiệu quả t&aacute;c động mạnh tới hầu hết c&aacute;c cơ v&ugrave;ng m&ocirc;ng cho&nbsp;<a href=\"https://benhvienthammykangnam.vn/tham-my-vong-3/vong-3-dep/\"><strong>v&ograve;ng 3 đẹp săn chắc</strong></a>, nảy nở sau một thời gian thực hiện.</p>\r\n\r\n<ul>\r\n	<li><strong>Chiến binh I</strong></li>\r\n</ul>\r\n\r\n<p>Số lần thực hiện: 15 lần/ 1 buổi</p>\r\n\r\n<p>Chuẩn bị: Thảm yoga</p>\r\n\r\n<p>Lưu &yacute;: Trong qu&aacute; tr&igrave;nh tập đảm bảo lưng thẳng, cổ thẳng đầu thẳng để tr&aacute;nh c&aacute;c sự cố đau mỏi sau khi tập luyện.</p>\r\n\r\n<p><img alt=\"các bài tập mông cho nữ tại nhà\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/cac-bai-tap-mong-cho-nu-tai-nha.jpg\" style=\"height:400px; width:600px\" title=\"Tư thế chiến binh I - bài tập yoga nâng mông hiệu quả tại nhà\" /></p>\r\n\r\n<p><em>Tư thế chiến binh I &ndash; b&agrave;i tập yoga n&acirc;ng m&ocirc;ng hiệu quả tại nh&agrave;</em></p>\r\n\r\n<ul>\r\n	<li><strong>Chiến binh II</strong></li>\r\n</ul>\r\n\r\n<p>Số lần thực hiện: 20 lần/ 1 buổi</p>\r\n\r\n<p>Chuẩn bị: Thảm yoga</p>\r\n\r\n<p>Lưu &yacute;: Tập vừa sức trong những buổi đầu c&oacute; thể chỉ thực hiện 10 &ndash; 15 lần sau tăng dần để cơ thể l&agrave;m quen, th&iacute;ch ứng dần.</p>\r\n\r\n<p><img alt=\"bài tập mông cho nữ\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-cho-nu.jpg\" style=\"height:445px; width:600px\" title=\"Yoga chiến bình II một trong những bài tập mông hiệu quả nhất\" /></p>\r\n\r\n<p><em>Yoga chiến b&igrave;nh II một trong những b&agrave;i tập m&ocirc;ng hiệu quả nhất</em></p>\r\n\r\n<p><strong>[3] Yoga Plank</strong></p>\r\n\r\n<p>L&agrave; một trong số b&agrave;i tập m&ocirc;ng nở đơn giản được nhiều chị em &aacute;p dụng ngay tại nh&agrave;. Chỉ cần chuẩn bị một chiếc thảm yoga l&agrave; bạn ho&agrave;n to&agrave;n c&oacute; thể thực hiện những động t&aacute;c dưới đ&acirc;y v&agrave; nhanh ch&oacute;ng cải thiện được k&iacute;ch thước v&ograve;ng 3 của bản th&acirc;n.</p>\r\n\r\n<ul>\r\n	<li><strong>Plank ngược</strong></li>\r\n</ul>\r\n\r\n<p>Chuẩn bị: Ngồi thoải m&aacute;i tr&ecirc;n thảm, hai ch&acirc;n duỗi thẳng về trước, hai tay để thả lỏng xoải theo th&acirc;n người</p>\r\n\r\n<p>Thực hiện:</p>\r\n\r\n<ul>\r\n	<li>Đưa hai tay ra sau, &uacute;p hai l&ograve;ng b&agrave;n tay xuống thảm.</li>\r\n	<li>Lấy g&oacute;t b&agrave;n ch&acirc;n l&agrave;m điểm trụ tr&ecirc;n thảm từ từ n&acirc;ng người l&ecirc;n cao sao cho th&acirc;n người, thảm v&agrave; c&aacute;nh tay tạo th&agrave;nh một tam gi&aacute;c vu&ocirc;ng.</li>\r\n	<li>Giữ y&ecirc;n trong 3 gi&acirc;y rồi từ từ hạ th&acirc;n người xuống trở về ban đầu.</li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập plank mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-plank-mong.jpg\" style=\"height:338px; width:500px\" title=\"Bài tập Plank mông ngược giúp nâng cơ mông, săn chắc vòng bụng\" /></p>\r\n\r\n<p><em>B&agrave;i tập Plank m&ocirc;ng ngược gi&uacute;p n&acirc;ng cơ m&ocirc;ng, săn chắc v&ograve;ng bụng</em></p>\r\n\r\n<ul>\r\n	<li><strong>Plank 1 ch&acirc;n</strong></li>\r\n</ul>\r\n\r\n<p>Chuẩn bị: Th&acirc;n người ở tư thế quay mặt xuống s&agrave;n, hai tay chống thẳng xuống s&agrave;n, mũi b&agrave;n ch&acirc;n chạm s&agrave;n</p>\r\n\r\n<p>Thực hiện:</p>\r\n\r\n<ul>\r\n	<li>N&acirc;ng ch&acirc;n tr&aacute;i l&ecirc;n tới ngang th&acirc;n người đồng thời h&iacute;t thật s&acirc;u v&agrave;o.</li>\r\n	<li>Giữ nguy&ecirc;n trong 3 gi&acirc;y v&agrave; trở về tư thế ban đồng.</li>\r\n	<li>L&uacute;c thở ra nhẹ nh&agrave;ng v&agrave; tiếp tục với b&ecirc;n ch&acirc;n phải.</li>\r\n</ul>\r\n\r\n<p><img alt=\"các bài tập làm mông nhỏ lại\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/cac-bai-tap-lam-mong-nho-lai.jpg\" style=\"height:275px; width:429px\" title=\"Tập mông đá chân sau là bài tập không thể thiếu nếu muốn mông cong tự nhiên\" /></p>\r\n\r\n<p><em>Tập m&ocirc;ng đ&aacute; ch&acirc;n sau l&agrave; b&agrave;i tập kh&ocirc;ng thể thiếu nếu muốn m&ocirc;ng cong tự nhi&ecirc;n</em></p>\r\n\r\n<h2><strong>II. 4 B&agrave;i tập cho v&ograve;ng m&ocirc;ng ho&agrave;n hảo tại ph&ograve;ng gym</strong></h2>\r\n\r\n<p>Tại ph&ograve;ng tập gym c&oacute; nhiều c&aacute;c dụng cụ hỗ trợ tập luyện gi&uacute;p cơ m&ocirc;ng ph&aacute;t triển, săn chắc, nở ra, &hellip; hiệu quả cao hơn. V&igrave; vậy, nếu c&oacute; thể sắp xếp thời gian c&ocirc;ng việc th&igrave; bạn n&ecirc;n tới đ&acirc;y để thực hiện những b&agrave;i tập m&ocirc;ng cho nữ.</p>\r\n\r\n<h3>2.1 B&agrave;i tập m&ocirc;ng với tạ</h3>\r\n\r\n<p>Sử dụng c&aacute;c loại tạ trong ph&ograve;ng tập gym trong việc thực hiện c&aacute;c động t&aacute;c gi&uacute;p m&ocirc;ng to hiệu quả cao. Bạn sẽ kh&ocirc;ng l&acirc;u để sở hữu h&ocirc;ng tr&aacute;i t&aacute;o si&ecirc;u quyến rũ khi tập c&aacute;c b&agrave;i dưới đ&acirc;y.</p>\r\n\r\n<p><strong>[1] Tập&nbsp;m&ocirc;ng với tạ đơn</strong></p>\r\n\r\n<p>Khi mới bắt đầu, bạn n&ecirc;n để cơ thể l&agrave;m quen dần dần v&agrave; lựa chọn tạ đơn, tạ cầm tay vừa sức để thực hiện c&aacute;c b&agrave;i tập m&ocirc;ng với tạ. Sau n&agrave;y khi đ&atilde; quen dần c&oacute; thể n&acirc;ng cấp l&ecirc;n tại đ&ograve;n hoặc tạ k&eacute;o.</p>\r\n\r\n<ul>\r\n	<li>Nửa th&acirc;n người tr&ecirc;n đứng thẳng, hai ch&acirc;n mở rộng hơn vai vừa phải, đầu gối hơi khụy.</li>\r\n	<li>Hai tay cần v&agrave;o tạ đưa thẳng về ph&iacute;a trước mặt ngang vai.</li>\r\n	<li>Từ từ hạ thấp th&acirc;n người xuống sao cho đ&ugrave;i vu&ocirc;ng g&oacute;c với cẳng ch&acirc;n.</li>\r\n	<li>Đồng thời đưa tay xuống, tạ thấp dưới m&ocirc;ng.</li>\r\n	<li>Giữ nguy&ecirc;n khoảng 5 giấy rồi trở lại vị tr&iacute; bạn đầu.</li>\r\n</ul>\r\n\r\n<p><img alt=\"tập mông với tạ đơn \" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-mong-voi-ta-don.jpg\" style=\"height:449px; width:600px\" title=\"Squat mông với tạ đơn tại phòng gym để mông nảy nở, săn chắc\" /></p>\r\n\r\n<p><em>Squat m&ocirc;ng với tạ đơn tại ph&ograve;ng gym để m&ocirc;ng nảy nở, săn chắc</em></p>\r\n\r\n<ul>\r\n	<li>Thực hiện lặp đi lặp lại khoảng 10 lần/ 1 hiệp.</li>\r\n	<li>Tập kh&ocirc;ng qu&aacute; 10 hiệp/ 1 buổi v&agrave; mỗi hiệp nghỉ tại chỗ khoảng 60 giấy.</li>\r\n</ul>\r\n\r\n<p><strong>[2] B&agrave;i tập m&ocirc;ng cho nữ với tạ đ&ograve;n</strong></p>\r\n\r\n<ul>\r\n	<li>Hai b&agrave;n tay nắm chặt lấy tạ đ&ograve;n, đưa tạ ra ph&iacute;a sau vai đặt song song với s&agrave;n, người thẳng hai ch&acirc;n rộng bằng vai.</li>\r\n	<li>Đưa ch&acirc;n tr&aacute;i ra ph&iacute;a sau ch&acirc;n phải, hạ thấp gối d&ugrave;ng mũi ch&acirc;n l&agrave;m trụ nh&uacute;n mạnh v&agrave; trở lại vị tr&iacute; ban đầu.</li>\r\n</ul>\r\n\r\n<p><img alt=\"tập mông cho nữ\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-mong-cho-nu.jpg\" style=\"height:309px; width:600px\" title=\"Lưu ý: tạ đòn khá nặng nên chỉ khi đủ thể trạng mới áp dụng bài tập mông to với tạ đòn.\" /></p>\r\n\r\n<p><em>Lưu &yacute;: tạ đ&ograve;n kh&aacute; nặng n&ecirc;n chỉ khi đủ thể trạng mới &aacute;p dụng b&agrave;i tập m&ocirc;ng to với tạ đ&ograve;n.</em></p>\r\n\r\n<h3>2.2 B&agrave;i tập m&ocirc;ng kh&ocirc;ng to ch&acirc;n</h3>\r\n\r\n<p>Phần lớn chị em lo lắng những b&agrave;i tập gym m&ocirc;ng to sẽ k&eacute;o theo đ&ugrave;i, ch&acirc;n bị to ra. Với hệ thống những b&agrave;i tập được huấn luyện vi&ecirc;n chuy&ecirc;n nghiệp chia sẻ đảm bảo m&ocirc;ng to ch&acirc;n thon như &yacute;.</p>\r\n\r\n<p><strong>[1] Hip Thrust</strong></p>\r\n\r\n<p>Lưu &yacute; trước khi tập:</p>\r\n\r\n<p>Đ&acirc;y l&agrave; b&agrave;i tập kh&oacute;, đ&ograve;i hỏi kết hợp của kh&eacute;o l&eacute;o v&agrave; sức nặng nhiều n&ecirc;n những buồi đầu tốt nhất bạn cần nhờ HLV hướng dẫn chi tiết tr&aacute;nh thực hiện sai thao t&aacute;c.</p>\r\n\r\n<p>Thực hiện:</p>\r\n\r\n<ul>\r\n	<li>Phần đầu v&agrave; vai đặt tr&ecirc;n ghế ngang.</li>\r\n	<li>Ch&acirc;n đặt xuống s&agrave;n giữ th&acirc;n người kh&ocirc;ng chạm đất.</li>\r\n	<li>Hai tay cầm v&agrave;o hai đầu của tạ từ từ d&ugrave;ng trọng lực ở ch&acirc;n đẩy h&ocirc;ng l&ecirc;n cao tới khi lưng, đầu, đ&ugrave;i tạo th&agrave;nh 1 đường thẳng th&igrave; giữ nguy&ecirc;n 3 gi&acirc;y.</li>\r\n	<li>Thở ra v&agrave; trở về vị tr&iacute; ban đầu</li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập mông cho nữ tại phòng gym\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-cho-nu-tai-phong-gym.jpg\" style=\"height:452px; width:461px\" title=\"Thực hiện 10 lần/ hiệp và nghỉ 1 phút trước khi tập hiệp tiếp theo\" /></p>\r\n\r\n<p><em>Thực hiện 10 lần/ hiệp v&agrave; nghỉ 1 ph&uacute;t trước khi tập hiệp tiếp theo</em></p>\r\n\r\n<p><strong>[2] Hyper &ndash; Extentions</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; b&agrave;i tập gi&uacute;p m&ocirc;ng n&acirc;ng cao, cong tự nhi&ecirc;n. Tuy động t&aacute;c kh&ocirc;ng qu&aacute; phức tạp nhưng hiệu quả đem lại cho v&ugrave;ng m&ocirc;ng kh&ocirc;ng hề nhỏ.</p>\r\n\r\n<p>Lưu &yacute;: Tập từng bước chậm, kh&ocirc;ng cần gấp vội để cơ m&ocirc;ng được t&aacute;c động mạnh, s&acirc;u.</p>\r\n\r\n<p>Sử dụng m&aacute;y Hyper &ndash; Extentions, tự điều chỉnh th&ocirc;ng số m&aacute;y hoặc nhờ HLV hướng dẫn, điều chỉnh ph&ugrave; hợp với khả năng.</p>\r\n\r\n<p><img alt=\"các bài tập nâng mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/cac-bai-tap-nang-mong.jpg\" style=\"height:338px; width:600px\" title=\"Thân người gập xuống, hít sâu vào và thở ra khi nâng người lên dựa vào sức lực của cơ mông và đôi chân\" /></p>\r\n\r\n<p><em>Th&acirc;n người gập xuống, h&iacute;t s&acirc;u v&agrave;o v&agrave; thở ra khi n&acirc;ng người l&ecirc;n dựa v&agrave;o sức lực của cơ m&ocirc;ng v&agrave; đ&ocirc;i ch&acirc;n</em></p>\r\n\r\n<h3>2.3 C&aacute;c b&agrave;i tập isolation cho m&ocirc;ng (b&agrave;i tập c&ocirc; lập)</h3>\r\n\r\n<p>B&ecirc;n cạnh những b&agrave;i tập tổng hợp th&igrave; bạn n&ecirc;n c&oacute; những buổi tập isolation (b&agrave;i tập ri&ecirc;ng lẻ) cho m&ocirc;ng để khớp cơ m&ocirc;ng được vận động với mức độ tối đa. Với nh&oacute;m b&agrave;i tập n&agrave;y, m&ocirc;ng h&oacute;p hay m&ocirc;ng chảy xệ đều c&oacute; thể &aacute;p dụng để sở hữu số đo v&ograve;ng 3 l&yacute; tưởng.</p>\r\n\r\n<p><strong>[1] Donkey Kicks &ndash; M&ocirc;ng to bụng nhỏ</strong></p>\r\n\r\n<p>C&aacute;ch tập:</p>\r\n\r\n<p>Chuẩn bị: tư thế vu&ocirc;ng g&oacute;c với s&agrave;n ( nửa th&acirc;n người tr&ecirc;n song song với s&agrave;n, d&ugrave;ng tay thẳng v&agrave; đầu gối chống xuống s&agrave;n như h&igrave;nh.</p>\r\n\r\n<ul>\r\n	<li>Đ&aacute; ch&acirc;n tr&aacute;i l&ecirc;n ngang với th&acirc;n người thật mạnh v&agrave; hạ xuống từ từ về vị tr&iacute; ban đầu.</li>\r\n	<li>Thực hiện như cũ với ch&acirc;n b&ecirc;n phải v&agrave; lu&acirc;n phi&ecirc;n nhau.</li>\r\n</ul>\r\n\r\n<p><img alt=\"các bài tập isolation cho mông\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/cac-bai-tap-isolation-cho-mong.jpg\" style=\"height:457px; width:500px\" title=\"các bài tập isolation cho mông\" /></p>\r\n\r\n<p><em>C&aacute;c b&agrave;i tập isolation cho m&ocirc;ng vận động cả cơ m&ocirc;ng v&agrave; bụng</em></p>\r\n\r\n<p>Lưu &yacute;:</p>\r\n\r\n<ul>\r\n	<li>Số lần: 10 lần/ 1 buổi (cho 1 b&ecirc;n)</li>\r\n	<li>Số hiệp: 3 hiệp</li>\r\n	<li>Sau 1 tuần tập luyện c&oacute; thể tăng số lần l&ecirc;n 15 &ndash; 20 mỗi hiệp để hiệu quả hơn.</li>\r\n</ul>\r\n\r\n<p><strong>[2] Tập săn m&ocirc;ng với Cable Pull Through</strong></p>\r\n\r\n<p>Tiến h&agrave;nh b&agrave;i tập đều đặn, đ&uacute;ng lịch sẽ nhanh ch&oacute;ng c&oacute; được v&ograve;ng 3 săn chắc, kh&ocirc;ng c&ograve;n chảy xệ, nhăn nheo nữa. Đ&acirc;y l&agrave; một trong những b&agrave;i tập isolation cho m&ocirc;ng hiệu quả m&agrave; chị em n&ecirc;n tham khảo.</p>\r\n\r\n<p>C&aacute;ch tập m&ocirc;ng đ&uacute;ng c&aacute;ch:</p>\r\n\r\n<ul>\r\n	<li>Đứng trước m&aacute;y k&eacute;o, hai ch&acirc;n mở rộng, hai tay c&ugrave;ng nắm v&agrave;o tay cầm của d&acirc;y cao su k&eacute;o, d&acirc;y tập nằm giữa hai ch&acirc;n.</li>\r\n	<li>C&uacute;i th&acirc;n người xuống bằng c&aacute;ch hạ thấp ch&acirc;n lưng thẳng.</li>\r\n	<li>D&ugrave;ng lực ở ch&acirc;n v&agrave; c&aacute;nh tay k&eacute;o mạnh d&acirc;y l&ecirc;n v&agrave; th&acirc;n người đứng thẳng đồng thời siết m&ocirc;ng.</li>\r\n	<li>Giữ chặt d&acirc;y trong 3 gi&acirc;y v&agrave; thả ra nhẹ nh&agrave;ng.</li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập mông tại phòng gym\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-tai-phong-gym.jpg\" style=\"height:253px; width:500px\" title=\"bài tập mông tại phòng gym\" /></p>\r\n\r\n<p><em>Cable Pull Through phải thực hiện trong ph&ograve;ng gym</em></p>\r\n\r\n<p>Lưu &yacute;:</p>\r\n\r\n<ul>\r\n	<li>Số lần: 20 lần/ hiệp</li>\r\n	<li>Số hiệp: 3 hiệp trong tuần đầu v&agrave; 5 hiệp cho c&aacute;c tuần tiếp theo</li>\r\n</ul>\r\n\r\n<p><strong><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f525.svg\" /><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f525.svg\" /><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f525.svg\" />&nbsp;THAM KHẢO:&nbsp;<a href=\"https://benhvienthammykangnam.vn/tham-my-vong-3/bai-tap-mong-cho-nam/\">20 b&agrave;i tập m&ocirc;ng cho Nam ở nh&agrave;</a></strong></p>\r\n\r\n<p><strong>[3] Inner Thigh Machine &ndash; B&agrave;i tập m&ocirc;ng với m&aacute;y</strong></p>\r\n\r\n<p>Một b&agrave;i tập m&ocirc;ng với m&aacute;y kh&ocirc;ng to ch&acirc;n dễ tập d&agrave;nh cho chị em m&agrave; hiệu quả đem lại v&ocirc; c&ugrave;ng cao.</p>\r\n\r\n<p>C&aacute;ch thực hiện:</p>\r\n\r\n<ul>\r\n	<li>Ngồi v&agrave;o m&aacute;y tập ở tư thế thoải m&aacute;i nhất, đặt ch&acirc;n tay đ&uacute;ng vị tr&iacute; tr&ecirc;n m&aacute;y.</li>\r\n	<li>D&ugrave;ng lực ở v&ugrave;ng h&ocirc;ng để đẩy hai ch&acirc;n về hai ph&iacute;a đồng thời gồng m&ocirc;ng để c&aacute;c nh&oacute;m cơ m&ocirc;ng hoạt động t&iacute;ch cực.</li>\r\n	<li>K&eacute;o hai ch&acirc;n lại v&agrave; đẩy ra tiếp tục những lần tiếp theo của b&agrave;i tập.</li>\r\n</ul>\r\n\r\n<p><img alt=\"tập gym cho mông cong\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-gym-cho-mong-cong.jpg\" style=\"height:335px; width:500px\" title=\"tập gym cho mông cong\" /></p>\r\n\r\n<p><em>V&ograve;ng 3 cong, eo thon, đ&ugrave;i săn chắc với b&agrave;i tập isolation cho m&ocirc;ng</em></p>\r\n\r\n<p>Lưu &yacute;:</p>\r\n\r\n<ul>\r\n	<li>Số lần: 10 lần/ 1 hiệp</li>\r\n	<li>Số hiệp: 2 &ndash; 3 hiệp t&ugrave;y thể lực</li>\r\n	<li>Lựa chọn mức tạ ph&ugrave; hợp: mới bắt đầu mức thấp sau tăng dần.</li>\r\n</ul>\r\n\r\n<p><strong>[4] M&ocirc;ng cong săn chắc &ndash; Kneeling smith machine squat&nbsp;</strong></p>\r\n\r\n<p>Một biến thể của Squat m&ocirc;ng với tạ, Kneeling smith machine squat ph&ugrave; hợp với chị em c&oacute; thể lực khỏe c&oacute; thể &aacute;p dụng v&agrave; đạt được hiệu quả m&ocirc;ng cong nhanh hơn.</p>\r\n\r\n<p>Hướng dẫn:</p>\r\n\r\n<ul>\r\n	<li>Sử dụng m&aacute;y tập m&ocirc;ng với tạ đ&ograve;n.</li>\r\n	<li>Khụy hai đầu gối xuống s&agrave;n, m&ocirc;ng đặt l&ecirc;n hai g&oacute;t ch&acirc;n, hai tay cầm tạ đặt ph&iacute;a sau cổ.</li>\r\n	<li>D&ugrave;ng lực ưỡng h&ocirc;ng về trước, gồng cơ thể l&ecirc;n, siết m&ocirc;ng chặt v&agrave; hạ xuống từ từ sau 2 &ndash; 3 gi&acirc;y giữ tư thế.</li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập gym cho mông cong\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-gym-cho-mong-cong.jpg\" style=\"height:446px; width:500px\" title=\"bài tập gym cho mông cong\" /></p>\r\n\r\n<p>Lưu &yacute;:</p>\r\n\r\n<ul>\r\n	<li>Số lần: 12 lần</li>\r\n	<li>Số hiệp: 3 hiệp</li>\r\n</ul>\r\n\r\n<h3>2.4 Tập m&ocirc;ng với d&acirc;y c&aacute;p kh&aacute;ng lực, d&acirc;y đ&agrave;n hồi</h3>\r\n\r\n<p>D&acirc;y đ&agrave;n hồi (mini band) l&agrave; dụng cụ tập luyện m&ocirc;ng to săn chắc hiệu quả cao lại tiện lợi, nhỏ gọn c&oacute; thể tập tại ph&ograve;ng gym hoặc tại nh&agrave;.</p>\r\n\r\n<p>Những b&agrave;i tập m&ocirc;ng với d&acirc;y c&aacute;p kh&aacute;ng lực (d&acirc;y đ&agrave;n hồi) thực hiện đơn giản nhưng được đ&aacute;nh gi&aacute; đặc biệt tốt cho v&ugrave;ng m&ocirc;ng, h&ocirc;ng v&agrave; đ&ugrave;i.</p>\r\n\r\n<p>Thực hiện c&aacute;c b&agrave;i tập n&agrave;y mỗi ng&agrave;y để m&ocirc;ng to, bụng nhỏ chỉ sau 1 th&aacute;ng.</p>\r\n\r\n<p><strong>Động t&aacute;c 1: Ankle Jumping Jacks&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>Cho hai ch&acirc;n v&agrave;o b&ecirc;n trong d&acirc;y cao su tập luyện đặt ngang v&ugrave;ng mắt c&aacute; ch&acirc;n.</li>\r\n	<li>Người đứng thẳng, hai đầu gối hơi khụy mở rộng ngang h&ocirc;ng, hai b&agrave;n tay nắm hờ đặt trước ngực.</li>\r\n	<li>Nhảy nhẹ ch&acirc;n sang hai b&ecirc;n v&agrave; thu về vị tr&iacute; ban đầu.</li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập mông với dây cao su\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-voi-day-cao-su.jpg\" style=\"height:383px; width:500px\" title=\"Bài tập mông với dây cao su giúp nở mông sau vài tuần tập luyện\" /></p>\r\n\r\n<p><em>B&agrave;i tập m&ocirc;ng với d&acirc;y cao su gi&uacute;p nở m&ocirc;ng sau v&agrave;i tuần tập luyện</em></p>\r\n\r\n<p>Tập m&ocirc;ng với d&acirc;y đ&agrave;n hồi động t&aacute;c Ankle Jumping Jacks tối thiểu 20 lần v&agrave; nghỉ giữa động t&aacute;c 1 ph&uacute;t trước khi sang động t&aacute;c tiếp theo.</p>\r\n\r\n<p><strong><img alt=\"✅\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/2705.svg\" /><img alt=\"✅\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/2705.svg\" /><img alt=\"✅\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/2705.svg\" />&nbsp;XEM NGAY:&nbsp;<a href=\"https://benhvienthammykangnam.vn/tham-my-vong-3/cach-lam-mong-to/\">C&aacute;ch l&agrave;m cho m&ocirc;ng to nhanh tại nh&agrave;</a></strong></p>\r\n\r\n<p><strong>Động t&aacute;c 2: Lateral Band Walks &ndash; 20 lần</strong></p>\r\n\r\n<ul>\r\n	<li>D&acirc;y c&aacute;p kh&aacute;ng lực được đặt ở ngang mắt c&aacute; ch&acirc;n, hơi khom lưng, đầu gối khụy nhẹ, hai tay đan v&agrave;o nhau đưa ra ph&iacute;a trước.</li>\r\n	<li>Ch&acirc;n tr&aacute;i sang tr&aacute;i một bước vừa phải sao cho hai ch&acirc;n mở rộng bằng vai.</li>\r\n	<li>Ch&acirc;n phải bước tiếp sang b&ecirc;n tr&aacute;i một bước nhỏ để thu nhỏ khoảng c&aacute;ch hai ch&acirc;n lại ban đầu.</li>\r\n	<li>Đi tiếp tục về b&ecirc;n tr&aacute;i 3 bước v&agrave; lặp lại với b&ecirc;n ch&acirc;n phải.</li>\r\n</ul>\r\n\r\n<p><img alt=\"tập mông với dây đàn hồi\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-mong-voi-day-dan-hoi.jpg\" style=\"height:383px; width:500px\" title=\"tập mông với dây đàn hồi\" /></p>\r\n\r\n<p><em>Tập m&ocirc;ng với d&acirc;y đ&agrave;n hồi đơn giản th&iacute;ch hợp m&ocirc;ng bị h&oacute;p hai b&ecirc;n</em></p>\r\n\r\n<p><strong>Động t&aacute;c 3: Standing Glute Kicks &ndash; 20 lần mỗi b&ecirc;n</strong></p>\r\n\r\n<ul>\r\n	<li>D&acirc;y c&aacute;p kh&aacute;ng lực vẫn đặt tại vị tr&iacute; ngang mắt c&aacute; ch&acirc;n, th&acirc;n người thẳng, hai tay nắm nhẹ đặt trước ngực.</li>\r\n	<li>Dồn trọng lực cơ thể về ch&acirc;n phải, nhấc v&agrave; đ&aacute; ch&acirc;n phải ra sau (cao hết mức c&oacute; thể)</li>\r\n	<li>Đưa ch&acirc;n về tư thế ban đầu v&agrave; đặt mũi b&agrave;n ch&acirc;n chạm đất.</li>\r\n	<li>Thực hiện mỗi b&ecirc;n 20 lần mỗi ng&agrave;y.</li>\r\n</ul>\r\n\r\n<p><img alt=\"tập mông với dây cáp\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-mong-voi-day-cap.jpg\" style=\"height:383px; width:500px\" title=\"Tập mông với dây cáp mini band 10 phút mỗi ngày để nâng cơ mông\" /></p>\r\n\r\n<p><em>Tập m&ocirc;ng với d&acirc;y c&aacute;p mini band 10 ph&uacute;t mỗi ng&agrave;y để n&acirc;ng cơ m&ocirc;ng</em></p>\r\n\r\n<p><strong>Động t&aacute;c 4: Banded Walks &ndash; 20 lần</strong></p>\r\n\r\n<ul>\r\n	<li>Tư thế chuẩn bị như hai động t&aacute;c tr&ecirc;n: d&acirc;y đ&agrave;n hồi tr&ecirc;n mắt c&aacute; ch&acirc;n, người hơi khom.</li>\r\n	<li>Tiến 10 bước về ph&iacute;a trước với ch&acirc;n tr&aacute;i bước trước, ch&acirc;n phải bước sau rồi lại l&ugrave;i lại vị tr&iacute; ban đầu 10 bước.</li>\r\n	<li>Tiến h&agrave;nh lặp đi lặp lại khoảng 15 &ndash; 20 lần/ 1 buổi tập.</li>\r\n</ul>\r\n\r\n<p><img alt=\"cách tập mông với dây kháng lực\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/cach-tap-mong-voi-day-khang-luc.jpg\" style=\"height:383px; width:500px\" title=\"cách tập mông với dây kháng lực\" /></p>\r\n\r\n<p><em>C&aacute;ch tập m&ocirc;ng với d&acirc;y kh&aacute;ng lực khoa học</em></p>\r\n\r\n<p>Tập m&ocirc;ng với d&acirc;y cao su qua động t&aacute;c Banded Walks kh&ocirc;ng kh&oacute; nhưng lưu &yacute; lưng, vai, cổ phải lu&ocirc;n thẳng trong suốt qu&aacute; tr&igrave;nh tập luyện.</p>\r\n\r\n<p><strong>Động t&aacute;c 5: Squat to Lateral Leg Lifts &ndash; 20 lần</strong></p>\r\n\r\n<ul>\r\n	<li>Kh&aacute;c với c&aacute;c b&agrave;i tập m&ocirc;ng với d&acirc;y c&aacute;p kh&aacute;ng lực trước, trong động t&aacute;c thứ 5 bạn đặt cao d&acirc;y đ&agrave;n hồi l&ecirc;n ngang đầu gối.</li>\r\n	<li>Đứng hai ch&acirc;n mở rộng ngang h&ocirc;ng, tay nắm v&agrave;o nhau để trước ngực.</li>\r\n	<li>Hạ thấp người xuống bằng c&aacute;ch khụy gối nhưng lưng thẳng, cẳng ch&acirc;n vẫn thẳng.</li>\r\n	<li>Đẩy h&ocirc;ng hết sức về ph&iacute;a sau để c&aacute;c cơ m&ocirc;ng được vận động tối đa.</li>\r\n	<li>Đứng l&ecirc;n v&agrave; đ&aacute; ch&acirc;n tr&aacute;i sang ngang.</li>\r\n	<li>Tiếp tục thực hiện với đ&aacute; ch&acirc;n phải.</li>\r\n</ul>\r\n\r\n<p><img alt=\"các bài tập mông với dây kháng lực \" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/cac-bai-tap-mong-voi-day-khang-luc.jpg\" style=\"height:383px; width:500px\" title=\"các bài tập mông với dây kháng lực\" /></p>\r\n\r\n<p><em>C&aacute;c b&agrave;i tập m&ocirc;ng với d&acirc;y kh&aacute;ng lực &aacute;p dụng cho cả người gầy cũng hiệu quả</em></p>\r\n\r\n<p>C&aacute;c b&agrave;i tập m&ocirc;ng với d&acirc;y đ&agrave;n hồi kh&ocirc;ng kh&oacute; nhưng bạn phải thực hiện ki&ecirc;n tr&igrave;, tập m&ocirc;ng đ&uacute;ng c&aacute;ch mới c&oacute; thể đem lại hiệu quả như &yacute;.</p>\r\n\r\n<h3>2.5 Lịch tập m&ocirc;ng cho nữ khoa học</h3>\r\n\r\n<p>Tập tại nh&agrave; hay tại ph&ograve;ng gym đ&ograve;i hỏi bạn thực hiện trong một thời gian d&agrave;i v&agrave; theo đ&uacute;ng khoa học mới c&oacute; hiệu quả. Bạn kh&ocirc;ng thể tập luyện theo &yacute; th&iacute;ch, tập luyện thất thường bởi việc l&agrave;m n&agrave;y kh&ocirc;ng đem lại kết quả mong to tr&ograve;n như bạn mong muốn được.</p>\r\n\r\n<p>Một số lưu &yacute;:</p>\r\n\r\n<ul>\r\n	<li>Số buổi: 5 buổi/ 1 tuần nghỉ hai ng&agrave;y thứ bảy, chủ nhật</li>\r\n	<li>Thời gian nghỉ: 60 gi&acirc;y giữa c&aacute;c hiệp trong một b&agrave;i tập, nghỉ 2 ph&uacute;t giữa c&aacute;c b&agrave;i tập</li>\r\n</ul>\r\n\r\n<p>Lịch tập m&ocirc;ng cho nữ tại nh&agrave; hay ph&ograve;ng tập bạn đều c&oacute; thể &aacute;p dụng l&agrave;:</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>THỨ</td>\r\n			<td>B&Agrave;I TẬP</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thứ hai</td>\r\n			<td>Squat (3 hiệp mỗi hiệp 20 lần)\r\n			<p>&nbsp;</p>\r\n\r\n			<p>Lunges (3 hiệp mỗi hiệp 10 lần)</p>\r\n\r\n			<p>Leg (3 hiệp mỗi hiệp 15 lần)</p>\r\n\r\n			<p>Glute (4 hiệp mỗi hiệp 12 lần)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thứ ba</td>\r\n			<td>Lat Pulldown (3 hiệp mỗi hiệp 10 lần)\r\n			<p>&nbsp;</p>\r\n\r\n			<p>Cable Rows (3 hiệp, 10 lần/ hiệp)</p>\r\n\r\n			<p>Hyperextension (3 hiệp, 20 lần/ 1 hiệp)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thứ tư</td>\r\n			<td>Leg (3 12 lần), Plank (2 hiệp, 20 lần/ hiệp)\r\n			<p>&nbsp;</p>\r\n\r\n			<p>Crunch(3 hiệp, 15 lần/ hiệp)</p>\r\n\r\n			<p>Cardio (lựa chọn t&ugrave;y &yacute; đạp xe, m&aacute;y chạy bộ)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thứ năm</td>\r\n			<td>Bench Press (3 hiệp, 10 lần/ hiệp)\r\n			<p>&nbsp;</p>\r\n\r\n			<p>Shoulder Press (3 hiệp, 10 lần/ hiệp)</p>\r\n\r\n			<p>Push up (2 hiệp)</p>\r\n\r\n			<p>Push down (3 hiệp 15 lần)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Thứ s&aacute;u</td>\r\n			<td>Leg (3 hiệp 12 lần/ hiệp)\r\n			<p>&nbsp;</p>\r\n\r\n			<p>Glute (4 hiệp, 15 lần)</p>\r\n\r\n			<p>Calf Raise (1 hiệp)</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><em>Lịch tập m&ocirc;ng cho nữ chỉ mang t&iacute;nh chất tham khảo!</em></p>\r\n\r\n<h2><strong>III. L&yacute; do bạn tập m&ocirc;ng kh&ocirc;ng hiệu quả?</strong></h2>\r\n\r\n<p>Thực tế, nhiều chị em ki&ecirc;n tr&igrave; thực hiện c&aacute;c b&agrave;i tập m&ocirc;ng cho nữ tại nh&agrave; nhưng kh&ocirc;ng hiệu quả hoặc cải thiện kh&ocirc;ng nhiều. C&oacute; nhiều nguy&ecirc;n nh&acirc;n của t&igrave;nh trạng n&agrave;y, trong đ&oacute; chủ yếu l&agrave; c&aacute;c l&yacute; do sau:</p>\r\n\r\n<ul>\r\n	<li><strong>Luyện tập sai c&aacute;ch</strong></li>\r\n</ul>\r\n\r\n<p>Kh&ocirc;ng phải cứ tập đều đặn mỗi ng&agrave;y l&agrave; cho kết quả m&ocirc;ng căn tr&ograve;n, săn chắc. Nếu bạn tập sai b&agrave;i tập, kh&ocirc;ng đ&uacute;ng động t&aacute;c th&igrave; vừa kh&ocirc;ng hiệu quả lại khiến c&aacute;c cơ tay ch&acirc;n bị đau nhức, mỏi.</p>\r\n\r\n<p>Đặc biệt khi thực hiện tại nh&agrave;, kh&ocirc;ng được huấn luyện vi&ecirc;n hướng dẫn rất dễ thực hiện kh&ocirc;ng chuẩn động t&aacute;c. Mỗi b&agrave;i tập gi&uacute;p m&ocirc;ng cong eo thon, bạn cần t&igrave;m hiểu kĩ c&agrave;ng tường bước, c&oacute; thể xem th&ecirc;m qua video để nắm được ch&iacute;nh x&aacute;c trước khi tiến h&agrave;nh thực hiện l&acirc;u d&agrave;i.</p>\r\n\r\n<p><strong><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f449.svg\" /><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f449.svg\" /><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f449.svg\" />&nbsp;T&Igrave;M HIỂU:&nbsp;<a href=\"https://benhvienthammykangnam.vn/tham-my-vong-3/mong-cong/#I_-Mong_cong_la_dep_hay_xau\">M&ocirc;ng cong Đẹp hay Xấu</a></strong></p>\r\n\r\n<ul>\r\n	<li><strong>Chế độ dinh dưỡng kh&ocirc;ng ph&ugrave; hợp</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"bài tập mông nở\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-no.jpg\" style=\"height:400px; width:600px\" title=\"Ăn nhiều đồ cay nóng, dầu mỡ cản trở quá trình làm mông nảy nở dù tập luyện kiên trì\" /></p>\r\n\r\n<p><em>Ăn nhiều đồ cay n&oacute;ng, dầu mỡ cản trở qu&aacute; tr&igrave;nh l&agrave;m m&ocirc;ng nảy nở d&ugrave; tập luyện ki&ecirc;n tr&igrave;</em></p>\r\n\r\n<p>Dinh dưỡng cũng đ&oacute;ng g&oacute;p một phần kh&ocirc;ng nhỏ quyết định tới kết quả tập luyện cho h&ocirc;ng quả t&aacute;o căng tr&ograve;n, săn chắc. Bổ sung những thực phẩm nhiều vitamin, uống nhiều nước, &hellip; v&agrave;o thực đơn h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ khi thực hiện c&aacute;c b&agrave;i tập cơ m&ocirc;ng cho nữ, trong chế độ ăn uống bạn sử dụng đồ ăn nhanh, chứa nhiều dầu chi&ecirc;n, cay n&oacute;ng, đồ uống c&oacute; chất k&iacute;ch th&iacute;ch th&igrave; việc tập luyện sẽ kh&ocirc;ng hiệu quả v&agrave; hại sức khỏe.</p>\r\n\r\n<ul>\r\n	<li><strong>B&agrave;i tập kh&ocirc;ng đ&uacute;ng với t&igrave;nh trạng</strong></li>\r\n</ul>\r\n\r\n<p>Mỗi người sẽ c&oacute; mức độ khuyết điểm v&ugrave;ng m&ocirc;ng kh&aacute;c nhau n&ecirc;n kết quả sau khi &aacute;p dụng c&aacute;c b&agrave;i tập m&ocirc;ng tại nh&agrave; cũng sẽ c&oacute; sự kh&aacute;c biệt. C&oacute; người thực hiện sở hữu eo thon m&ocirc;ng to nhưng c&oacute; người lại kh&ocirc;ng cải thiện được.</p>\r\n\r\n<p>Thực tế, phương ph&aacute;p tập luyện chỉ th&iacute;ch hợp với những t&igrave;nh trạng hai b&ecirc;n m&ocirc;ng c&oacute; độ nh&ocirc; nhưng chưa đạt số đo l&yacute; tưởng, chảy xệ &iacute;t.</p>\r\n\r\n<p><img alt=\"bài tập mông hiệu quả\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/bai-tap-mong-hieu-qua.jpg\" style=\"height:226px; width:600px\" title=\"Tình trạng mông chảy xệ, da chùng nhão nặng đã tập luyện như không hiệu quả\" /></p>\r\n\r\n<p><em>T&igrave;nh trạng m&ocirc;ng chảy xệ, da ch&ugrave;ng nh&atilde;o nặng đ&atilde; tập luyện như kh&ocirc;ng hiệu quả</em></p>\r\n\r\n<p>Những trường hợp cơ m&ocirc;ng kh&ocirc;ng ph&aacute;t triển, b&ecirc;n to b&ecirc;n nhỏ hay qu&aacute; l&eacute;p (gần như kh&ocirc;ng c&oacute; độ nh&ocirc; cao) hay chảy xệ l&acirc;u năm, l&atilde;o h&oacute;a, sinh đẻ nhiều lần th&igrave; b&agrave;i tập gi&uacute;p m&ocirc;ng to hơn kh&ocirc;ng hiệu quả nhiều.</p>\r\n\r\n<h2><strong>IV. Giải ph&aacute;p n&acirc;ng m&ocirc;ng hiệu quả mọi trường hợp</strong></h2>\r\n\r\n<p>C&aacute;ch tập để m&ocirc;ng to tại nh&agrave; hay luyện tập trong ph&ograve;ng gym đ&atilde; qu&aacute; quen thuộc với chị em. Thế nhưng,&nbsp; do hiệu quả kh&ocirc;ng cao, thời gian thực hiện k&eacute;o d&agrave;i n&ecirc;n nhiều chị em đ&atilde; t&igrave;m tới một giải ph&aacute;p mới gi&uacute;p sở hữu h&ocirc;ng quả t&aacute;o nhanh ch&oacute;ng hơn, l&acirc;u d&agrave;i hơn.</p>\r\n\r\n<p>Với phương ph&aacute;p n&agrave;y, mọi t&igrave;nh trạng d&aacute;ng m&ocirc;ng kh&ocirc;ng ưng &yacute; đều c&oacute; thể khắc phục tối đa, cho kết quả số đo v&ograve;ng 3 đạt con số ti&ecirc;u chuẩn.</p>\r\n\r\n<p>Nhiều chị em thực sự bị thuyết phục, c&oacute; được d&aacute;ng m&ocirc;ng căng đầy như &yacute; sau khi &aacute;p dụng phương ph&aacute;p n&agrave;y.</p>\r\n\r\n<p><strong><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f525.svg\" /><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f525.svg\" /><img alt=\"????\" src=\"https://s.w.org/images/core/emoji/13.0.1/svg/1f525.svg\" />&nbsp;ĐỌC TIẾP:&nbsp;<a href=\"https://benhvienthammykangnam.vn/tham-my-vong-3/nang-mong-o-dau-dep/\">N&acirc;ng m&ocirc;ng ở đ&acirc;u đẹp v&agrave; an to&agrave;n nhất</a></strong></p>\r\n\r\n<p><img alt=\"tập mông gym\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-mong-gym.jpg\" style=\"height:333px; width:500px\" title=\"Trước: Mông bên to bên nhỏ - Sau: Mông được nâng cao, cân đối căng tròn quyến rũ\" /></p>\r\n\r\n<p><em>Trước: M&ocirc;ng b&ecirc;n to b&ecirc;n nhỏ &ndash; Sau: M&ocirc;ng được n&acirc;ng cao, c&acirc;n đối căng tr&ograve;n quyến rũ</em></p>\r\n\r\n<p><img alt=\"tập mông săn chắc\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-mong-san-chac.jpg\" style=\"height:333px; width:500px\" title=\"Trước: Mông xệ sau sinh - Sau: Vòng 3 nâng cao, to tròn, không còn da chùng nhão\" /></p>\r\n\r\n<p><em>Trước: M&ocirc;ng xệ sau sinh &ndash; Sau: V&ograve;ng 3 n&acirc;ng cao, to tr&ograve;n, kh&ocirc;ng c&ograve;n da ch&ugrave;ng nh&atilde;o</em></p>\r\n\r\n<p><img alt=\"tập cơ mông cho nữ\" src=\"https://benhvienthammykangnam.vn/wp-content/uploads/2019/10/tap-co-mong-cho-nu.jpg\" style=\"height:333px; width:500px\" title=\"Trước: Mông lép, cơ mông không phát triển - Sau: Mông căm tròn, sexy với hông quả táo mơ ước\" /></p>\r\n\r\n<p><em>Trước: M&ocirc;ng l&eacute;p, cơ m&ocirc;ng kh&ocirc;ng ph&aacute;t triển &ndash; Sau: M&ocirc;ng căm tr&ograve;n, sexy với h&ocirc;ng quả t&aacute;o mơ ước</em></p>\r\n', 4, 0, 0, 0, NULL, 0, 'user');
INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `SoLanXem`, `NoiBat`, `AnHien`, `tags`, `Duyet`, `NguoiDang`) VALUES
(6, 'vi', 'Giá Trị Dinh Dưỡng Của Gạo Trắng Đã Nấu Chín', 'Chất tinh bột chứa trong hạt gạo dưới hình thức carbohydrate (carb) và trong con người dưới dạng glucogen, gồm loại carb đơn giản như chất đường glucose, fructuose, lactose và sucrose; và loại carb hỗn tạp là một chuỗi phân tử glucose nối kết nhau chứa nhiều chất sợi. Tinh bột cung cấp phần lớn năng lượng cho con người. Gạo trắng chứa chỉ số đường huyết cũng khá cao nên thận trọng khi sữ dụng. Ngoài ra gạo chứa ít chất béo bão hòa, cholesterol và natri.Nó cũng là một nguồn tốt của Mangan .', 'uploads/images/1-chen-com-bao-nhieu-calo.jpg', '2021-06-02', 3, '<p><strong>Chất tinh bột chứa trong hạt gạo dưới h&igrave;nh thức carbohydrate (carb) v&agrave; trong con người dưới dạng glucogen, gồm loại carb đơn giản như chất đường glucose, fructuose, lactose v&agrave; sucrose; v&agrave; loại carb hỗn tạp l&agrave; một chuỗi ph&acirc;n tử glucose nối kết nhau chứa nhiều chất sợi. Tinh bột cung cấp phần lớn năng lượng cho con người. Gạo trắng chứa chỉ số đường huyết cũng kh&aacute; cao n&ecirc;n thận trọng khi sữ dụng. Ngo&agrave;i ra gạo chứa &iacute;t chất b&eacute;o b&atilde;o h&ograve;a, cholesterol v&agrave; natri.N&oacute; cũng l&agrave; một nguồn tốt của Mangan .</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<thead>\r\n		<tr>\r\n			<th colspan=\"2\">\r\n			<h4>TH&Agrave;NH PHẦN DINH DƯỠNG</h4>\r\n			</th>\r\n		</tr>\r\n		<tr>\r\n			<th>Khối lượng liều d&ugrave;ng</th>\r\n			<th>100g</th>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Th&agrave;nh phần dinh dưỡng trong mỗi liều</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Năng lượng 97</td>\r\n			<td>Từ fat 1,6</td>\r\n		</tr>\r\n		<tr>\r\n			<th colspan=\"2\">Nhu cầu hằng ng&agrave;y</th>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất b&eacute;o</strong>&nbsp;0,2g</td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- B&eacute;o b&atilde;o h&ograve;a&nbsp;</em>0g</td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Trans fat</em></td>\r\n			<td>~g</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Cholesterol 0mg</td>\r\n			<td>0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chất bột đường 21,1g</strong></td>\r\n			<td>7%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Chất xơ 1&nbsp;g</em></td>\r\n			<td>4%</td>\r\n		</tr>\r\n		<tr>\r\n			<td><em>- Đường 0g</em></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Protein 2g</td>\r\n			<td>4%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vitamin A0%</td>\r\n			<td>Vitamin C0%</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Canxi0%</td>\r\n			<td>Sắt1%</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\"><small>Ch&uacute; &yacute;: nhu cầu hằng ng&agrave;y dựa tr&ecirc;n t&iacute;nh to&aacute;n 1 người cần 2000 calories 1 ng&agrave;y, nhu cầu hằng ng&agrave;y của bạn c&oacute; thể cao hoặc thấp hơn t&ugrave;y thuộc v&agrave;o lượng calories bạn cần.</small></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>1. H&agrave;m lượng chất xơ v&agrave; c&aacute;c chất dinh dưỡng</h2>\r\n\r\n<p>Gạo trắng v&agrave; gạo n&acirc;u l&agrave; hai loại gạo phổ biến nhất v&agrave; c&oacute; nguồn gốc giống nhau. Gạo lứt l&agrave; to&agrave;n bộ hạt gạo, chứa c&aacute;m gi&agrave;u&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/nao-la-chat-xo-hoa-tan/\"><strong>chất xơ</strong></a>, mầm chứa chất dinh dưỡng v&agrave; nội nhũ gi&agrave;u&nbsp;<a href=\"https://www.vinmec.com/vi/tin-tuc/thong-tin-suc-khoe/carbohydrate-la-gi-va-co-tac-dung-gi/\"><strong>carbohydrate</strong></a>. Mặt kh&aacute;c, gạo trắng l&agrave; hạt gạo bị tước đi c&aacute;m v&agrave; mầm, chỉ để lại nội nhũ. Sau đ&oacute;, được chế biến để cải thiện hương vị, k&eacute;o d&agrave;i thời hạn sử dụng v&agrave; tăng cường c&aacute;c đặc t&iacute;nh chế biến. Gạo trắng được coi l&agrave; carbs rỗng v&igrave; đ&atilde; mất đi nguồn dinh dưỡng ch&iacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>2. Điểm chỉ số đường huyết</h2>\r\n\r\n<p><a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/dinh-duong/chi-so-duong-huyet-gi-cach-xac-dinh-thuc-pham-co-duong-huyet-cao-va-thap/\"><strong>Chỉ số đường huyết (GI)</strong></a>&nbsp;l&agrave; thước đo tốc độ cơ thể chuyển đổi carbs th&agrave;nh đường v&agrave; hấp thụ v&agrave;o m&aacute;u. Chỉ số dao động từ 0 đến 100 với c&aacute;c mốc như:</p>\r\n\r\n<ul>\r\n	<li>GI thấp: 55 trở xuống</li>\r\n	<li>GI trung b&igrave;nh: 56 đến 69</li>\r\n	<li>GI cao: 70 đến 100</li>\r\n</ul>\r\n\r\n<p>Thực phẩm c&oacute; chỉ số GI thấp được cho l&agrave; tốt hơn đối với những người mắc bệnh tiểu đường loại 2. Ngược lại, thực phẩm c&oacute; chỉ số GI cao c&oacute; thể g&acirc;y ra sự tăng đột biến lượng đường trong m&aacute;u..</p>\r\n\r\n<p>Gạo trắng c&oacute; chỉ số GI l&agrave; 64, trong khi gạo lứt c&oacute; chỉ số GI l&agrave; 55. V&igrave; vậy,&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/carbs-tot-carbs-xau-tai-sao-carbohydrate-co-van-de-voi-ban/\"><strong>carbs</strong></a>&nbsp;trong gạo trắng được chuyển h&oacute;a th&agrave;nh đường huyết nhanh hơn so với gạo n&acirc;u. Đ&acirc;y c&oacute; thể l&agrave; một l&yacute; do tại sao gạo trắng c&oacute; nguy cơ g&acirc;y ra bệnh&nbsp;<a href=\"https://vinmec.com/tin-tuc/hoi-dap-bac-si/bi-tieu-duong-tuyp-2-co-nen-cac-loai-cu-qua/\"><strong>tiểu đường loại 2</strong></a>&nbsp;cao hơn.</p>\r\n\r\n<p>Khi xem x&eacute;t c&aacute;c nghi&ecirc;n cứu ở hơn 350.000 người, c&aacute;c nh&agrave; nghi&ecirc;n cứu ph&aacute;t hiện ra rằng những người ăn nhiều gạo trắng thường xuy&ecirc;n c&oacute; nguy cơ mắc bệnh tiểu đường loại 2 cao hơn những người &iacute;t ăn loại gạo n&agrave;y. Hơn nữa, mỗi khẩu phần cơm trắng c&oacute; khả năng l&agrave;m tăng 11% nguy cơ mắc bệnh tiểu đường loại 2.</p>\r\n\r\n<h2>3. Nguy cơ hội chứng chuyển h&oacute;a</h2>\r\n\r\n<p><a href=\"https://www.vinmec.com/vi/tin-tuc/thong-tin-suc-khoe/suc-khoe-tong-quat/hoi-chung-chuyen-hoa/\"><strong>Hội chứng chuyển h&oacute;a</strong></a>&nbsp;l&agrave; t&ecirc;n của một nh&oacute;m c&aacute;c yếu tố l&agrave;m tăng nguy cơ mắc c&aacute;c t&igrave;nh trạng sức khỏe, chẳng hạn như bệnh tim, tiểu đường loại 2 v&agrave; đột quỵ.</p>\r\n\r\n<p>Những yếu tố n&agrave;y bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Huyết &aacute;p cao</li>\r\n	<li>Đường huyết cao</li>\r\n	<li>Mức chất b&eacute;o trung t&iacute;nh cao</li>\r\n	<li>Thừa mỡ bụng</li>\r\n	<li>Mức&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/chi-so-hdl-cholesterol-trong-mau-cao-co-y-nghia-gi/\"><strong>cholesterol c&oacute; lợi HDL</strong></a>&nbsp;thấp</li>\r\n</ul>\r\n\r\n<p>C&aacute;c nghi&ecirc;n cứu đ&atilde; chỉ ra rằng những người thường xuy&ecirc;n ăn một lượng lớn gạo trắng c&oacute; nguy cơ mắc hội chứng chuyển h&oacute;a cao hơn, đặc biệt l&agrave; người trưởng th&agrave;nh ở ch&acirc;u &Aacute;. Trong khi c&aacute;c nghi&ecirc;n cứu đ&atilde; kết luận mối li&ecirc;n hệ giữa việc ti&ecirc;u thụ gạo trắng v&agrave; bệnh tiểu đường, mối li&ecirc;n hệ giữa gạo trắng v&agrave; bệnh tim vẫn chưa được chứng minh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, những người ti&ecirc;u thụ gạo lứt thường xuy&ecirc;n được cho l&agrave; c&oacute; nguy cơ mắc bệnh tim thấp hơn. V&iacute; dụ, người trưởng th&agrave;nh ti&ecirc;u thụ nhiều ngũ cốc nguy&ecirc;n hạt như&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/dinh-duong/gia-tri-dinh-duong-cua-gao-lut/\"><strong>gạo lứt</strong></a>&nbsp;c&oacute; thể c&oacute; nguy cơ mắc bệnh tim thấp hơn tới 21% so với những người kh&ocirc;ng hoặc &iacute;t ti&ecirc;u thụ loại thực phẩm n&agrave;y. Gạo lứt cũng c&oacute; chứa lignans, một hợp chất thực vật đ&atilde; được chứng minh l&agrave; gi&uacute;p giảm huyết &aacute;p, giảm lượng chất b&eacute;o trong m&aacute;u v&agrave;&nbsp;<strong>giảm độ cứng động mạch</strong>.</p>\r\n\r\n<h2>4. Hiệu quả giảm c&acirc;n</h2>\r\n\r\n<p><img alt=\"Thuốc tránh thai có thể khiến cơ thể tăng cân tích nước\" src=\"https://vinmec-prod.s3.amazonaws.com/images/20191127_155618_575153_uong-thuoc-tranh-th.max-1800x1800.jpg\" /></p>\r\n\r\n<p>Một số nghi&ecirc;n cứu đ&atilde; cho thấy chế độ ăn nhiều ngũ cốc tinh chế như gạo trắng c&oacute; thể g&acirc;y tăng c&acirc;n, tăng mỡ bụng v&agrave; b&eacute;o ph&igrave;</p>\r\n\r\n<p>Gạo trắng được ph&acirc;n loại l&agrave; ngũ cốc tinh chế v&igrave; đ&atilde; bị xử l&yacute; bỏ đi c&aacute;m v&agrave; mầm c&oacute; trong hạt gạo. Trong khi nhiều nghi&ecirc;n cứu đ&atilde; cho thấy chế độ ăn nhiều ngũ cốc tinh chế c&oacute; thể g&acirc;y ra b&eacute;o ph&igrave; v&agrave; tăng c&acirc;n, th&igrave; những kết quả n&agrave;y kh&ocirc;ng c&oacute; t&iacute;nh nhất qu&aacute;n khi thử nghiệm tr&ecirc;n&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/dinh-duong/gia-tri-dinh-duong-cua-gao-lut/\"><strong>gạo trắng</strong></a>.</p>\r\n\r\n<p>Một số nghi&ecirc;n cứu đ&atilde; cho thấy chế độ ăn nhiều ngũ cốc tinh chế như gạo trắng c&oacute; thể g&acirc;y tăng c&acirc;n, tăng mỡ bụng v&agrave; b&eacute;o ph&igrave;, trong khi c&aacute;c nghi&ecirc;n cứu kh&aacute;c kh&ocirc;ng t&igrave;m thấy mối tương quan. Th&ecirc;m v&agrave;o đ&oacute;, chế độ ăn ki&ecirc;ng bao gồm gạo trắng đ&atilde; được chứng minh l&agrave; c&oacute; t&aacute;c dụng th&uacute;c đẩy&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/san-phu-khoa-va-ho-tro-sinh-san/giam-can-va-hormone/\"><strong>giảm c&acirc;n</strong></a>, đặc biệt l&agrave; ở c&aacute;c quốc gia nơi loại gạo n&agrave;y l&agrave; một loại thực phẩm h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>V&igrave; vậy, gạo trắng dường như kh&ocirc;ng g&acirc;y bất lợi hay c&oacute; lợi &iacute;ch g&igrave; đ&aacute;ng kể trong việc giảm c&acirc;n. Tuy nhi&ecirc;n, chế độ ăn nhiều&nbsp;<a href=\"https://www.vinmec.com/vi/tin-tuc/thong-tin-suc-khoe/chon-ngu-coc-nguyen-hat-de-tang-cuong-suc-khoe-va-giup-ban-giam-can/\"><strong>ngũ cốc nguy&ecirc;n hạt</strong></a>&nbsp;như&nbsp;<a href=\"https://www.vinmec.com/vi/tin-tuc/thong-tin-suc-khoe/dinh-duong/gia-tri-dinh-duong-cua-gao-lut/\"><strong>gạo lứt</strong></a>&nbsp;đ&atilde; được chứng minh một c&aacute;ch nhất qu&aacute;n c&oacute; thể hỗ trợ giảm c&acirc;n v&agrave; gi&uacute;p duy tr&igrave; trọng lượng huyết khỏe mạnh. Do đ&oacute;, gạo lứt c&oacute; thể l&agrave; lựa chọn h&agrave;ng đầu cho việc giảm c&acirc;n.</p>\r\n\r\n<h2>5. H&agrave;m lượng thạch t&iacute;n</h2>\r\n\r\n<p>Gạo được trồng ở một số v&ugrave;ng địa l&yacute; nhất định c&oacute; thể bị nhiễm asen.</p>\r\n\r\n<p>C&acirc;y l&uacute;a thường t&iacute;ch lũy nhiều asen hơn hầu hết c&aacute;c loại c&acirc;y lương thực kh&aacute;c. Điều n&agrave;y trở th&agrave;nh một vấn đề ở những nơi m&agrave; nguồn đất hoặc nguồn nước bị nhiễm thạch t&iacute;n. Hấp thụ nhiều asen c&oacute; thể g&acirc;y ra nguy cơ ung thư,&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/tim-mach/benh-tim-co-di-truyen-khong/\"><strong>bệnh tim</strong></a>&nbsp;v&agrave;&nbsp;<strong>tiểu đường tu&yacute;p 2</strong>. Ngo&agrave;i ra, thạch t&iacute;n g&acirc;y độc cho thần kinh v&agrave; c&oacute; thể ảnh hưởng đến chức năng n&atilde;o.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; một mối quan t&acirc;m đặc biệt đối với những người thường xuy&ecirc;n sử dụng sản phẩm từ gạo, đặc biệt l&agrave; trẻ em. C&aacute;c chuy&ecirc;n gia khuy&ecirc;n c&aacute;c bậc phụ huynh n&ecirc;n tr&aacute;nh cho trẻ ăn nhiều gạo hoặc c&aacute;c sản phẩm l&agrave;m từ gạo. Một số loại gạo chứa lượng asen thấp hơn c&aacute;c loại kh&aacute;c như gạo hoa nh&agrave;i v&agrave; gạo basmati, cũng như gạo được trồng ở khu vực Hy M&atilde; Lạp Sơn.</p>\r\n\r\n<p>Ngo&agrave;i ra, asen c&oacute; xu hướng t&iacute;ch lũy trong c&aacute;m. V&igrave; vậy, gạo lứt thường chứa lượng asen cao hơn gạo trắng</p>\r\n\r\n<h2>6. C&oacute; n&ecirc;n sử dụng gạo trắng</h2>\r\n\r\n<p><img alt=\"gạo trắng\" src=\"https://vinmec-prod.s3.amazonaws.com/images/20200418_122620_029206_gao-trang.max-1800x1800.jpg\" /></p>\r\n\r\n<p>Trong một số trường hợp, gạo trắng l&agrave; lựa chọn tốt hơn so với gạo lứt</p>\r\n\r\n<p>Một chế độ ăn &iacute;t chất xơ c&oacute; thể l&agrave;m giảm hoạt động của đường ti&ecirc;u h&oacute;a, cho ph&eacute;p ruột v&agrave; bụng nghỉ ngơi. Những chế độ ăn ki&ecirc;ng n&agrave;y c&oacute; thể l&agrave;m giảm c&aacute;c triệu chứng kh&oacute; chịu do&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/suc-khoe-tong-quat/benh-crohn-co-lay-khong/\"><strong>bệnh Crohn</strong></a>, vi&ecirc;m lo&eacute;t đại tr&agrave;ng, vi&ecirc;m ruột v&agrave; c&aacute;c rối loạn ti&ecirc;u h&oacute;a kh&aacute;c.</p>\r\n\r\n<p>Những người mắc chứng ợ n&oacute;ng, buồn n&ocirc;n v&agrave; n&ocirc;n hoặc những người hồi phục sau c&aacute;c tiểu phẫu hoặc phẫu thuật ảnh hưởng đến&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/tieu-hoa-gan-mat/chung-o-nong-o-tre-em-va-tre-so-sinh/\"><strong>hệ ti&ecirc;u h&oacute;a</strong></a>&nbsp;cũng c&oacute; thể &aacute;p dụng chế độ ăn n&agrave;y. V&igrave; vậy, gạo trắng thường được khuy&ecirc;n d&ugrave;ng trong những trường hợp được kể tr&ecirc;n, v&igrave; ch&uacute;ng &iacute;t chất xơ, cấu tạo đơn giản v&agrave; dễ ti&ecirc;u h&oacute;a.</p>\r\n\r\n<p>Gạo trắng thường bị nhiều người coi l&agrave; c&oacute; hại, trong khi thực tế, loại gạo n&agrave;y c&oacute; thể được sử dụng như một lựa tốt hơn gạo lứt trong một số t&igrave;nh huống. V&iacute; dụ, phụ nữ trải qua thời kỳ mang thai c&oacute; thể hấp thụ lượng lớn folate bổ sung c&oacute; trong gạo trắng. Ngo&agrave;i ra, những người c&oacute; chế độ ăn &iacute;t chất xơ v&agrave; người mắc chứng buồn n&ocirc;n hoặc&nbsp;<a href=\"https://vinmec.com/tin-tuc/thong-tin-suc-khoe/tieu-hoa-gan-mat/chung-o-nong-o-tre-em-va-tre-so-sinh/\"><strong>ợ n&oacute;ng</strong></a>&nbsp;c&oacute; thể thấy gạo trắng dễ ti&ecirc;u h&oacute;a hơn v&agrave; kh&ocirc;ng g&acirc;y ra c&aacute;c triệu chứng kh&oacute; chịu như gạo lứt.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, gạo lứt vẫn l&agrave; lựa chọn tốt hơn trong hầu hết trường hợp v&igrave; loại gạo n&agrave;y chứa nhiều vitamin, kho&aacute;ng chất, axit amin thiết yếu v&agrave; c&aacute;c hợp chất từ thực vật. Đồng thời cũng c&oacute; chỉ số đường huyết thấp hơn, carbs từ gạo lứt được chuyển h&oacute;a th&agrave;nh đường huyết chậm hơn, từ đ&oacute; c&oacute; lợi cho những người mắc bệnh tiểu đường hoặc tiền tiểu đường.</p>\r\n', 3, 0, 0, 0, NULL, 0, 'user'),
(7, 'vi', 'HIIT LÀ GÌ? BÀI TẬP HIIT GIẢM MỠ TOÀN THÂN NHANH TRONG 30 NGÀY', 'Để đạt được kết quả tốt thì chắc chắc HIIT là cực kỳ thử thách. Nó không phải bài tập mà bạn có thể làm trong khi đọc tạp chí hay tán gẫu với bạn bè. Bạn sẽ phải chăm chỉ tập trong suốt khoảng thời gian này nếu muốn giảm mỡ toàn thân nhanh và hiệu quả.', 'uploads/images/7fc4a700b764f9d8f5cc9e30bf845e92.jpg', '2021-06-02', 3, '<h2><strong>1. HIIT l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>HIIT l&agrave; từ viết tắt của &ldquo;<em>High intensity interval training</em>&rdquo;. B&agrave;i tập HIIT l&agrave; xu hướng tập thể dục thứ 2 tr&ecirc;n thế giới (theo&nbsp;<em>Nghi&ecirc;n cứu to&agrave;n cầu về xu hướng thể thao của trường ĐH Mỹ năm 2015</em>).</p>\r\n\r\n<p>HIIT, c&oacute; thể hiểu l&agrave;&nbsp;<em><strong>b&agrave;i tập cường độ cao ngắt qu&atilde;ng</strong></em>, nghĩa l&agrave; sự kết hợp của tập luyện ở cường độ cao c&ugrave;ng với c&aacute;c giai đoạn phục hồi ở cường độ thấp (luyện tập v&agrave; nghỉ ngơi). Khoa học chứng minh HIIT l&agrave; hiệu quả hơn nhiều so với c&aacute;ch đ&agrave;o tạo sức bền truyền thống (ET).</p>\r\n\r\n<p><img alt=\"hiit là gì\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/hiit-workouts-for-women-weight-loss.jpg\" style=\"height:700px; width:700px\" title=\"Chạy là 1 loại HIIT\" /></p>\r\n\r\n<p>Một số b&agrave;i tập HIIT th&ocirc;ng dụng nhất l&agrave;:&nbsp;<em>chạy v&agrave; đạp xe</em></p>\r\n\r\n<h2><strong>2. Lợi &iacute;ch của HIIT l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>Một nghi&ecirc;n cứu được c&ocirc;ng bố tr&ecirc;n&nbsp;<em>Tạp ch&iacute; Sinh l&yacute; học</em>&nbsp;cho thấy b&agrave;i tập HIIT mang lại tất cả c&aacute;c lợi &iacute;ch về sức khỏe v&agrave; thể lực ở trạng th&aacute;i ổn định.</p>\r\n\r\n<p><strong><em>HIIT c&oacute; thể l&agrave;m bạn th&ocirc;ng minh hơn</em></strong>.&nbsp;<em>Viện tim Montreal (MHI)</em>&nbsp;chỉ ra rằng, sau khi thực hiện lịch tập HIIT 2 lần 1 tuần v&agrave; trong v&ograve;ng 4 th&aacute;ng, những người tập đều đạt kết quả cao hơn rất nhiều trong c&aacute;c b&agrave;i kiểm tra tr&iacute; tuệ.</p>\r\n\r\n<p><strong><em>HIIT gi&uacute;p tăng cường trao đổi chất</em></strong>. Theo&nbsp;<em>một nghi&ecirc;n cứu tại Trường ĐH Colorado</em>, 150s tập ở hiit cường độ cao c&oacute; thể đốt ch&aacute;y 200 calo trong suốt 24 giờ nhờ tốc độ trao đổi chất.</p>\r\n\r\n<p><img alt=\"hiit cardio\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/top-10-benefits-of-HIIT.jpg\" style=\"height:449px; width:800px\" title=\"HIIT giúp giảm cân\" /></p>\r\n\r\n<p><strong><em>HIIT gi&uacute;p giảm mỡ to&agrave;n th&acirc;n</em></strong>. V&igrave; HIIT l&agrave; b&agrave;i tập ở cường độ cao, n&ecirc;n n&oacute; c&oacute; khả năng đốt ch&aacute;y mỡ rất hiệu quả.</p>\r\n\r\n<p>Để đạt được kết quả tốt th&igrave; chắc chắc HIIT l&agrave; cực kỳ thử th&aacute;ch. N&oacute; kh&ocirc;ng phải b&agrave;i tập m&agrave; bạn c&oacute; thể l&agrave;m trong khi đọc tạp ch&iacute; hay t&aacute;n gẫu với bạn b&egrave;. Bạn sẽ phải chăm chỉ tập trong suốt khoảng thời gian n&agrave;y nếu muốn giảm mỡ to&agrave;n th&acirc;n nhanh v&agrave; hiệu quả.</p>\r\n\r\n<p>C&aacute;i gi&aacute; của b&agrave;i tập HIIT l&agrave; sự ki&ecirc;n nhẫn của bạn để thấy được kết quả. Bạn c&oacute; thể cảm thấy đau đớn nhưng bạn chắc chắn sẽ kh&ocirc;ng buồn ch&aacute;n v&agrave; c&agrave;ng tập sẽ c&agrave;ng &quot;say&quot; v&igrave; th&agrave;nh quả bạn c&oacute; được.</p>\r\n\r\n<h2><strong>3. B&agrave;i tập HIIT giảm mỡ to&agrave;n th&acirc;n tại nh&agrave;</strong></h2>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; hướng dẫn c&aacute;c b&agrave;i tập hiit giảm mỡ bụng tại nh&agrave; đạt hiệu quả cao. Kh&ocirc;ng chỉ l&agrave; b&agrave;i tập hiit cho nam m&agrave; nữ cũng ho&agrave;n to&agrave;n c&oacute; thể tham khảo tập.</p>\r\n\r\n<h3><strong>Phần 1: L&agrave;m mỗi động t&aacute;c hiit 45s, nghỉ 15s sau mỗi b&agrave;i. Lặp lại 2 lần</strong></h3>\r\n\r\n<p>Ở phần đầu ti&ecirc;n, bạn sẽ bắt đầu với c&aacute;c b&agrave;i tập hiit đơn giản trước khi đến với b&agrave;i tập hiit giảm mỡ bụng kh&oacute; hơn một ch&uacute;t. Đọc kỹ hướng dẫn v&agrave; xem h&igrave;nh ảnh để hiểu được c&aacute;c tư thế HIIT.</p>\r\n\r\n<h4><strong>1.1. HIIT chạy bộ: Butt Kicks &ndash; 45s</strong></h4>\r\n\r\n<p>- Đứng 2 ch&acirc;n c&aacute;ch xa</p>\r\n\r\n<p>- Đ&aacute; g&oacute;t ch&acirc;n tr&aacute;i l&ecirc;n bọng đ&ugrave;i ph&iacute;a sau</p>\r\n\r\n<p>- Đặt ch&acirc;n trở lại l&uacute;c đầu v&agrave; lặp lại với ch&acirc;n phải. Tiếp tục tập xen kẽ nhanh trong 45s</p>\r\n\r\n<p><img alt=\"bài tập hiit\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/hrfgrth.gif\" style=\"height:860px; width:860px\" title=\"Butt Kicks\" /></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một dạng thức của hiit chạy bộ nhưng tại một vị tr&iacute; cố định. Phương ph&aacute;p hiit n&agrave;y t&aacute;c động trực tiếp đến cơ ch&acirc;n v&agrave; bụng của bạn. Việc thực hiện li&ecirc;n tục động t&aacute;c n&agrave;y sẽ gi&uacute;p bạn đốt ch&aacute;y mỡ v&ugrave;ng bụng.</p>\r\n\r\n<h4><strong>1.2. HIIT Jump Squats &ndash; 45s</strong></h4>\r\n\r\n<p>- Đứng ở tư thế b&agrave;n ch&acirc;n hơi rộng hơn so với khoảng c&aacute;ch h&ocirc;ng</p>\r\n\r\n<p>- Cong đầu gối v&agrave; m&ocirc;ng như ở tư thế ngồi tr&ecirc;n kh&ocirc;ng kh&iacute;, giữ ngực thẳng đứng</p>\r\n\r\n<p>- Nhảy l&ecirc;n kh&ocirc;ng trung cao nhất c&oacute; thể, hạ c&aacute;nh nhẹ nh&agrave;ng</p>\r\n\r\n<p>- L&agrave;m c&agrave;ng nhiều lần c&agrave;ng tốt trong 45s</p>\r\n\r\n<p><img alt=\"tập hiit\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/SQUAT_JUMP.gif\" style=\"height:860px; width:860px\" title=\"Jump Squats\" /></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một b&agrave;i tập hiit tại nh&agrave; rất phổ biến. Kh&ocirc;ng giống ho&agrave;n to&agrave;n tư thế squat v&igrave; động t&aacute;c n&agrave;y đ&ograve;i hỏi ch&acirc;n phải hoạt động li&ecirc;n tục để đẩy m&ocirc;ng l&ecirc;n. V&ograve;ng 3 của bạn sẽ được cải thiện rất nhiều khi thực hiện c&aacute;ch tập hiit n&agrave;y.</p>\r\n\r\n<h4><strong>1.3. HIIT Burpees &ndash; 45s</strong></h4>\r\n\r\n<p>- Đứng c&aacute;ch 2 ch&acirc;n xa h&ocirc;ng v&agrave; đưa l&ograve;ng b&agrave;n tay xuống s&agrave;n</p>\r\n\r\n<p>- Nhảy ch&acirc;n trở lại ở phần tr&ecirc;n của thảm, n&acirc;ng h&ocirc;ng của bạn l&ecirc;n</p>\r\n\r\n<p>- Cong khuỷu tay v&agrave; thực hiện 1 động t&aacute;c chống đẩy</p>\r\n\r\n<p>- Nhảy ch&acirc;n của bạn ra ngo&agrave;i b&agrave;n tay. Đứng l&ecirc;n, nhảy cao nhất c&oacute; thể, đưa c&aacute;nh tay l&ecirc;n cao</p>\r\n\r\n<p>- L&agrave;m c&agrave;ng nhiều lần c&agrave;ng tốt trong 45s</p>\r\n\r\n<p><img alt=\"các bài tập hiit\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/burpee.gif\" style=\"height:425px; width:640px\" title=\"HIIT Burpees\" /></p>\r\n\r\n<p>Một trong những b&agrave;i tập hiit giảm mỡ bụng v&agrave; tăng sức mạnh to&agrave;n th&acirc;n cho cơ thể tốt nhất. Kh&ocirc;ng chỉ vận động ch&acirc;n, tay m&agrave; to&agrave;n bộ c&aacute;c v&ugrave;ng cơ kh&aacute;c của bạn cũng cần tham gia v&agrave;o b&agrave;i tập hiit n&agrave;y.</p>\r\n\r\n<h4><strong>1.4. HIIT Mountain Climbers (Leo n&uacute;i) &ndash; 45s</strong></h4>\r\n\r\n<p>- Bắt đầu với tư thế&nbsp;<a href=\"https://thethao247.vn/318-so-huu-co-bung-san-chac-voi-5-dong-tac-plank-don-gian-d176545.html\" rel=\"nofollow\" target=\"_blank\">plank cơ bản</a>, đặt đầu gối ở dưới th&acirc;n, giữ c&aacute;c ng&oacute;n ch&acirc;n khỏi mặt đất</p>\r\n\r\n<p>- Đưa ch&acirc;n phải về vị tr&iacute; ban đầu</p>\r\n\r\n<p><ins><ins><iframe id=\"content-inread-video-670bb33931534c96a41e79d98f19dc8c-1622626583548\"></iframe></ins></ins></p>\r\n\r\n<p>- Đổi ch&acirc;n v&agrave; đưa đầu gối tr&aacute;i ở dưới ngực của bạn</p>\r\n\r\n<p>- Tiếp tục đổi ch&acirc;n như thể đang chạy tại chỗ</p>\r\n\r\n<p>L&agrave;m c&agrave;ng nhiều c&agrave;ng tốt trong v&ograve;ng 45s</p>\r\n\r\n<p><img alt=\"bài tập hiit giảm mỡ bụng\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/mountain-climber.gif\" style=\"height:533px; width:800px\" title=\"Mountain Climbers\" /></p>\r\n\r\n<h4><strong>1.5. HIIT Alternating Side Lunges &ndash; 45s</strong></h4>\r\n\r\n<p>- Bắt đầu đứng thẳng 2 ch&acirc;n</p>\r\n\r\n<p>- Bước ch&acirc;n tr&aacute;i sang tr&aacute;i, giữ ch&acirc;n phải thẳng v&agrave; uốn cong đầu gối tr&aacute;i của bạn</p>\r\n\r\n<p>- Bước ch&acirc;n tr&aacute;i trở lại tư thế đứng, lặp lại v&agrave; bước ch&acirc;n phải ra l&agrave;m tương tự</p>\r\n\r\n<p>Chuyển đổi qua lại v&agrave; l&agrave;m c&agrave;ng nhiều c&agrave;ng tốt trong 45s</p>\r\n\r\n<p><img alt=\"bài tập hiit cho nữ\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/623e81db6aecae39142bf9c2161ed8b6.gif\" style=\"height:387px; width:564px\" title=\"Alternating Side Lunges\" /></p>\r\n\r\n<p>Bạn sẽ bụng phẳng eo thon nhờ b&agrave;i tập hiit n&agrave;y. Cơ đ&ugrave;i, cơ m&ocirc;ng, ngực v&agrave; cả bụng đều được t&aacute;c động đến. Ch&uacute; &yacute; thẳng lưng khi thực hiện b&agrave;i tập.</p>\r\n\r\n<h4><strong>1.6. HIIT Jumping Lunges &ndash; 45s</strong></h4>\r\n\r\n<p>- Đứng 2 ch&acirc;n rộng bằng vai</p>\r\n\r\n<p>- Nhảy ch&acirc;n tr&aacute;i về ph&iacute;a trước, ch&acirc;n phải trở lại v&agrave; hạ c&aacute;nh ở vị tr&iacute; nhảy</p>\r\n\r\n<p>- Nhảy l&ecirc;n v&agrave; chuyển ch&acirc;n trong kh&ocirc;ng trung để hạ 1 bước nhảy với ch&acirc;n phải ở ph&iacute;a trước</p>\r\n\r\n<p>Tiếp tục nhảy qua lại v&agrave; dừng lại &iacute;t nhất c&oacute; thể trong 45s</p>\r\n\r\n<p><img alt=\"tập hiit là gì\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/HGcceH.gif\" style=\"height:830px; width:1280px\" title=\"Jumping Lunges\" /></p>\r\n\r\n<p>L&agrave;m lại tất cả c&aacute;c b&agrave;i tập HIIT n&agrave;y ở lần thực hiện 2.</p>\r\n\r\n<h3><strong>Phần 2: L&agrave;m mỗi b&agrave;i tập HIIT 40s, plank trong 20s rồi nghỉ 1 ph&uacute;t</strong></h3>\r\n\r\n<p>Sau khi khởi động tập hiit th&ocirc;ng qua phần 1, bạn sẽ đến với phần 2 cần nhiều nỗ lực v&agrave; thử th&aacute;ch v&igrave; c&oacute; sự kết hợp của động t&aacute;c plank.</p>\r\n\r\n<h4><strong>2.1. HIIT Mountain Climbers (Leo n&uacute;i) &ndash; 40s</strong></h4>\r\n\r\n<p>- Bắt đầu ở tư thế plank cao, để đầu gối phải dưới th&acirc;n, giữ c&aacute;c ng&oacute;n ch&acirc;n khỏi mặt đất</p>\r\n\r\n<p>- Đưa ch&acirc;n phải về vị tr&iacute; bắt đầu</p>\r\n\r\n<p>- Đổi ch&acirc;n v&agrave; đưa đầu gối tr&aacute;i để ở dưới ngực của bạn</p>\r\n\r\n<p>- Tiếp tục đổi ch&acirc;n như thể bạn đang chạy tại chỗ</p>\r\n\r\n<p>Thực hiện c&agrave;ng nhiều c&agrave;ng tốt trong 40s</p>\r\n\r\n<p><img alt=\"bài tập hiit giảm mỡ toàn thân\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/ee19b66eeab9a9d9f8d3f7ea3b16679b.gif\" style=\"height:447px; width:673px\" title=\"Mountain Climbers\" /></p>\r\n\r\n<h4><strong>2.2. HIIT Forearm Plank (Plank tay trước) &ndash; Giữ 20s</strong></h4>\r\n\r\n<p>- Bắt đầu với cẳng tay v&agrave; đầu gối đặt tr&ecirc;n mặt đất, rộng bằng tay</p>\r\n\r\n<p>- Nhấc đầu gối l&ecirc;n khỏi mặt đất v&agrave; đẩy ch&acirc;n ra sau để đưa cơ thể mở rộng ho&agrave;n to&agrave;n, cơ thể tạo ra một đường thẳng</p>\r\n\r\n<p>- Giữ h&ocirc;ng của bạn n&acirc;ng l&ecirc;n, giữ cho cổ thẳng h&agrave;ng với cột sống</p>\r\n\r\n<p>Giữ tư thế trong khoảng20s</p>\r\n\r\n<p><img alt=\"hiit workout\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/forearm-plank.gif\" style=\"height:411px; width:654px\" title=\"Forearm Plank\" /></p>\r\n\r\n<p>Bạn sẽ kh&ocirc;ng giữ y&ecirc;n tư thế plank trong 40s, m&agrave; cơ thể cần c&oacute; sự di chuyển l&ecirc;n xuống với dạng hiit workout n&agrave;y để bản th&acirc;n c&oacute; thử th&aacute;ch thực sự.</p>\r\n\r\n<h4><strong>2.3. B&agrave;i tập HIIT Plank Jacks &ndash; 40s</strong></h4>\r\n\r\n<p>- Ở tư thế plank cao</p>\r\n\r\n<p>- Giữ phần trung t&acirc;m của cơ thể,&nbsp; nhảy ch&acirc;n ra ngo&agrave;i rồi v&agrave;o trong</p>\r\n\r\n<p>Thực hiện động t&aacute;c nhiều nhất c&oacute; thể trong 40s</p>\r\n\r\n<p><em>Động t&aacute;c Forearm Plank &ndash; giữ khoảng 20s rồi d&agrave;nh 1 ph&uacute;t nghỉ ngơi</em></p>\r\n\r\n<p><em><img alt=\"bài tập hiit tại nhà\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/plank-jack.gif\" style=\"height:1333px; width:2000px\" title=\"Plank Jacks\" /></em></p>\r\n\r\n<h4><strong>2.4. HIIT Lateral Plank Walks &ndash; 40s</strong></h4>\r\n\r\n<p>- Bắt đầu ở tư thế plank cao</p>\r\n\r\n<p>- Bước ch&acirc;n phải v&agrave; tay phải sang b&ecirc;n phải ngay sau đ&oacute; l&agrave; ch&acirc;n tr&aacute;i v&agrave; tay tr&aacute;i</p>\r\n\r\n<p>- Thực hiện một v&agrave;i bước theo một hướng, rồi theo hướng ngược lại. L&agrave;m c&agrave;ng nhiều c&agrave;ng tốt trong 40s</p>\r\n\r\n<p><img alt=\"hiit 30 ngày\" src=\"https://cdn-thethao247.com/upload/nguyetdt/2019/08/02/lateral-plank.gif\" style=\"height:532px; width:800px\" title=\"Lateral Plank Walks\" /></p>\r\n\r\n<p><em>Động t&aacute;c Forearm Plank &ndash; giữ khoảng 20s rồi d&agrave;nh 1 ph&uacute;t nghỉ ngơi</em></p>\r\n\r\n<p>Cuối c&ugrave;ng, l&agrave;m lại to&agrave;n bộ động t&aacute;c ở phần 2 trong lần tập thứ 2</p>\r\n\r\n<p>Thực hiện HIIT đ&uacute;ng c&aacute;ch sẽ gi&uacute;p bạn tr&aacute;nh được chấn thương v&agrave; gặt h&aacute;i tối đa lợi &iacute;ch về thể lực v&agrave; sức khỏe. Tập HIIT ở cường độ cao nghe c&oacute; vẻ mệt mỏi v&agrave; thử th&aacute;ch, thậm ch&iacute; mạo hiểm nhưng điều kỳ diệu m&agrave; n&oacute; mang lại th&igrave; rất đ&aacute;ng để bạn đổ mồ h&ocirc;i!</p>\r\n', 4, 0, 0, 0, NULL, 0, 'user');
INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `SoLanXem`, `NoiBat`, `AnHien`, `tags`, `Duyet`, `NguoiDang`) VALUES
(8, 'vi', 'Cardio là gì? Bài tập giảm cân và đốt mỡ hiệu quả nhất', 'Một thân hình nặng nề, nhiều mỡ ở phần đùi và bụng khiến bạn mất tự tin khi diện đồ xuống phố hay gặp mặt bạn bè trong các buổi tiệc. Vấn đề đáng lo ngại này sẽ được giải quyết nếu bạn tìm đến các bài tập thể dục giảm cân. Để giảm cân cũng như muốn cơ thể mình săn chắc, bạn nên áp dụng quá trình luyện tập cardio ngay từ bây giờ.', 'uploads/images/25-bai-tap-dot-mo-thua-giam-can-hieu-qua-nhat-600X314.jpg', '2021-06-02', 3, '<p><strong>Một th&acirc;n h&igrave;nh nặng nề, nhiều mỡ ở phần đ&ugrave;i v&agrave; bụng khiến bạn mất tự tin khi diện đồ xuống phố hay gặp mặt bạn b&egrave; trong c&aacute;c buổi tiệc. Vấn đề đ&aacute;ng lo ngại n&agrave;y sẽ được giải quyết nếu bạn t&igrave;m đến c&aacute;c b&agrave;i tập thể dục giảm c&acirc;n. Để giảm c&acirc;n cũng như muốn cơ thể m&igrave;nh săn chắc, bạn n&ecirc;n &aacute;p dụng qu&aacute; tr&igrave;nh luyện tập cardio ngay từ b&acirc;y giờ.</strong></p>\r\n\r\n<p>Trước khi bắt tay v&agrave;o luyện tập cardio đề c&oacute; được v&oacute;c d&aacute;ng thon gọn v&agrave; quyết rũ, bạn n&ecirc;n hiễu r&otilde; kh&aacute;i niệm cardio l&agrave; g&igrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/cardio-la-gi.jpg\" /></p>\r\n\r\n<h2>Cardio l&agrave; g&igrave;?</h2>\r\n\r\n<p>Cardio l&agrave; từ viết tắt của Cardiovascular, nghĩa l&agrave; c&aacute;c b&agrave;i tập c&oacute; li&ecirc;n quan tới tim mạch. Đ&acirc;y kh&ocirc;ng phải l&agrave; một b&agrave;i tập cụ thể m&agrave; l&agrave; một phương ph&aacute;p kết hợp hệ thống nhiều b&agrave;i tập.</p>\r\n\r\n<p>C&aacute;c b&agrave;i tập Cardio gi&uacute;p cơ thể tăng đồng thời kiểm so&aacute;t nhịp tim, hỗ trợ qu&aacute; tr&igrave;nh lưu th&ocirc;ng m&aacute;u. Nhờ vậy, việc cung cấp nhiều oxy tới to&agrave;n bộ cơ thể n&oacute;i chung v&agrave; tế b&agrave;o cơ bắp n&oacute;i ri&ecirc;ng cũng được cải thiện theo hướng t&iacute;ch cực.</p>\r\n\r\n<p>Cardio c&ograve;n cho ph&eacute;p c&aacute;c tế b&agrave;o đốt ch&aacute;y mỡ thừa của cơ thể hiệu quả hơn trong qu&aacute; tr&igrave;nh tập luyện v&agrave; cả khi nghỉ ngơi.</p>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/loi-ich-cardio.jpg\" /></p>\r\n\r\n<h2>Lợi &iacute;ch m&agrave; Cardio mang lại cho bạn l&agrave; g&igrave;?</h2>\r\n\r\n<p>B&agrave;i tập Cardio được đ&aacute;nh gi&aacute; tốt hơn c&aacute;c b&agrave;i tập kh&aacute;c như tập tạ, kh&aacute;ng lực v&igrave; cardio gi&uacute;p điều khiển tăng nhịp tim theo &yacute; muốn của bạn v&agrave; duy tr&igrave; n&oacute; trong một khoảng thời gian nhất định.</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng t&igrave;m hiểu r&otilde; hơn những lợi &iacute;ch của c&aacute;c b&agrave;i tập Cardio bạn nh&eacute;.</p>\r\n\r\n<h3><strong>Đốt ch&aacute;y mỡ thừa hiệu quả</strong></h3>\r\n\r\n<p>C&oacute; thể n&oacute;i nhắc đến c&aacute;c b&agrave;i tập Cardio th&igrave; mọi người tr&ecirc;n thế giới đều nghĩ ngay đến t&iacute;nh năng đốt ch&aacute;y mỡ thừa hiệu quả. Kh&ocirc;ng c&oacute; g&igrave; bất ngờ khi n&oacute;i b&agrave;i tập n&agrave;y gi&uacute;p ti&ecirc;u hao rất nhiều năng lượng, giảm lượng mỡ thừa của cơ thể, đặc biệt l&agrave; lượng mỡ thừa ở phần h&ocirc;ng, đ&ugrave;i v&agrave; bụng, k&iacute;ch th&iacute;ch dạ d&agrave;y ti&ecirc;u h&oacute;a thức ăn, mang lại khả năng giảm c&acirc;n si&ecirc;u tốc chỉ trong v&ograve;ng một th&aacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/cardio-voi-bodyjam.jpg\" /></p>\r\n\r\n<p>Hầu như c&aacute;c b&agrave;i tập Cardio thường được c&aacute;c nh&agrave; khoa học nghi&ecirc;n cứu v&agrave; đề xuất đưa v&agrave;o kế hoạch tập luyện giảm c&acirc;n hiệu quả.</p>\r\n\r\n<h3><strong>Giảm stress</strong></h3>\r\n\r\n<p>C&aacute;c b&agrave;i tập Cardio gi&uacute;p m&aacute;u lưu th&ocirc;ng l&ecirc;n n&atilde;o nhiều hơn v&igrave; thế bạn c&oacute; thể giảm được &aacute;p lực c&ocirc;ng việc nhờ v&agrave;o việc luyện tập Cardio hằng ng&agrave;y. Giảm bớt căng thẳng trong cuộc sống l&agrave; bạn đang giảm bớt những nguy vấn đề sức khoẻ kh&aacute;c nữa đấy.</p>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/cardio-dance.JPG\" /></p>\r\n\r\n<h3><strong>Tăng cường thể chất, r&egrave;n luyện sức khoẻ</strong></h3>\r\n\r\n<p>Cũng như c&aacute;c b&agrave;i tập r&egrave;n luyện thể chất kh&aacute;c, Cardio gi&uacute;p bạn luyện tập sự dẻo dai, sức bền, cơ thể lu&ocirc;n cảm thấy thoải m&aacute;i v&agrave; tr&agrave;n đầy sức sống.</p>\r\n\r\n<h3><strong>Cải thiện hệ thống mạch m&aacute;u</strong></h3>\r\n\r\n<p>Tập b&agrave;i tập Cardio gi&uacute;p mở rộng c&aacute;c mạch m&aacute;u lớn nhỏ để oxy được lưu th&ocirc;ng tới cơ, gi&uacute;p cải thiện t&iacute;nh đ&agrave;n hồi, tăng cường qu&aacute; tr&igrave;nh lưu th&ocirc;ng m&aacute;u v&agrave; ổn định mức đường huyết hiệu quả.</p>\r\n\r\n<h3><strong>Cardio gi&uacute;p tăng khả năng phục hồi của cơ thể</strong></h3>\r\n\r\n<p>Cardio kết hợp rất nhiều b&agrave;i tập kh&aacute;c nhau v&agrave; đặc biệt c&aacute;c dạng b&agrave;i tập LISS Carido c&oacute; thể gi&uacute;p bạn phục hồi nhanh ch&oacute;ng hơn. Nếu như bạn vừa c&oacute; một buổi tập nặng th&igrave; h&atilde;y l&ecirc;n m&aacute;y tập chạy bộ thực hiện b&agrave;i tập đi bộ với cường độ thấp, n&oacute; sẽ l&agrave;m tăng khả năng xử l&yacute; acid lactic t&iacute;ch tụ trong cơ bắp.</p>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/cardio-cxworx.JPG\" /></p>\r\n\r\n<h3><strong>Cải thiện sức khỏe tim mạch</strong></h3>\r\n\r\n<p>Tim của bạn cũng được xem như l&agrave; một phần cơ bắp. Nếu kh&ocirc;ng được &ldquo;tập thể dục&rdquo;, tim cũng sẽ suy yếu. Tuy nhi&ecirc;n, nếu bạn thường gặp t&igrave;nh trạng thở dốc, tim đập nhanh khi chỉ mới chạy một đoạn hoặc chỉ mới l&ecirc;n cầu thang v&agrave;i bước, c&oacute; thể bạn sẽ phải cần đến c&aacute;c b&agrave;i tập Cardio.</p>\r\n\r\n<p>Cardio l&agrave; một b&agrave;i tập rất tốt cho nhịp tim, gi&uacute;p bạn thở mạnh v&agrave; s&acirc;u hơn cũng như giảm thiểu r&otilde; rệt nguy cơ mắc c&aacute;c bệnh về tim mạch.</p>\r\n\r\n<h2>Ph&acirc;n loại Cardio</h2>\r\n\r\n<p>Cardio được chia th&agrave;nh 2 loại l&agrave; Cardio cường độ thấp v&agrave; Cardio cường độ cao.</p>\r\n\r\n<h3><strong>Cardio cường độ thấp l&agrave; g&igrave;?</strong></h3>\r\n\r\n<p>Cardio cường độ thấp được gọi l&agrave; LISS. LISS l&agrave; từ viết tắt của Low Intensity Steady State (b&agrave;i tập cường độ trung b&igrave;nh/thấp ổn định). Bạn c&oacute; thể &aacute;p dụng LISS để phục hồi cơ thể v&agrave; đốt ch&aacute;y lượng mỡ thừa. Nếu bạn tập LISS trong khoảng thời gian 20-30 ph&uacute;t, cơ thể sẽ được phục hồi nhanh ch&oacute;ng. Nếu tập Liss trong khoảng thời gian 1 tiếng, calo trong cơ thể sẽ được đốt ch&aacute;y hiệu quả.</p>\r\n\r\n<h3><strong>Cardio cường độ cao l&agrave; g&igrave;?</strong></h3>\r\n\r\n<p>Cardio cường độ cao được gọi l&agrave; HIIT. HIIT l&agrave; từ viết tắt của High Intensive Interval Training (b&agrave;i tập cường độ cao ngắt qu&atilde;ng), một phương ph&aacute;p v&ocirc; c&ugrave;ng hiệu quả v&agrave; ph&ugrave; hợp với đa số mọi người.</p>\r\n\r\n<p>HIIT được &aacute;p dụng để đốt ch&aacute;y calo ph&aacute;t sinh trong v&agrave; sau buổi tập. Calo được đốt chủ yếu lấy từ chất b&eacute;o, đường v&agrave; protein. B&ecirc;n cạnh lợi &iacute;ch đốt nhiều calo th&igrave; Cardio cường độ cao c&ograve;n một lợi &iacute;ch kh&aacute;c nữa đ&oacute; l&agrave; l&agrave;m tăng hệ số trao đổi chất BMR của cơ thể l&ecirc;n, gi&uacute;p đốt năng lượng li&ecirc;n tục trong ng&agrave;y v&agrave; giảm mỡ nhanh hơn.</p>\r\n\r\n<p>V&igrave; l&agrave; cường độ cao, n&ecirc;n tối đa bạn chỉ tập được trong khoảng thời gian 20&ndash;30 ph&uacute;t. Thoạt nghe việc luyện tập 20 ph&uacute;t c&oacute; thể &iacute;t, nhưng nếu bạn luyện tập đ&uacute;ng c&aacute;ch, bạn sẽ cảm nhận được hiệu quả m&agrave; c&aacute;c b&agrave;i tập Cardio cường độ cao mang lại.&nbsp;</p>\r\n\r\n<h3><strong>Vậy khi n&agrave;o bạn n&ecirc;n tập HIIT, khi n&agrave;o n&ecirc;n tập LISS?</strong></h3>\r\n\r\n<p>Để an to&agrave;n v&agrave; đạt hiệu quả cao, bạn n&ecirc;n tập LISS v&agrave;o buổi s&aacute;ng l&uacute;c chưa ăn g&igrave;. Qu&aacute; tr&igrave;nh đốt chất b&eacute;o v&agrave; giảm mỡ bụng sẽ hiệu quả hơn rất nhiều. Ngược lại HIIT kh&ocirc;ng n&ecirc;n tập v&agrave;o s&aacute;ng sớm v&igrave; đ&acirc;y l&agrave; b&agrave;i tập cường độ cao, l&uacute;c n&agrave;y cơ thể kh&ocirc;ng c&oacute; đủ năng lượng sẽ dễ kiệt sức.</p>\r\n\r\n<h3><strong>Tập Cardio bạn c&oacute; cần phải &aacute;p dụng chế độ ăn ki&ecirc;ng hay kh&ocirc;ng?</strong></h3>\r\n\r\n<p>Sẽ hiệu quả hơn nếu bạn luyện tập Cardio v&agrave; vẫn duy tr&igrave; chế độ ăn uống của m&igrave;nh. Nếu bạn kh&ocirc;ng kiểm so&aacute;t được chế độ ăn uống hằng ng&agrave;y, c&oacute; thể việc luyện tập sẽ kh&ocirc;ng mang lại hiệu quả như mong muốn. V&igrave; thế, vẫn phải duy tr&igrave; chế độ ăn ki&ecirc;ng bạn nh&eacute;.</p>\r\n\r\n<h3><strong>Tập Cardio như thế n&agrave;o để hiệu quả v&agrave; kh&ocirc;ng mất cơ?</strong></h3>\r\n\r\n<p>HIIT chắc chắn sẽ đốt mỡ nhiều hơn so với LISS Cardio. Tuy nhi&ecirc;n, nếu bạn qu&aacute; lạm dụng th&igrave; rất dễ bị mất cơ. Một số b&iacute; quyết sau để gi&uacute;p tập Cardio hiệu quả:</p>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng n&ecirc;n tập Cardio khi chưa ăn g&igrave;.</li>\r\n	<li>Tập tạ v&agrave; HIIT n&ecirc;n c&aacute;ch nhau 8 tiếng.</li>\r\n	<li>Kết hợp chế độ dinh dưỡng hợp l&yacute; trong thời gian luyện tập.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Giới thi&ecirc;̣u 21 động t&aacute;c cardio giúp bạn cải thi&ecirc;̣n vóc dáng và th&ecirc;̉ lực</h2>\r\n\r\n<h3><strong>1. Động t&aacute;c đấm m&oacute;c</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/tap-cardio-voi-bodycombat.jpg\" /></p>\r\n\r\n<p>Động t&aacute;c n&agrave;y sẽ rất quen thuộc đối với những ai đ&atilde; từng biết đến boxing. Để thực hiện tốt động t&aacute;c Caridio n&agrave;y, bạn sẽ cần đến một bao c&aacute;t hoặc nhờ đến sự hỗ trợ của người kh&aacute;c)</p>\r\n\r\n<p>Bạn sẽ luyện tập động t&aacute;c n&agrave;y như sau:</p>\r\n\r\n<p>Đứng thẳng người, hai ch&acirc;n dang rộng bằng vai.</p>\r\n\r\n<p>Hai tay để ngang ngực v&agrave; giữ ở tư thế thủ.</p>\r\n\r\n<p>Kế đến, bạn đấm từ dưới l&ecirc;n tr&ecirc;n theo h&igrave;nh v&ograve;ng cung đồng thời hơi xoay người qua b&ecirc;n phải (nếu d&ugrave;ng tay tr&aacute;i) để đấm v&agrave;o bao c&aacute;t (hoặc tay người hỗ trợ).</p>\r\n\r\n<p>Sau đ&oacute; bạn thu tay về, đổi ta v&agrave; thực hiện tương tự.</p>\r\n\r\n<h3><strong>2. Động t&aacute;c chạy bộ n&acirc;ng cao gối</strong></h3>\r\n\r\n<p><strong><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/dong-tac-cardio-nang-cao-dui.jpg\" /></strong></p>\r\n\r\n<p>Động t&aacute;c n&agrave;y c&oacute; lẽ bạn đ&atilde; được học trong c&aacute;c b&agrave;i tập thể dục của trường l&uacute;c c&ograve;n l&agrave; học sinh phải kh&ocirc;ng n&agrave;o.</p>\r\n\r\n<p>C&aacute;ch thực hiện rất đơn giản:</p>\r\n\r\n<p>Đầu ti&ecirc;n, bạn đứng thẳng người, ch&acirc;n dang rộng bằng h&ocirc;ng.</p>\r\n\r\n<p>Thực hiện động t&aacute;c chạy tại chỗ v&agrave; cố gắng n&acirc;ng cao đ&ugrave;i nhất c&oacute; thể.</p>\r\n\r\n<p>N&acirc;ng cao đ&ugrave;i v&agrave; lu&ocirc;n giữ cho đ&ugrave;i tạo với bắp ch&acirc;n một g&oacute;c 90 độ.</p>\r\n\r\n<p>Động t&aacute;c n&agrave;y bạn thực hiện c&agrave;ng nhanh c&agrave;ng tốt.</p>\r\n\r\n<h3><strong>3. Động t&aacute;c chạy g&oacute;t chạm m&ocirc;ng</strong></h3>\r\n\r\n<p>Cũng kh&ocirc;ng qu&aacute; kh&oacute; đề c&oacute; thể h&igrave;nh dung ra động t&aacute;c n&agrave;y n&ecirc;n thực hiện như thế n&agrave;o.</p>\r\n\r\n<p>C&aacute;ch thực hiện rất đơn giản:</p>\r\n\r\n<p>Đầu ti&ecirc;n, bạn đứng thẳng người, ch&acirc;n dang rộng bằng h&ocirc;ng.</p>\r\n\r\n<p>Thưc hiện động t&aacute;c chạy tại chỗ sao cho g&oacute;t ch&acirc;n chạm v&agrave;o m&ocirc;ng.</p>\r\n\r\n<p>Động t&aacute;c n&agrave;y bạn cũng n&ecirc;n thực hiện nhanh để c&oacute; hiệu quả nh&eacute;.</p>\r\n\r\n<h3><strong>4. Động t&aacute;c đứng đấm thẳng</strong></h3>\r\n\r\n<p><strong><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/cardio-giam-can.jpg\" /></strong></p>\r\n\r\n<p>Đứng hai ch&acirc;n rộng bằng vai</p>\r\n\r\n<p>Bạn cần giữ mắt nh&igrave;n thẳng.</p>\r\n\r\n<p>Hai tay giữ trước ngực ở tư thế thủ</p>\r\n\r\n<p>Xoay h&ocirc;ng v&agrave; vai sang ph&iacute;a b&ecirc;n phải để đ&aacute;nh mạnh tay tr&aacute;i về ph&iacute;a trước mặt.</p>\r\n\r\n<p>Thu tay về v&agrave; thực hiện tay c&ograve;n lại.</p>\r\n\r\n<h3><strong>5. Động t&aacute;c Cardio leo n&uacute;i ch&eacute;o ch&acirc;n</strong></h3>\r\n\r\n<p><strong><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/bai-tap-cardio.jpg\" /></strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một b&agrave;i tập rất tốt cho việc giảm mỡ bụng. Khi tập b&agrave;i tập n&agrave;y, bạn h&atilde;y thử tưởng tượng m&igrave;nh đang đi leo n&uacute;i, nhưng thay v&igrave; bước những bước thẳng, bạn h&atilde;y bước ch&eacute;o ch&acirc;n.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; những c&aacute;ch thực hiện động t&aacute;c:</p>\r\n\r\n<p>Bạn v&agrave;o tư thế nằm chống đẩy.</p>\r\n\r\n<p>Co gối tr&aacute;i l&ecirc;n cao nhất c&oacute; thể v&agrave; đưa đầu gối của bạn hướng về ph&iacute;a eo b&ecirc;n phải.</p>\r\n\r\n<p>Sau đ&oacute; bạn nhanh ch&oacute;ng thu ch&acirc;n về v&agrave; đổi ch&acirc;n.</p>\r\n\r\n<h3><strong>6. Động t&aacute;c đi đều xoay tay</strong></h3>\r\n\r\n<p>Động t&aacute;c n&agrave;y bạn cần thực hiện như sau:</p>\r\n\r\n<p>Đầu ti&ecirc;n, tư thế đứng ch&acirc;n rộng bằng h&ocirc;ng.</p>\r\n\r\n<p>Thực hiện đi đều tại chỗ, n&acirc;ng gối cao ngang h&ocirc;ng.</p>\r\n\r\n<p>Xoay hai c&aacute;nh tay từ ph&iacute;a sau ra ph&iacute;a trước trong 30 gi&acirc;y</p>\r\n\r\n<p>Xoay tay theo hướng ngược lại v&agrave; cũng xoay trong 30 gi&acirc;y.</p>\r\n\r\n<h3><strong>7. Di chuyển ch&acirc;n sang ngang</strong></h3>\r\n\r\n<p>B&agrave;i tập n&agrave;y kh&aacute; l&yacute; tưởng trong việc gi&uacute;p bạn ti&ecirc;u hao calo đấy.</p>\r\n\r\n<p>Đầu ti&ecirc;n, bạn đứng thẳng với ch&acirc;n rộng bằng vai, hơi quỵ gối v&agrave; căng cứng cơ bụng.</p>\r\n\r\n<p>Kế đến, bạn di chuyển thật nhanh sang ngang 4 bước v&agrave; di chuyển ngược lại.</p>\r\n\r\n<p>B&agrave;i tập n&agrave;y, bạn nhớ l&agrave; di chuyển c&agrave;ng nhanh c&agrave;ng tốt nh&eacute;.</p>\r\n\r\n<h3><strong>8. Nhảy ngang tay vung l&ecirc;n cao</strong></h3>\r\n\r\n<p>B&agrave;i tập n&agrave;y chắc chắn kh&ocirc;ng thể thiếu trong qu&aacute; tr&igrave;nh luyện tập Cardio của bạn v&igrave; hiệu quả đốt ch&aacute;y calo của b&agrave;i tập n&agrave;y l&agrave; rất tốt.</p>\r\n\r\n<p>C&aacute;ch thực hiện:</p>\r\n\r\n<p>Bạn đứng thẳng người, hai ch&acirc;n kh&eacute;p lại, tay &aacute;p s&aacute;t v&agrave;o th&acirc;n.</p>\r\n\r\n<p>Thực hiện động t&aacute;c bật nhảy hai ch&acirc;n sang ngang rộng bằng vai v&agrave; kết hợp với hai tay giơ cao l&ecirc;n đỉnh đầu.</p>\r\n\r\n<p>Nhảy kh&eacute;p ch&acirc;n lại đồng thời thu hai tay về vị tr&iacute; ban đầu.</p>\r\n\r\n<p>Thực hiện động t&aacute;c n&agrave;y với tần suất nhanh sẽ gi&uacute;p bạn đạt hiệu quả cao.</p>\r\n\r\n<h3><strong>9. Động t&aacute;c chống đẩy tĩnh v&agrave; leo n&uacute;i</strong></h3>\r\n\r\n<p><strong><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/mountain-climber-cardio.jpg\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để giảm mỡ to&agrave;n th&acirc;n v&agrave; v&ugrave;ng bụng kh&ocirc;ng g&igrave; bằng c&aacute;ch vận dụng c&aacute;c b&agrave;i tập giảm mỡ bụng linh hoạt n&agrave;y sẽ gi&uacute;p v&ugrave;ng bụng săn chắc.</p>\r\n\r\n<p>Thực hiện động t&aacute;c như sau:</p>\r\n\r\n<p>Bạn để tay thẳng v&agrave; ch&acirc;n duỗi thẳng ở tư thế h&iacute;t đất.</p>\r\n\r\n<p>Luận phi&ecirc;n đưa 2 ch&acirc;n l&ecirc;n ph&iacute;a trước như chạy xe đạp.</p>\r\n\r\n<p>Bạn n&ecirc;n tập động t&aacute;c n&agrave;y 15 nhịp rồi chuyển sang động t&aacute;c kh&aacute;c nh&eacute;.</p>\r\n\r\n<h3><strong>10. Động t&aacute;c Cardio chống đẩy ch&eacute;o tay chạm đầu gối</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/bai-tap-plank-cham-goi-cheo-tay.jpg\" /></p>\r\n\r\n<p>Bạn v&agrave;o tư thế chống đẩy cao, hai ch&acirc;n rộng hơn vai v&agrave; hai tay chống thẳng;</p>\r\n\r\n<p>Sau đ&oacute;, bạn đẩy h&ocirc;ng l&ecirc;n c&agrave;ng cao c&agrave;ng tốt, tiếp tục giữ ch&acirc;n thẳng, tay chống thẳng;</p>\r\n\r\n<p>Kế đến, bạn gập người l&ecirc;n, d&ugrave;ng tay tr&aacute;i đưa ra sau chạm v&agrave;o gối ch&acirc;n phải (hoặc co ch&acirc;n chạm v&agrave;o g&oacute;t ch&acirc;n phải) v&agrave; l&agrave;m ngược lại cho tay phải v&agrave; ch&acirc;n tr&aacute;i.</p>\r\n\r\n<h3><strong>11. Động t&aacute;c duỗi tay v&agrave; ch&acirc;n tập bụng</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/cardio-gap-bung.png\" /></p>\r\n\r\n<p>B&agrave;i tập n&agrave;y sẽ rất tốt trong việc đốt ch&aacute;y mỡ bụng bởi c&aacute;c động t&aacute;c đều t&aacute;c động v&agrave;o v&ugrave;ng trung t&acirc;m.</p>\r\n\r\n<p>C&aacute;ch thực hiện động t&aacute;c n&agrave;y như sau:</p>\r\n\r\n<p>Nằm sấp tr&ecirc;n s&agrave;n, tay v&agrave; ch&acirc;n thẳng.</p>\r\n\r\n<p>Kế đến, bạn duỗi căng tay v&agrave; ch&acirc;n, rồi hất căng cao tay tr&aacute;i v&agrave; ch&acirc;n phải.</p>\r\n\r\n<p>Tay v&agrave; ch&acirc;n c&ograve;n lại vẫn duỗi thẳng kh&ocirc;ng chạm s&agrave;n.</p>\r\n\r\n<p>Đổi b&ecirc;n v&agrave; thực hiện tương tự.</p>\r\n\r\n<p>Bạn thực hiện li&ecirc;n tục trong v&ograve;ng 30&ndash;60 gi&acirc;y.</p>\r\n\r\n<h3><strong>12. Plank cơ bản</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/huong-dan-tap-plank.jpg\" /></p>\r\n\r\n<p>Chống khuỷu tay xuống đất v&agrave; giữ cơ thể như trong h&igrave;nh.</p>\r\n\r\n<p>Lưu &yacute;, từ vai cho đến ch&acirc;n bạn phải nằm tr&ecirc;n một đường thẳng, phần h&ocirc;ng kh&ocirc;ng được đưa l&ecirc;n cao qu&aacute; hoặc thấp qu&aacute;.</p>\r\n\r\n<p>Giữ như vậy 60 gi&acirc;y trước khi chuyển sang động t&aacute;c 2.</p>\r\n\r\n<p>Nếu kh&ocirc;ng giữ được một l&uacute;c 60 gi&acirc;y, h&atilde;y giữ đến l&uacute;c n&agrave;o kh&ocirc;ng chịu được nữa, hạ xuống nghỉ 3-5 gi&acirc;y rồi tiếp tục sao cho tổng thời gian vẫn l&agrave; 60 gi&acirc;y.</p>\r\n\r\n<h3><strong>13. Gập bụng co gối</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/gap-bung-giam-mo.jpg\" /></p>\r\n\r\n<p>Chuẩn bị: Nằm ngửa, lưng kh&ocirc;ng chạm đất m&agrave; tạp với mặt đất/ s&agrave;n g&oacute;c 45 độ C. Đ&acirc;y l&agrave; một trong những b&agrave;i tập thể dục giảm mỡ bụng t&aacute;c động cực lớn v&agrave;o v&ugrave;ng bụng dưới m&agrave; chị em kh&ocirc;ng n&ecirc;n bỏ qua.</p>\r\n\r\n<p>C&aacute;ch thực hiện động t&aacute;c n&agrave;y:</p>\r\n\r\n<p>Bạn để 2 b&agrave;n tay để chạm đất v&agrave; để s&aacute;t v&agrave;o h&ocirc;ng.</p>\r\n\r\n<p>Đưa 2 ch&acirc;n l&ecirc;n cao so với mặt đất, co ch&acirc;n lại v&agrave; gập c&agrave;ng gần ngực c&agrave;ng tốt.</p>\r\n\r\n<p>Thực hiện 10-15 lần rồi chuyển sang động t&aacute;c kh&aacute;c.</p>\r\n\r\n<h3><strong>14. Động t&aacute;c gập bụng duỗi ch&acirc;n</strong></h3>\r\n\r\n<p><strong><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/Seated-Leg-Tuck.jpg\" /></strong></p>\r\n\r\n<p>Bạn ngồi tr&ecirc;n s&agrave;n, co hai ch&acirc;n để đầu gối gần ngực, hai tay mở rộng, chống ở ph&iacute;a sau v&agrave; khuỷu tay cong nhẹ;</p>\r\n\r\n<p>Kế đến, bạn từ từ ngả người ra ph&iacute;a sau, cố gắng giữ vững cơ thể đồng thời duỗi ch&acirc;n thẳng v&agrave; n&acirc;ng ch&acirc;n l&ecirc;n khỏi s&agrave;n;</p>\r\n\r\n<p>Sau đ&oacute; bạn thu ch&acirc;n v&agrave; thực hiện lại động t&aacute;c.</p>\r\n\r\n<h3><strong>15. B&agrave;i tập Cardio bước ch&acirc;n s&aacute;o</strong></h3>\r\n\r\n<p>Đầu ti&ecirc;n, bạn chạy với tốc độ chậm;</p>\r\n\r\n<p>Sau đ&oacute;, một ch&acirc;n bật nhảy l&ecirc;n cao, ch&acirc;n c&ograve;n lại để thẳng v&agrave; giữ im tư thế đ&oacute; đến khi cham đất, bạn nhớ l&agrave; cố gắng bật nhảy cao hết sức c&oacute; thể v&agrave; tiếp đất bằng mũi ch&acirc;n nh&eacute;. Cứ sau một lần bật ch&acirc;n n&agrave;y, bạn lại đổi ch&acirc;n c&ograve;n lại v&agrave; l&agrave;m tương tự như vậy;</p>\r\n\r\n<p>B&agrave;i tập dạng n&agrave;y thường được gọi l&agrave; plyometric (kh&ocirc;ng cần sử dụng đến bất kỳ vật dụng n&agrave;o).</p>\r\n\r\n<h3><strong>16. Động t&aacute;c h&iacute;t đất v&agrave; trườn tới</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"http://images.cfyc.com.vn/cardio/dong-tac-hit-dat-truon-toi.jpg\" /></p>\r\n\r\n<p>Đầu ti&ecirc;n, bạn v&agrave;o vị tr&iacute; chống đẩy..</p>\r\n\r\n<p>Sau đ&oacute;, đẩy h&ocirc;ng l&ecirc;n cao nhất c&oacute; thể, hai tay v&agrave; hai ch&acirc;n chống thẳng, đầu c&uacute;i xuống song song với tay, tạo th&agrave;nh h&igrave;nh chữ V ngược.</p>\r\n\r\n<p>Kế đến, bạn từ từ hạ khuỷu tay xuống nhưng ch&acirc;n vẫn giữ thẳng.</p>\r\n\r\n<p>Bạn cố gắng đẩy phần cơ thể ph&iacute;a tr&ecirc;n v&agrave; trườn người từ dưới thấp l&ecirc;n cao. chống tay thẳng v&agrave; vươn phần tr&ecirc;n cơ thể từ đầu đến v&ugrave;ng bụng về ph&iacute;a mặt trời trong tư thế nằm sấp.</p>\r\n\r\n<h3><strong>17. Động t&aacute;c nhảy xa v&agrave; chạy l&ugrave;i bước nhỏ</strong></h3>\r\n\r\n<p>Đầu ti&ecirc;n, bạn đứng thẳng người, ch&acirc;n rộng bằng h&ocirc;ng.</p>\r\n\r\n<p>Thực hiện động t&aacute;c tạo đ&agrave; giống với động t&aacute;c bật nhảy tại chỗ.</p>\r\n\r\n<p>Bạn h&atilde;y nhảy bật xa, xa nhất c&oacute; thể.</p>\r\n\r\n<p>Sau khi tiếp đất bằng mũi ch&acirc;n, bạn lập tức chạy l&ugrave;i về vị tr&iacute; cũ bằng bước nhỏ.</p>\r\n\r\n<h3><strong>18. Động t&aacute;c nhảy d&acirc;y tay kh&ocirc;ng</strong></h3>\r\n\r\n<p>Bạn thực hiện động t&aacute;c nhảy d&acirc;y như b&igrave;nh thường. Bạn cứ tưởng tượng hai tay vẫn quay d&acirc;y v&agrave; ch&acirc;n chỉ cần nhảy đều nhịp chứ kh&ocirc;ng cần nhảy cao qu&aacute; bạn nh&eacute;.</p>\r\n\r\n<h3><strong>19. Động t&aacute;c bước l&ecirc;n bục</strong></h3>\r\n\r\n<p>Bạn cần một c&aacute;i ghế cao hoặc một c&aacute;i bục cao khoảng 40-50 cm.</p>\r\n\r\n<p>Bạn thực hiện bước 1 ch&acirc;n l&ecirc;n đ&oacute;, sau đ&oacute; bước ch&acirc;n c&ograve;n lại l&ecirc;n theo.</p>\r\n\r\n<p>Ch&uacute; &yacute; cảm nhận cơ m&ocirc;ng của m&igrave;nh khi bước l&ecirc;n xuống bạn nh&eacute;.</p>\r\n\r\n<h3><strong>20. Động t&aacute;c nhảy l&ograve; c&ograve;</strong></h3>\r\n\r\n<p>Động t&aacute;c l&ograve; c&ograve; c&oacute; gợi nhớ tuổi thơ trong bạn hay kh&ocirc;ng? B&acirc;y giờ th&igrave; h&atilde;y &aacute;p dụng để c&ugrave;ng giảm c&acirc;n với động t&aacute;c đơn giản n&agrave;y nh&eacute;.</p>\r\n\r\n<p>C&aacute;ch thực hiện động t&aacute;c n&agrave;y:</p>\r\n\r\n<p>Đứng thẳng người v&agrave; co một ch&acirc;n ra sau.</p>\r\n\r\n<p>Nhảy tới trước từ 3-5 bước.</p>\r\n\r\n<p>Sau đ&oacute;, bạn đổi ch&acirc;n, xoay người v&agrave; nhảy về vị tr&iacute; cũ.</p>\r\n\r\n<h3><strong>21. Động t&aacute;c chống đẩy căn bản</strong></h3>\r\n\r\n<p>Bạn v&agrave;o tư thế chống đẩy, hạ gối xuống v&agrave; co ch&acirc;n l&ecirc;n, hai tay duỗi thẳng. Bạn ch&uacute; &yacute; kh&ocirc;ng để ch&acirc;n chạm đất nh&eacute;, chỉ c&oacute; phần đầu gối l&uacute;c n&agrave;y được chạm đất.</p>\r\n\r\n<p>Kế đến, bạn giữ nguy&ecirc;n tư thế n&agrave;y v&agrave; chống đẩy.</p>\r\n\r\n<p>D&ugrave;ng sức tay hạ người thấp xuống chậm chậm nhưng vẫn giữ thẳng lưng cho đến khi bạn cảm thấy cơ ngực căng ra th&igrave; dừng lại 1 nhịp.</p>\r\n\r\n<p>Sau đ&oacute;, bạn n&acirc;ng người l&ecirc;n ph&iacute;a tr&ecirc;n, trở lại vị tr&iacute; bắt đầu v&agrave; thở ra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với 21 động t&aacute;c Cardio, bạn h&atilde;y thử &aacute;p dụng động t&aacute;c n&agrave;o ph&ugrave; hợp với m&igrave;nh để mang lại hiệu quả cao trong việc luyện tập nh&eacute;.</p>\r\n', 4, 0, 0, 0, NULL, 0, 'user'),
(9, 'vi', 'Street Workout là gì? Hướng dẫn tập Street Workout cơ bản', 'Việc không phải đến phòng Gym cũng giúp bạn tiết kiệm thêm một khoản chi phí nho nhỏ. Số tiền này sẽ giúp bạn mua một bộ quần áo đẹp hàng tháng, hẹn hò với một bạn nữ dễ thương nào đó, hoặc tiến gần hơn đến ước mơ có 1 con siêu xe mà bạn thường hay gặp trong những giấc mơ. :))', 'uploads/images/Street-Workout-là-gì-1.png', '2021-06-02', 3, '<p dir=\"ltr\">Thể dục đường phố hay Street Workout hiện đang l&agrave; xu hướng của giới trẻ. Vậy Street Workout l&agrave; g&igrave;? C&aacute;ch thức tập luyện như thế n&agrave;o cho đ&uacute;ng.... Mời c&aacute;c bạn c&ugrave;ng tham khảo qua b&agrave;i viết sau nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>I. Street workout l&agrave; g&igrave;?</h2>\r\n\r\n<p dir=\"ltr\">Street workout hay c&ograve;n gọi l&agrave; Calisthenics - l&agrave; một m&ocirc;n thể thao ngo&agrave;i trời mang t&iacute;nh mềm dẻo, kh&ocirc;ng sử dụng tạ m&agrave; sử dụng ch&iacute;nh sức nặng cơ thể của bạn th&acirc;n để luyện tập.&nbsp;</p>\r\n\r\n<p dir=\"ltr\">Street workout bắt đầu từ Hi Lạp, nhưng lại l&agrave; tr&agrave;o lưu phổ biến nhất của c&aacute;c bạn trẻ tại Nga. Ngo&agrave;i ra, n&oacute; ph&aacute;t triển mạnh ở Đ&ocirc;ng &Acirc;u v&agrave; Hoa Kỳ, đặc biệt l&agrave; ở th&agrave;nh phố đ&ocirc;ng d&acirc;n, hiện đại bậc nhất New York.</p>\r\n\r\n<p dir=\"ltr\">Hiện tại, Street workout đ&atilde; lan rộng ra to&agrave;n thế giới, v&agrave; hầu hết c&aacute;c bạn trẻ sau khi tiếp x&uacute;c đều rất y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/Street-workout-la%CC%80-gi%CC%80.png\"><img alt=\"Street Workout là gì bg\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/Street-workout-la%CC%80-gi%CC%80.png\" style=\"height:667px; width:1000px\" title=\"Street workout là gì\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>1. Street workout Việt Nam</h3>\r\n\r\n<p dir=\"ltr\">Tại Việt Nam, Street workout xuất hiện khoảng 6 năm trở lại đ&acirc;y. 2 cộng đồng thể thao đường phố lớn nhất phải kể đến như: H&agrave; Nội Street Workout - Calisthenics, SaiGon Calisthenics, Street workout L&agrave;ng Hoa. Ngo&agrave;i ra, c&ograve;n một số c&acirc;u lạc bộ ở c&aacute;c tỉnh như: Đ&agrave; Nẵng, Hải Ph&ograve;ng, Cần Thơ, Nha Trang.</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/the-thao-duong-pho-voi-suc-hut-manh-liet.jpg\"><img alt=\"Thể thao đường phố với sức hút mãnh liệt\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/the-thao-duong-pho-voi-suc-hut-manh-liet.jpg\" style=\"height:294px; width:520px\" title=\"Thể thao đường phố với sức hút mãnh liệt\" /></a></p>\r\n\r\n<p><em>Thể thao đường phố với sức h&uacute;t m&atilde;nh liệt</em></p>\r\n\r\n<p dir=\"ltr\">C&aacute;c bạn thường xuy&ecirc;n bắt gặp c&aacute;c bạn trẻ luyện tập ở c&ocirc;ng vi&ecirc;n, nh&agrave; thi đấu, khu thể thao.., thường bao gồm c&aacute;c b&agrave;i tập như: Pull-up, -Chin-up, Push-up, Dips, Muscle-up v&agrave; Squats.</p>\r\n\r\n<p dir=\"ltr\">Hiện tại, cộng đồng street workout tại Việt Nam đ&atilde; l&ecirc;n tới h&agrave;ng chục ngh&igrave;n người, v&agrave; c&oacute; hơn 1000 bạn luyện tập chuy&ecirc;n nghiệp.&nbsp;</p>\r\n\r\n<h3>2. Lợi &iacute;ch của việc tập luyện Street Workout so với tập Gym</h3>\r\n\r\n<p>Lợi thế đầu ti&ecirc;n phải kể đến l&agrave; kh&ocirc;ng gian tập. Bạn c&oacute; thể tập Calisthenics ở bất kỳ đ&acirc;u, v&igrave; bộ m&ocirc;n n&agrave;y kh&ocirc;ng y&ecirc;u cầu dụng cụ. Bạn c&oacute; thể chọn cho m&igrave;nh một kh&ocirc;ng gian bằng phẳng từ c&ocirc;ng vi&ecirc;n, ở nh&agrave; ri&ecirc;ng, một b&atilde;i đất trống.</p>\r\n\r\n<p dir=\"ltr\">Việc kh&ocirc;ng phải đến ph&ograve;ng Gym cũng gi&uacute;p bạn tiết kiệm th&ecirc;m một khoản chi ph&iacute; nho nhỏ. Số tiền n&agrave;y sẽ gi&uacute;p bạn mua một bộ quần &aacute;o đẹp h&agrave;ng th&aacute;ng, hẹn h&ograve; với một bạn nữ dễ thương n&agrave;o đ&oacute;, hoặc tiến gần hơn đến ước mơ c&oacute; 1 con si&ecirc;u xe m&agrave; bạn thường hay gặp trong những giấc mơ. :))</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/Lo%CC%9B%CC%A3i-i%CC%81ch-cu%CC%89a-vie%CC%A3%CC%82c-ta%CC%A3%CC%82p-luye%CC%A3%CC%82n-Street-Workout-so-vo%CC%9B%CC%81i-ta%CC%A3%CC%82p-Gym.png\"><img alt=\"Lợi ích của việc tập luyện Street Workout so với tập Gym\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/Lo%CC%9B%CC%A3i-i%CC%81ch-cu%CC%89a-vie%CC%A3%CC%82c-ta%CC%A3%CC%82p-luye%CC%A3%CC%82n-Street-Workout-so-vo%CC%9B%CC%81i-ta%CC%A3%CC%82p-Gym.png\" style=\"height:667px; width:1000px\" title=\"Lợi ích của việc tập luyện Street Workout so với tập Gym\" /></a></p>\r\n\r\n<p><em>Lợi ích của việc tập luyện Street Workout so với tập Gym</em></p>\r\n\r\n<p dir=\"ltr\">Hơn nữa, giới trẻ ch&uacute;ng ta hiện tại đều c&oacute; c&ocirc;ng việc bận rộn, bạn kh&ocirc;ng thể sắp xếp được thời gian cố định đến c&aacute;c ph&ograve;ng tập Gym. Kh&ocirc;ng cần phải ng&aacute;n ngẫm mỗi l&uacute;c gặp cảnh tắc đường.&nbsp;</p>\r\n\r\n<p dir=\"ltr\">Cuối c&ugrave;ng, Street workout (Calisthenics) cho bạn rất nhiều thứ từ sự dẻo dai, mạnh mẽ, c&oacute; sức khỏe thật sự chứ kh&ocirc;ng phải chỉ c&oacute; vẻ đẹp cơ bắp.</p>\r\n\r\n<h2>II. C&aacute;c h&igrave;nh thức tập luyện Street Workout</h2>\r\n\r\n<h3>1. Tập luyện để duy tr&igrave; thể lực, sức khỏe cho bản th&acirc;n</h3>\r\n\r\n<p>Với h&igrave;nh thức n&agrave;y, bạn c&oacute; thể tập ở bất kỳ đ&acirc;u. Mục đ&iacute;ch ch&iacute;nh của tập thể thao đường phố l&agrave; gi&uacute;p bạn duy tr&igrave; được sức khỏe, giảm stress sau khi l&agrave;m việc căng thẳng. Ngo&agrave;i ra, nếu luyện tập đều đặn, sẽ gi&uacute;p bạn lu&ocirc;n năng động, tăng th&ecirc;m sức mạnh đối với c&aacute;c hoạt động thể thao ngo&agrave;i trời.</p>\r\n\r\n<p dir=\"ltr\">C&oacute; một số bạn c&oacute; chung đam m&ecirc;, gom lại th&agrave;nh c&aacute;c nh&oacute;m nhỏ c&ugrave;ng nhau tập luyện, c&oacute; th&ecirc;m động lực.</p>\r\n\r\n<h3>2. Tập luyện c&oacute; huấn luyện vi&ecirc;n theo chuy&ecirc;n m&ocirc;n</h3>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/Ta%CC%A3%CC%82p-luye%CC%A3%CC%82n-co%CC%81-hua%CC%82%CC%81n-luye%CC%A3%CC%82n-vie%CC%82n-theo-chuye%CC%82n-mo%CC%82n.jpg\"><img alt=\"Tập luyện có huấn luyện viên theo chuyên môn\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/Ta%CC%A3%CC%82p-luye%CC%A3%CC%82n-co%CC%81-hua%CC%82%CC%81n-luye%CC%A3%CC%82n-vie%CC%82n-theo-chuye%CC%82n-mo%CC%82n.jpg\" style=\"height:667px; width:1000px\" title=\"Tập luyện có huấn luyện viên theo chuyên môn\" /></a></p>\r\n\r\n<p><em>Tập luyện có huấn luyện viên theo chuyên môn</em></p>\r\n\r\n<p dir=\"ltr\">Với h&igrave;nh thức n&agrave;y, đ&ograve;i hỏi bạn phải cố gắng luyện tập từ cơ bản đến n&acirc;ng cao trong thời gian d&agrave;i.&nbsp;</p>\r\n\r\n<p>Những động t&aacute;c chuy&ecirc;n m&ocirc;n thường li&ecirc;n quan đến sức mạnh như: Giữ Planche, Back Lever hay Front Level,&hellip;Ngo&agrave;i ra c&ograve;n c&oacute; nhiều b&agrave;i tập kh&oacute; kh&aacute;c.</p>\r\n\r\n<p>Một động t&aacute;c điển h&igrave;nh trong Street workout được nhiều bạn ấn tượng nhất l&agrave; động t&aacute;c Muscle up - xoay người 360 độ. Kỹ thuật n&agrave;y được đ&aacute;nh gi&aacute; l&agrave; đẹp mắt, g&acirc;y ấn tượng, v&agrave; n&oacute; đ&ograve;i hỏi tốc độ, ch&iacute;nh x&aacute;c v&agrave; độ linh hoạt khi xoay người tr&ecirc;n x&agrave;.</p>\r\n\r\n<h2>III. Lợi &iacute;ch của việc tập Street Workout mang lại</h2>\r\n\r\n<ul>\r\n	<li>Việc tập luyện street Workout thường xuy&ecirc;n sẽ gi&uacute;p khung xương của ch&uacute;ng ta khỏe mạnh hơn, vận động rất nhiều cũng sẽ k&iacute;ch th&iacute;ch việc ph&aacute;t triển cơ bắp.</li>\r\n	<li>Gi&uacute;p tăng cường sức đề kh&aacute;ng. Người tập luyện thể thao đường phố sẽ &iacute;t bị ốm vặt hơn, v&agrave; khả năng phục hồi cũng nhanh hơn.</li>\r\n	<li>Gi&uacute;p giải tỏa căng thẳng, stress sau một ng&agrave;y l&agrave;m việc mệt mỏi. Từ đ&oacute; t&acirc;m trạng sẽ tốt hơn, t&iacute;ch cực hơn để vượt qua những kh&oacute; khăn trong cuộc sống.</li>\r\n	<li>Gi&uacute;p tăng cường sinh lực. C&aacute;c b&agrave;i tập Street Workout c&ograve;n c&oacute; t&aacute;c dụng tăng mức Testosterone v&agrave; ngăn chặn sự suy giảm của Testosterone ở nam giới; tăng cường Estrogen cho nữ giới, từ đ&oacute; cải thiện khả năng t&igrave;nh dục. Tr&ocirc;ng bạn lu&ocirc;n tự tin, quyến rũ v&agrave; thu h&uacute;t hơn đối với người kh&aacute;c ph&aacute;i.</li>\r\n	<li>Street Workout ho&agrave;n to&agrave;n miễn ph&iacute;</li>\r\n</ul>\r\n\r\n<p>V&agrave; c&ograve;n v&ocirc; v&agrave;n lợi &iacute;ch kh&aacute;c m&agrave; chưa thể kh&aacute;m ph&aacute; hết được. Bạn n&ecirc;n thử luyện tập để cảm nhận ngay sự thay đổi của cơ thể.</p>\r\n\r\n<h2>IV. C&aacute;ch tập Street Workout từ cơ bản đến chuy&ecirc;n nghiệp</h2>\r\n\r\n<p>Tương tự như c&aacute;c bộ m&ocirc;n kh&aacute;c, để luyện tập street workout bạn cũng cần phải đầu tư thời gian v&agrave; c&ocirc;ng sức của m&igrave;nh. Đặc biệt l&agrave; phải lu&ocirc;n ki&ecirc;n tr&igrave; v&agrave; kh&ocirc;ng được bỏ cuộc. Đặc biệt phải lu&ocirc;n tu&acirc;n thủ từng động t&aacute;c, tập đ&uacute;ng, tập đủ, kh&ocirc;ng được nhảy c&oacute;c.</p>\r\n\r\n<p dir=\"ltr\">Luyện tập từ cơ bản đến n&acirc;ng cao, v&igrave; tập Street Workout l&agrave; cả một qu&aacute; tr&igrave;nh chứ kh&ocirc;ng phải một, hai ng&agrave;y. Dưới đ&acirc;y l&agrave; c&aacute;c b&agrave;i tập bạn c&oacute; thể tham khảo.</p>\r\n\r\n<h3>1. Chống đẩy - b&agrave;i tập street workout cơ bản</h3>\r\n\r\n<p dir=\"ltr\">Đ&acirc;y l&agrave; b&agrave;i tập cơ bản v&agrave; th&ocirc;ng dụng nhất, hầu hết mọi người đều biết. Chống đẩy c&ograve;n được kết hợp ngay cả trong qu&acirc;n đội v&igrave; đ&acirc;y l&agrave; một b&agrave;i tập r&egrave;n luyện thể lực, x&acirc;y dựng cơ bắp một c&aacute;ch hiệu quả nhất. Khi cơ thể bạn đ&atilde; bắt đầu quen với b&agrave;i tập chống đẩy, bạn h&atilde;y tăng dần số lần thực hiện l&ecirc;n.</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/cho%CC%82%CC%81ng-%C4%91a%CC%82%CC%89y-ba%CC%80i-ta%CC%A3%CC%82p-street-workout-co%CC%9B-ba%CC%89n.png\"><img alt=\"chống đẩy - bài tập street workout cơ bản\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/cho%CC%82%CC%81ng-%C4%91a%CC%82%CC%89y-ba%CC%80i-ta%CC%A3%CC%82p-street-workout-co%CC%9B-ba%CC%89n.png\" style=\"height:667px; width:1000px\" title=\"chống đẩy - bài tập street workout cơ bản\" /></a></p>\r\n\r\n<p><em>Chống đẩy - bài tập street workout cơ bản</em></p>\r\n\r\n<p><em><strong>C&aacute;c bước thực hiện:</strong></em>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Chống hai tay tr&ecirc;n mặt phẳng, lưng v&agrave; cổ giữ thẳng, khiển 2 mũi ch&acirc;n chạm s&agrave;n. Đảm bảo m&ocirc;ng thấp hơn lưng, tư thế chắc chắn.</li>\r\n	<li>Giữ nguy&ecirc;n tư thế, từ từ hạ tay thấp xuống.</li>\r\n	<li>N&acirc;ng tay trở lại tư thế ban đầu.</li>\r\n	<li>Tập từ 15 - 20 lần</li>\r\n</ul>\r\n\r\n<h3>2. B&agrave;i tập Street Workout h&iacute;t x&agrave; đơn</h3>\r\n\r\n<p dir=\"ltr\">B&agrave;i tập n&agrave;y ph&aacute;t triển cơ lưng to&agrave;n diện, kết hợp cơ vai, cơ bụng, cơ tay trước cho bạn th&acirc;n h&igrave;nh chuẩn, body 6 m&uacute;i. N&ecirc;n kết hợp th&ecirc;m với b&agrave;i tập h&iacute;t đất l&agrave; một combo ho&agrave;n hảo.</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/Ba%CC%80i-ta%CC%A3%CC%82p-Street-Workout-hi%CC%81t-xa%CC%80-%C4%91o%CC%9Bn.png\"><img alt=\"Bài tập Street Workout hít xà đơn\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/Ba%CC%80i-ta%CC%A3%CC%82p-Street-Workout-hi%CC%81t-xa%CC%80-%C4%91o%CC%9Bn.png\" style=\"height:777px; width:1000px\" title=\"Bài tập Street Workout hít xà đơn\" /></a></p>\r\n\r\n<p><em>Bài tập Street Workout hít xà đơn</em></p>\r\n\r\n<p><em><strong>C&aacute;c bước thực hiện:</strong></em>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Bạn nắm x&agrave; đơn với l&ograve;ng b&agrave;n tay hướng về th&acirc;n người (c&oacute; thể hướng ra ngo&agrave;i) v&agrave; ch&uacute; &yacute; độ rộng hẹp hơn chiều ngang của vai.</li>\r\n	<li>Ngực hơi ưỡn ra, cơ thể lu&ocirc;n giữ thẳng (nhất l&agrave; cột sống phải thẳng). Nếu bạn giữ lưng thẳng th&igrave; sẽ rất tốt, gi&uacute;p k&iacute;ch th&iacute;ch cơ nhị đầu v&agrave; giảm tới thiểu tối ảnh hưởng lưng.</li>\r\n	<li>Bạn d&ugrave;ng lực từ c&aacute;nh tay v&agrave; vai k&eacute;o cơ thể l&ecirc;n tr&ecirc;n, đến khi đầu ngang tầm với x&agrave;. Thực hiện động t&aacute;c tập trung v&agrave;o cơ nhị đầu để hiệu quả hơn. Giữ khủy tay với th&acirc;n (Ch&uacute; &yacute;: khi l&ecirc;n x&agrave; th&igrave; nhịp thở ra từ từ, phần th&acirc;n tr&ecirc;n cơ thể kh&ocirc;ng n&ecirc;n di chuyển trong khi thực hiện động t&aacute;c, chỉ c&oacute; tay l&agrave; co lại).</li>\r\n	<li>Khi bạn đang ở vị tr&iacute; co lại tr&ecirc;n c&ugrave;ng th&igrave; l&uacute;c n&agrave;y bạn gồng cơ nhị đầu khoảng 3 gi&acirc;y, sau đ&oacute; từ từ hạ th&acirc;n xuống vị tr&iacute; bắt đầu cho đến khi duỗi ra to&agrave;n bộ (Ch&uacute; &yacute;: h&iacute;t v&agrave;o từ từ trong động t&aacute;c n&agrave;y)</li>\r\n</ul>\r\n\r\n<h3>3. H&iacute;t đất, bật người - b&agrave;i tập street workout tại nh&agrave;</h3>\r\n\r\n<p>C&ocirc;ng dụng của b&agrave;i tập n&agrave;y gi&uacute;p c&aacute;c bạn trải mở rộng cơ, gi&uacute;p cơ ngực săn chắc hơn. Đồng thời, h&iacute;t đất sau c&ograve;n t&aacute;c dụng l&ecirc;n phần tay sau v&agrave; vai.</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/Hi%CC%81t-%C4%91a%CC%82%CC%81t-ba%CC%A3%CC%82t-ngu%CC%9Bo%CC%9B%CC%80i-ba%CC%80i-ta%CC%A3%CC%82p-street-workout-ta%CC%A3i-nha%CC%80.png\"><img alt=\"Hít đất, bật người - bài tập street workout tại nhà\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/Hi%CC%81t-%C4%91a%CC%82%CC%81t-ba%CC%A3%CC%82t-ngu%CC%9Bo%CC%9B%CC%80i-ba%CC%80i-ta%CC%A3%CC%82p-street-workout-ta%CC%A3i-nha%CC%80.png\" style=\"height:637px; width:1000px\" title=\"Hít đất, bật người - bài tập street workout tại nhà\" /></a></p>\r\n\r\n<p><em>Hít đất, bật người - bài tập street workout tại nhà</em></p>\r\n\r\n<p><em><strong>C&aacute;c bước thực hiện:</strong></em>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Hai tay chống l&ecirc;n mặt phẳng, ch&acirc;n rộng bằng vai.</li>\r\n	<li>H&iacute;t v&agrave;o, gập khủy tay để hạ thấp cơ thể.</li>\r\n	<li>Khi ngực từ từ chạm xuống gần bằng s&agrave;n, d&ugrave;ng sức mạnh của hai tay bật l&ecirc;n, c&oacute; thể kết hợp với vỗ tay.</li>\r\n	<li>Trở về tư thế l&uacute;c chuẩn bị v&agrave; lặp lại động t&aacute;c.</li>\r\n</ul>\r\n\r\n<h3>4. B&agrave;i tập Street workout chống đẩy với x&agrave; k&eacute;p</h3>\r\n\r\n<p dir=\"ltr\">Chống đẩy với x&agrave; k&eacute;p l&agrave; b&agrave;i tập ph&aacute;t triển cơ tay sau, cơ ngực ph&iacute;a triển. Nếu bạn kết hợp với c&aacute;c b&agrave;i tập trước sẽ c&oacute; một th&acirc;n h&igrave;nh 6 m&uacute;i tuyệt vời.</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/Ba%CC%80i-ta%CC%A3%CC%82p-Street-workout-cho%CC%82%CC%81ng-%C4%91a%CC%82%CC%89y-vo%CC%9B%CC%81i-xa%CC%80-ke%CC%81p.png\"><img alt=\"Bài tập Street workout chống đẩy với xà kép\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/Ba%CC%80i-ta%CC%A3%CC%82p-Street-workout-cho%CC%82%CC%81ng-%C4%91a%CC%82%CC%89y-vo%CC%9B%CC%81i-xa%CC%80-ke%CC%81p.png\" style=\"height:448px; width:1000px\" title=\"Bài tập Street workout chống đẩy với xà kép\" /></a></p>\r\n\r\n<p><em>Bài tập Street workout chống đẩy với xà kép</em></p>\r\n\r\n<p><em><strong>C&aacute;c bước thực hiện:</strong></em>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>2 tay bạn chống l&ecirc;n hai b&ecirc;n của thanh x&agrave; k&eacute;p, sau đ&oacute; h&iacute;t thật s&acirc;u d&ugrave;ng cơ đ&ugrave;i nh&uacute;n mạnh.</li>\r\n	<li>Bạn gồng cơ tay sau, vai, ngực để tiếp tục đẩy th&acirc;n người l&ecirc;n cao sao cho c&aacute;nh tay thẳng, lưng thẳng h&igrave;nh chữ S. M&ocirc;ng đưa ra ph&iacute;a sau, mặt ngẩng l&ecirc;n ph&iacute;a trước, kết hợp với thở ra.</li>\r\n	<li>Tại vị tr&iacute; bắt đầu th&igrave; bạn h&iacute;t s&acirc;u, từ từ hạ thấp xuống nhưng kh&ocirc;ng chồm người về ph&iacute;a trước. Tại vị tr&iacute; thấp nhất th&igrave; c&aacute;nh tay v&agrave; cẳng tay sẽ vu&ocirc;ng g&oacute;c với nhau. Cơ tay sau sẽ được căng tối đa. L&uacute;c n&agrave;y, bạn tập trung điều khiển sức tập trung của cơ tay sau &eacute;p thật mạnh, đẩy người về vị tr&iacute; bắt đầu đồng thời thở ra ( cố gắng hạn chế sự hỗ trợ tối đa của cơ ngực v&agrave; vai)</li>\r\n	<li>Quay trở về tư thế ban đầu v&agrave; lặp lại động t&aacute;c.</li>\r\n</ul>\r\n\r\n<h3>5. Jumping Squat</h3>\r\n\r\n<p>Khi bạn tập b&agrave;i tập đ&uacute;ng c&aacute;ch, c&aacute;c m&uacute;i cơ ở v&ugrave;ng m&ocirc;ng, v&ugrave;ng bụng, cơ bắp,.. của bạn sẽ săn chắc hơn. Gi&uacute;p bạn đốt ch&aacute;y mỡ thừa ở đ&ugrave;i, m&ocirc;ng v&agrave; đồng thời ph&aacute;t triển cơ to&agrave;n diện.</p>\r\n\r\n<p><a href=\"https://mstyle.vn/wp-content/uploads/2019/12/Jumping-Squat.png\"><img alt=\"Jumping Squat\" src=\"https://mstyle.vn/wp-content/uploads/2019/12/Jumping-Squat.png\" style=\"height:928px; width:1000px\" title=\"Jumping Squat\" /></a></p>\r\n\r\n<p><em>Jumping Squat</em></p>\r\n\r\n<p><em><strong>C&aacute;c bước thực hiện:</strong></em>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Đứng thẳng, hai ch&acirc;n dang rộng bằng vai; Hai tay hướng thẳng tr&ecirc;n trần nh&agrave;. Thả lỏng phần bả vai để tr&aacute;nh chấn thương cho v&ugrave;ng vai v&agrave; cổ trong l&uacute;c tập luyện</li>\r\n	<li>H&iacute;t v&agrave;o, duỗi lưng kết hợp với đẩy h&ocirc;ng, m&ocirc;ng về ph&iacute;a sau. Đầu gối mở rộng thẳng h&agrave;ng với mũi ch&acirc;n. Cố gắng để đầu gối kh&ocirc;ng vượt qu&aacute; mũi ch&acirc;n.</li>\r\n	<li>Nhảy bật l&ecirc;n cao, 2 tay đưa ra sau, lưng thẳng.</li>\r\n	<li>Tiếp đất trở về vị tr&iacute; ban đầu v&agrave; lặp lại động t&aacute;c.</li>\r\n</ul>\r\n', 4, 0, 0, 0, NULL, 0, 'user');
INSERT INTO `tin` (`idTin`, `lang`, `TieuDe`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `SoLanXem`, `NoiBat`, `AnHien`, `tags`, `Duyet`, `NguoiDang`) VALUES
(10, 'vi', 'Body Shaming là gì? Khi bị Body Shaming bạn cần làm gì để thoát khỏi nó', 'Bất cứ ai cũng có thể nằm trong vùng “Miệt thị” tuy nhiên đáng chú ý và dễ thấy nhất là những người nổi tiếng hay nói cách khác là những người có sức ảnh hưởng lớn. Họ nhận được nhiều sự quan tâm từ cộng đồng trong đó bao gồm cả những điều tích cực lẫn tiêu cực.', 'uploads/images/body-shaming-la-gi.jpg', '2021-06-02', 3, '<p><em><strong>Bạn hiểu về Body Shaming l&agrave; g&igrave;, những th&ocirc;ng điệp về body shaming cũng như c&aacute;ch vượt qua nỗi sợ body shaiming&hellip; H&atilde;y c&ugrave;ng t&igrave;m hiểu vấn đề n&agrave;y th&ocirc;ng qua b&agrave;i viết chia sẻ dưới đấy nh&eacute;</strong></em></p>\r\n\r\n<p>Ng&agrave;y nay khi m&agrave; internet ng&agrave;y c&agrave;ng ph&aacute;t triển mạnh mẽ k&eacute;o theo đ&oacute; l&agrave; sự lớn mạnh của c&aacute;c trang mạng x&atilde; hội ngo&agrave;i những mặt t&iacute;ch cực m&agrave; mạng x&atilde; hội đem lại như: L&agrave; nơi chia sẻ những t&acirc;m tư cảm x&uacute;c, giao lưu kết bạn, tr&ograve; chuyện&hellip; th&igrave; ẩn sau đ&oacute; l&agrave; những vấn đề kh&ocirc;ng l&agrave;nh mạnh của một nh&oacute;m bộ phận người sử dụng.</p>\r\n\r\n<p>Trong đ&oacute; body shaming người kh&aacute;c l&agrave; một vấn đề điển h&igrave;nh, họ coi đ&acirc;y như một tr&ograve; ti&ecirc;u khiển, niềm vui cho bản th&acirc;n, mặc kệ cho những người bị g&aacute;n gh&eacute;p l&agrave; body shaming ng&agrave;y ng&agrave;y cảm thấy tự ti, &aacute;m ảnh v&agrave; suy sụp tinh thần nghi&ecirc;m trọng. Vậy&nbsp;<strong>body shaming l&agrave; g&igrave;?</strong>&nbsp;H&atilde;y cũng Tackexinh.com đi t&igrave;m hiểu trong b&agrave;i viết chia sẻ dưới đ&acirc;y nh&eacute;</p>\r\n\r\n<h2><strong>Body shaming l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p><strong>Body shaming</strong>&nbsp;được hiểu theo nghĩa tiếng việt l&agrave;&nbsp;<strong>&ldquo;miệt thị ngoại h&igrave;nh&rdquo;</strong>&nbsp; l&agrave; một trong những h&agrave;nh động sử dụng ng&ocirc;n từ nhằm ch&ecirc; bai, chễ giễu về ngoại h&igrave;nh của người kh&aacute;c.</p>\r\n\r\n<p><img alt=\"tìm hiểu về Body shaming là gì\" src=\"https://tackexinh.com/wp-content/uploads/2019/12/tim-hieu-Body-Shaming-la-gi.jpg\" style=\"height:373px; width:620px\" title=\"tìm hiểu về Body shaming là gì\" /></p>\r\n\r\n<p><em>Body shaming l&agrave; g&igrave;? &ndash; Ảnh: Sưu tầm</em></p>\r\n\r\n<p>Những h&agrave;nh động n&agrave;y khiến cho người đ&oacute; cảm thấy tự ti, kh&oacute; chịu hoặc cảm thấy bị x&uacute;c phạm, sự miệt thị n&agrave;y c&ograve;n gieo rắc l&ecirc;n họ những suy nghĩ ti&ecirc;u cực, những lỗi &aacute;m ảnh cũng như suy sụp tinh thần một c&aacute;ch nghi&ecirc;m trọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Body shaming xuất hiện ở đ&acirc;u?</strong></h2>\r\n\r\n<p>Đến đ&acirc;y chắc hẳn bạn đ&atilde; hiểu được&nbsp;<strong>body shaming nghĩa l&agrave; g&igrave;</strong>&nbsp;rồi đ&uacute;ng kh&ocirc;ng? Ng&agrave;y nay khi m&agrave; Internet ph&aacute;t triển một c&aacute;ch b&ugrave;ng nổ, sự kết nối, mạng x&atilde; hội l&agrave; một trong những nơi l&yacute; tưởng để miệt thị người kh&aacute;c. Theo một số khảo s&aacute;t đ&atilde; cho thấy c&oacute; đến hơn 80% những lời miệt thị ngoại h&igrave;nh được xuất hiện v&agrave; diễn ra tr&ecirc;n c&aacute;c trang mạng x&atilde; hội phổ biến (facebook, instagram, Twitter&hellip;). Bởi việc đưa ra những lời miệt thị một c&aacute;ch kh&ocirc;ng trực tiếp n&ecirc;n n&oacute; được thực hiện dễ d&agrave;ng hơn.</p>\r\n\r\n<h2><strong>Những dấu hiệu nhận biết body shaming</strong></h2>\r\n\r\n<p>Body shaming được chia ra hai h&igrave;nh thức ch&iacute;nh l&agrave; chễ giễu người kh&aacute;c v&agrave; tự chễ giễu bản th&acirc;n. V&iacute; dụ như:</p>\r\n\r\n<ul>\r\n	<li><em>Nh&igrave;n c&ocirc; ấy b&eacute;o qu&aacute;</em></li>\r\n	<li><em>Con trai g&igrave; m&agrave; l&ugrave;n qu&aacute; vậy?</em></li>\r\n	<li><em>Nh&igrave;n n&oacute; đ&atilde; l&ugrave;n lại c&ograve;n b&eacute;o nữa chứ?</em></li>\r\n	<li><em>Con g&aacute;i g&igrave; m&agrave; mặt nhiều mụn qu&aacute; vậy?</em></li>\r\n	<li><em>Người g&igrave; m&agrave; gầy qu&aacute; vậy? To&agrave;n da bọc xương!</em></li>\r\n</ul>\r\n\r\n<p>Hay những v&iacute; dụ về tự chế giễu bản th&acirc;n như:</p>\r\n\r\n<ul>\r\n	<li><em>M&igrave;nh cảm thấy m&igrave;nh l&ugrave;n qu&aacute;!</em></li>\r\n	<li><em>&Ocirc;i! Sao m&igrave;nh vừa b&eacute;o, vừa l&ugrave;n như vậy cơ chứ?</em></li>\r\n	<li><em>Mặt m&igrave;nh sao nh&igrave;n mụn qu&aacute; vậy?</em></li>\r\n</ul>\r\n\r\n<h2><strong>Nạn nh&acirc;n của body shaming: Họ l&agrave; ai?</strong></h2>\r\n\r\n<p>Bất cứ ai cũng c&oacute; thể nằm trong v&ugrave;ng &ldquo;Miệt thị&rdquo; tuy nhi&ecirc;n đ&aacute;ng ch&uacute; &yacute; v&agrave; dễ thấy nhất l&agrave; những người nổi tiếng hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; những người c&oacute; sức ảnh hưởng lớn. Họ nhận được nhiều sự quan t&acirc;m từ cộng đồng trong đ&oacute; bao gồm cả những điều t&iacute;ch cực lẫn ti&ecirc;u cực. V&agrave; chắc rằng đ&atilde; c&oacute; rất nhiều những nghệ sĩ đ&atilde; v&agrave; đang phải trải qua những lời miệt thị về ngo&agrave;i h&igrave;nh của m&igrave;nh.</p>\r\n\r\n<p>Kh&ocirc;ng n&oacute;i đ&acirc;u xa tại Việt Nam đ&atilde; từng c&oacute; những nghệ sĩ, ng&ocirc;i sao bị ch&ecirc; bai, chỉ tr&iacute;ch về ngoại h&igrave;nh một c&aacute;ch nặng nề v&agrave; kh&ocirc;ng thương tiếc điển h&igrave;nh như: Sau đ&ecirc;m chung kết Vietnam&rsquo;s Next Top Model 2017 kh&ocirc;ng ai kh&aacute;c Cao Ng&acirc;n lại ch&iacute;nh l&agrave; người nhận được sự quan t&acirc;m nhiều nhất tr&ecirc;n c&aacute;c trang mạng x&atilde; hội. C&ocirc; bị mỉa mai, chế giễu l&agrave; bộ h&agrave;i cốt di động giống như nạn đ&oacute;i năm 1945 bởi th&acirc;n h&igrave;nh gầy g&ograve;, da bọc xương của c&ocirc;. Những ng&ocirc;n từ, từ ngữ nặng nề n&agrave;y lan truyền khắp tr&ecirc;n mạng x&atilde; hội cũng như ngo&agrave;i đời thực suốt v&agrave;i tuần v&agrave; thực sự đặt ra c&acirc;u hỏi về vấn nạn body shaming.</p>\r\n\r\n<h2><strong>C&aacute;c loại body shaming phổ biến hiện nay</strong></h2>\r\n\r\n<p>Sự miệt thị về ngoại h&igrave;nh c&oacute; nhiều loại trong đ&oacute; phải kể&nbsp;<strong>c&aacute;c loại body shaming</strong>&nbsp;phổ biến hiện nay đến như: miệt thị th&acirc;n h&igrave;nh (body-shape), miệt thị về l&agrave;n da, miệt thị về m&agrave; da, height-shaming, shaming for thinness, miệt thị về khu&ocirc;n mặt hay n&agrave;n da tr&ecirc;n khu&ocirc;n mặt vv&hellip; Trong đ&oacute; phổ biến nhất phải kể đến l&agrave;&nbsp;<strong>fat-shaming</strong>&nbsp;hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; miệt thị về c&acirc;n nặng như bị ch&ecirc; bai v&igrave; qu&aacute; mập, qu&aacute; gầy hay b&eacute;o ph&igrave;.</p>\r\n\r\n<p><img alt=\"Những trường hợp body shaming thường thấy\" src=\"https://tackexinh.com/wp-content/uploads/2019/12/truong-hop-body-shaming-pho-bien.jpg\" style=\"height:381px; width:620px\" title=\"Những trường hợp body shaming thường thấy\" /></p>\r\n\r\n<p><em>Những trường hợp body shaming phổ biến &ndash; Ảnh: Sưu tầm</em></p>\r\n\r\n<h3><strong>Face shaming l&agrave; g&igrave;?</strong></h3>\r\n\r\n<p>Ngo&agrave;i ra&nbsp;<strong>face shaming</strong>&nbsp;cũng l&agrave; trường hợp bị ch&ecirc; bai, chễ giễu kh&aacute; nhiều những đặc điểm tr&ecirc;n khu&ocirc;n mặt như xấu, mặt nhiều mụn, da mặt đen vv&hellip;. tạo ra sự mặc cảm, t&igrave;nh trạng suy sụp cho người bị chỉ tr&iacute;ch.</p>\r\n\r\n<h2><strong>Những t&aacute;c hại của body shaming g&acirc;y ra l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>Th&ocirc;ng qua những chia sẻ ở tr&ecirc;n về&nbsp;<strong>body shaming l&agrave; g&igrave;?</strong>&nbsp;th&igrave; chắc hẳn bạn đọc cũng phần n&agrave;o h&igrave;nh dung được những t&aacute;c hại của body shaming g&acirc;y ra rồi đ&uacute;ng kh&ocirc;ng? Người nhận những lời miệt thị đ&oacute; sẽ dễ d&agrave;ng cảm thấy tổn thương, mặc cảm thậm tr&iacute; l&agrave; g&acirc;y ảnh hưởng lớn đến sức khỏe. Họ sẽ t&igrave;m mọi c&aacute;ch để đạt được c&acirc;n nặng, ngoại h&igrave;nh như mong muốn th&ocirc;ng qua việc phải nhịn ăn, uống thuốc&hellip; Cụ thể hơn dưới đ&acirc;y sẽ l&agrave; một số t&aacute;c hại dễ thấy khi bị body shaming.</p>\r\n\r\n<p><ins><ins><ins><iframe frameborder=\"0\" height=\"345\" id=\"aswift_3\" name=\"aswift_3\" scrolling=\"no\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-4636850407669832&amp;output=html&amp;h=280&amp;slotname=5577245642&amp;adk=1994029394&amp;adf=1138725256&amp;pi=t.ma~as.5577245642&amp;w=750&amp;fwrn=4&amp;fwrnh=100&amp;lmt=1622609762&amp;rafmt=1&amp;psa=1&amp;format=750x280&amp;url=https%3A%2F%2Ftackexinh.com%2Fbody-shaming-la-gi%2F&amp;flash=0&amp;fwr=0&amp;fwrattr=true&amp;rpe=1&amp;resp_fmts=3&amp;wgl=1&amp;adsid=ChEI8NjchQYQq8THiaPw3qW9ARI5AMC-8dufME_OFP-ZlGIkSX6ki04asP0MiqppZFmDxmDWq5p2emklBNzdG-RHO3tnWvZyt30qLinG&amp;uach=WyJXaW5kb3dzIiwiMTAuMCIsIng4NiIsIiIsIjkwLjAuNDQzMC4yMTIiLFtdXQ..&amp;dt=1622627145260&amp;bpp=2&amp;bdt=1721&amp;idt=276&amp;shv=r20210525&amp;cbv=%2Fr20190131&amp;ptt=9&amp;saldr=aa&amp;abxe=1&amp;cookie=ID%3Dc2adda91c400646e-22729544c7c80014%3AT%3D1621431608%3ART%3D1621431608%3AS%3DALNI_MbU18Vvn26MBrrAIHiGrCHEm7Q4-A&amp;prev_fmts=0x0%2C728x90%2C750x280%2C300x600%2C345x280%2C300x600%2C1519x666&amp;nras=2&amp;correlator=2936634000912&amp;frm=20&amp;pv=1&amp;ga_vid=1112815435.1621431606&amp;ga_sid=1622627145&amp;ga_hid=1097779582&amp;ga_fc=0&amp;u_tz=420&amp;u_his=1&amp;u_java=0&amp;u_h=864&amp;u_w=1536&amp;u_ah=824&amp;u_aw=1536&amp;u_cd=24&amp;u_nplug=3&amp;u_nmime=4&amp;adx=190&amp;ady=4110&amp;biw=1519&amp;bih=666&amp;scr_x=0&amp;scr_y=1530&amp;oid=3&amp;psts=AGkb-H-YzIFK2Y0OTu3hoBkQarmHs_lCXmJl4gRbfz_m1Z5_4oizpxTFfObL3nt1Bn2eNyRupH0f3gHoNgs%2CAGkb-H8Rz1kxnDTS_-48ve0yrF3iOOk7cqx9v4F91k7vry8wHle2B3emC2jXDc1a9skQCIGx3g34iXqsv35m-w%2CAGkb-H-TtnPfOM9uXGUi08ukxDMKKMT_NyjxhuHJLRDDosHPCJVsSl4TxVDyiu9io2EZtcFuA4VDGU5LtPsXsD4%2CAGkb-H9v-NntZFJOOEIJp4IcPx0rPJRBzrCLghhwrsokKK_B-rBH1jqiK0d2GLpK0sbMIph843Kop3XBzBGXy9A&amp;pvsid=1898334961829480&amp;pem=2&amp;ref=https%3A%2F%2Fwww.google.com%2F&amp;eae=0&amp;fc=1920&amp;brdim=0%2C0%2C0%2C0%2C1536%2C0%2C1536%2C824%2C1536%2C666&amp;vis=1&amp;rsz=%7C%7CEebr%7C&amp;abl=CS&amp;pfx=0&amp;fu=128&amp;bc=31&amp;jar=2021-05-10-15&amp;ifi=4&amp;uci=a!4&amp;btvi=3&amp;fsb=1&amp;xpc=bted6Y2mXF&amp;p=https%3A//tackexinh.com&amp;dtd=1991\" width=\"750\"></iframe></ins></ins></ins></p>\r\n\r\n<ul>\r\n	<li><strong>Cảm thấy bị tự ti:</strong>&nbsp;C&oacute; lẽ đ&acirc;y l&agrave; t&aacute;c hại dễ thấy nhất của người bị miệt thị về ngoại h&igrave;nh, họ kh&ocirc;ng thể gạt bỏ được t&acirc;m trạng bị tự ti, mặc cảm với bản th&acirc;n khi bị người kh&aacute;c chỉ tr&iacute;ch. V&agrave; rồi từ một người vui vẻ, h&ograve;a hồng bỗng ph&uacute;t chốc c&oacute; thể th&agrave;nh một kẻ nh&uacute;t nh&aacute;t v&agrave; n&eacute; tr&aacute;nh những người xung quanh.</li>\r\n	<li><strong>L&agrave;m đẹp một c&aacute;ch bất chấp:</strong>&nbsp;Khi bị chỉ tr&iacute;ch về ngoại h&igrave;nh những người n&agrave;y thường c&oacute; xu hướng &aacute;p dụng những biện ph&aacute;p&nbsp;<a href=\"https://tackexinh.com/lam-dep/\" rel=\"noopener noreferrer\" target=\"_blank\" title=\"kiến thức làm đẹp\">l&agrave;m đẹp</a>&nbsp;kh&ocirc;ng đ&uacute;ng c&aacute;ch hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; phản khoa học chỉ với mục đ&iacute;ch nhanh ch&oacute;ng lấy lại được v&oacute;c d&aacute;ng, c&acirc;n nặng hay l&agrave;n da như mong muốn bất chấp việc những phương ph&aacute;p l&agrave;m đẹp n&agrave;y c&oacute; thể g&acirc;y hại đến sức khỏe.</li>\r\n	<li><strong>Tinh thần bị suy sụp:</strong>&nbsp;Những nạn nh&acirc;n của body shaming sẽ chỉ cảm thấy buồn trong thời gian đầu v&agrave; khi những lời miệt thị n&agrave;y tăng dần sẽ khiến họ bị &aacute;m ảnh, tinh thần suy sụp một c&aacute;ch nghi&ecirc;m trọng.</li>\r\n</ul>\r\n\r\n<h2><strong>N&ecirc;n l&agrave;m g&igrave; khi bị body shaming</strong></h2>\r\n\r\n<p>Những người bị body shaming n&ecirc;n l&agrave;m g&igrave; hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; l&agrave;m thế n&agrave;o để vượt qua body shaming. Dưới đ&acirc;y sẽ l&agrave; một số lời khuy&ecirc;n m&agrave; ch&uacute;ng t&ocirc;i d&agrave;nh cho bạn:</p>\r\n\r\n<h3><strong>#1. Cần nhận thức được về việc &ldquo;kh&ocirc;ng ai l&agrave; ho&agrave;n hảo&rdquo;</strong></h3>\r\n\r\n<p><img alt=\"Làm gì để vượt qua body shaming - Cần nhận thức được về việc &quot;không ai là hoàn hảo&quot;\" src=\"https://tackexinh.com/wp-content/uploads/2019/12/lam-gi-de-vuot-qua-body-shaming-01.jpg\" style=\"height:366px; width:620px\" title=\"Làm gì để vượt qua body shaming - Cần nhận thức được về việc &quot;không ai là hoàn hảo&quot;\" /></p>\r\n\r\n<p><em>Nhận được kh&ocirc;ng ai l&agrave; ho&agrave;n hảo gi&uacute;p bạn vượt qua nỗi sợ mang t&ecirc;n body shaming &ndash; Ảnh: Sưu tầm</em></p>\r\n\r\n<p>Theo một số nghi&ecirc;n cứu đ&atilde; chỉ ra rằng tr&ecirc;n thế giới cứ 2 người lại c&oacute; một người kh&ocirc;ng h&agrave;i l&ograve;ng về ngoại h&igrave;nh của ch&iacute;nh m&igrave;nh, điều n&agrave;y đồng nghĩa với việc sẽ c&oacute; một nửa d&acirc;n số tr&ecirc;n thế giới kh&ocirc;ng hề tự tin với ngoại h&igrave;nh, v&oacute;c d&aacute;ng của m&igrave;nh. Đ&ocirc;i khi ch&iacute;nh những người hay đi ch&ecirc; bai, chế giễu người kh&aacute;c về ngoại h&igrave;nh lại kh&ocirc;ng hề tự tin về v&oacute;c d&aacute;ng của ch&iacute;nh họ.</p>\r\n\r\n<h3><strong>#2. Tự học c&aacute;ch chăm s&oacute;c bản th&acirc;n</strong></h3>\r\n\r\n<p><img alt=\"làm gì để vượt qua body shaming - tự học cách chăm sóc bản thân\" src=\"https://tackexinh.com/wp-content/uploads/2019/12/lam-gi-de-vuot-qua-body-shaming-02.jpg\" style=\"height:389px; width:620px\" title=\"làm gì để vượt qua body shaming - tự học cách chăm sóc bản thân\" /></p>\r\n\r\n<p><em>H&atilde;y tự học c&aacute;ch chăm s&oacute;c tốt cho bản th&acirc;n &ndash; Ảnh: Sưu tầm</em></p>\r\n\r\n<p>Tr&ecirc;n thực tế ch&uacute;ng ta rất kh&oacute; bỏ ngo&agrave;i tai những lời ch&ecirc; bai, chỉ tr&iacute;ch về ngoại h&igrave;nh. Tuy nhi&ecirc;n nếu bạn c&oacute; thể học c&aacute;ch tự chăm s&oacute;c bản th&acirc;n m&igrave;nh tốt hơn, y&ecirc;u thương bản th&acirc;n m&igrave;nh hơn th&igrave; việc tiếp nhận những lời n&oacute;i ti&ecirc;u cực đ&oacute; sẽ nhẹ nh&agrave;ng hơn rất nhiều.</p>\r\n\r\n<h3><strong>#3. H&atilde;y thể hiện r&otilde; cảm gi&aacute;c của bạn</strong></h3>\r\n\r\n<p><img alt=\"Hãy thể hiện rõ cảm giác của bạn khi bị Body shaming\" src=\"https://tackexinh.com/wp-content/uploads/2019/12/no-body-shaming-lam-gi-de-vuot-qua-body-shaming.jpg\" style=\"height:375px; width:620px\" title=\"Hãy thể hiện rõ cảm giác của bạn khi bị Body shaming\" /></p>\r\n\r\n<p><em>Thể hiện r&otilde; cảm gi&aacute;c của m&igrave;nh khi bị&nbsp;body shaming &ndash; Ảnh: Sưu tầm</em></p>\r\n\r\n<p>C&oacute; thể những lời nhận x&eacute;t về ngoại h&igrave;nh của bạn từ những người th&acirc;n hay bạn b&egrave; chỉ l&agrave; những c&acirc;u n&oacute;i đ&ugrave;a, mua vui họ kh&ocirc;ng nghĩ rằng những lời n&oacute;i đ&oacute; lại g&acirc;y n&ecirc;n cảm gi&aacute;c kh&oacute; chịu v&agrave; khiến bạn cảm thấy tồi tệ. L&uacute;c n&agrave;y bạn h&atilde;y n&oacute;i r&otilde; cảm gi&aacute;c của m&igrave;nh cho họ biết, khi đ&oacute; những người thực sự y&ecirc;u thương v&agrave; quan t&acirc;m bạn sẽ hiểu v&agrave; kh&ocirc;ng lặp lại điều đ&oacute; nữa.</p>\r\n\r\n<h2><strong>H&atilde;y dừng body shaming ngay h&ocirc;m nay</strong></h2>\r\n\r\n<p>Một trong những vấn đề cần n&oacute;i ở đ&acirc;y ch&iacute;nh l&agrave; từ những người miệt thị. Liệu c&oacute; hay kh&ocirc;ng một c&aacute;c gọi l&agrave; chuẩn mực của c&aacute;i đẹp trong khi x&atilde; hội đang thay đổi từng ng&agrave;y. V&igrave; sao ch&uacute;ng ta lại đem những thứ kh&ocirc;ng thực tế ấy để giết chết, l&agrave;m hại người kh&aacute;c cả về thể chất lẫn tinh thần.</p>\r\n\r\n<p><img alt=\"Hãy dừng body shaming ngay hôm nay\" src=\"https://tackexinh.com/wp-content/uploads/2019/12/hay-dung-body-shaming.jpg\" style=\"height:472px; width:620px\" title=\"Hãy dừng body shaming ngay hôm nay\" /></p>\r\n\r\n<p><em>H&atilde;y dừng ngay h&agrave;nh động body shaming ngay h&ocirc;m nay &ndash; Ảnh: Internet</em></p>\r\n\r\n<p>Mỗi người khi sinh ra đ&atilde; kh&aacute;c nhau ho&agrave;n to&agrave;n về mọi kh&iacute;a cạnh. Do đ&oacute; đừng d&ugrave;ng những thước đo v&ocirc; căn cứ ấy để l&agrave;m tổn thương nhau. Tất cả mỗi ch&uacute;ng ta đều c&oacute; trong m&igrave;nh những n&eacute;t đẹp ri&ecirc;ng. V&igrave; vậy h&atilde;y giữ g&igrave;n n&oacute; bằng tất cả những t&acirc;m huyết v&agrave; sự tr&acirc;n trọng m&agrave; bản th&acirc;n c&oacute; được.</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"281\" src=\"https://www.youtube.com/embed/oe7YkoOvTd8?feature=oembed\" title=\"NÓI KHÔNG VỚI &quot;BODY SHAMING&quot; - MIỆT THỊ NGOẠI HÌNH BẰNG LỜI NÓI\" width=\"500\"></iframe></p>\r\n\r\n<p><em>N&oacute;i kh&ocirc;ng với &ldquo;Body Shaming&rdquo; &ndash; Nguồn: 4PMusic Official</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Như vậy tr&ecirc;n đ&acirc;y l&agrave; những chia sẻ gi&uacute;p bạn đọc hiểu hơn về&nbsp;<strong>body shaming l&agrave; g&igrave;?</strong>&nbsp;cũng như thực trạng body shaming hiện nay. Những h&agrave;nh động &ldquo;Miệt thị ngoại h&igrave;nh&rdquo; c&oacute; thể g&acirc;y ảnh hưởng xấu đến sức khỏe cũng như t&acirc;m l&yacute; của người bị chỉ tr&iacute;ch do đ&oacute; h&atilde;y c&acirc;n nhắc thật kỹ những lời nhận x&eacute;t, b&igrave;nh luận của m&igrave;nh về ngoại h&igrave;nh của người kh&aacute;c h&atilde;y&nbsp;<strong>Stop body shaming</strong>&nbsp;tr&ecirc;n mạng x&atilde; hội cũng như trong giao tiếp h&agrave;ng ng&agrave;y ngo&agrave;i đời thực nh&eacute;.</p>\r\n', 1, 0, 0, 0, NULL, 0, 'user'),
(11, 'vi', 'Body-shaming, 9 hành vi chế nhạo cơ thể cần ngăn chặn', 'Cơ thể con người là một trong những món quà quý giá nhất mà chúng ta có. Đó là một cơ quan rắc rối, phức tạp và đáng tin cậy, nó phục vụ chúng ta liên tục cả ngày và đêm', 'uploads/images/lam-gi-de-vuot-qua-body-shaming-02.jpg', '2021-06-02', 3, 'Cơ thể con người là một trong những món quà quý giá nhất mà chúng ta có. Đó là một cơ quan rắc rối, phức tạp và đáng tin cậy, nó phục vụ chúng ta liên tục cả ngày và đêm trong suốt cuộc đời mà hầu như không đòi hỏi gì ngược lại. Vì nhiều lý do, chúng ta thường sẵn sàng lắng nghe trí óc hơn là đặt niềm tin vào cơ thể. Chúng ta nghe theo trí óc mà hầu như không hề do dự, dành thời gian lắng nghe giả định, những lời dối trá, và cả những chỉ trích của nó. Tuy nhiên, khi nghĩ về cơ thể, chúng ta dễ dàng để trí óc ra lệnh cho tri giác. Các xác lập mặc định làm chúng ta dễ dàng quên đi rằng suy nghĩ của chúng ta được định hình bởi định kiến và các cấu trúc xã hội. Trí óc của chúng ta ngấu nghiến những quan điểm phi thực tế về cơ thể và còn bảo vệ chúng. Ngược lại, chúng ta luôn nghĩ cơ thể mình không hề thiên vị: Cơ thể của chúng ta chỉ là những gì chúng ta nghĩ nó là (mặc dù sự nhận định này thường bị áp đặt bởi các cấu trúc và quan điểm xã hội. Thông qua trí óc, chúng ta xem cơ thể như thứ gì đó tồn tại chỉ để phục vụ nhu cầu và khước từ chuyện nhìn nhận nó ngoài những định hướng mà trí óc đặt ra. Vấn đề là khi chúng ta nghĩ mình đang nhìn cơ thể mình, thật ra chúng ta chỉ đang nhìn nhận những áp đặt trong đó mà thôi. Cũng giống như nhiều hoạt động khác, là một giáo viên dạy Yoga nhiều năm, tôi hiểu rằng Yoga dạy cho chúng ta cách thoát ra khỏi tâm trí và đi sâu vào cơ thể mình. Bằng cách này, chúng ta gạt những cảm xúc gắn với cơ thể ra khỏi bản thân mình, một trong những cảm xúc đó là sự xấu hổ. Sự liên kết giữa hổ thẹn và cơ thể được bắt nguồn sâu xa từ thói quen phán xét cơ thể, Chúng ta làm điều này cả khi nhìn vào người khác và khi tự nhìn vào cơ thể mình. Sự chế nhạo cơ thể diễn ra dưới nhiều hình thức khác nhau nhưng tất cả đều để lại những hậu quả to lớn. Dẫu biết rằng ai cũng có những khó khăn phải đối mặt trong việc trở nên thoải mái với cơ thể mình, thế nhưng tại sao ta cứ tiếp tục khắc nghiệt như vậy? Tại sao chúng ta cứ tự tạo thêm áp lực cho mình? Đôi khi điều đó chỉ đơn giản rằng việc chế nhạo cơ thể đã trở nên vô thức đến mức khó có thể nhận ra những tình huống gắn với nó. Sau đây là 9 cách chúng ta vô thức chế nhạo cơ thể mình. Một khi những điều này trở nên dễ dàng nhận biết, chúng ta sẽ có thể ngăn chặn, chất vấn và thay đổi những suy nghĩ này. 9 HÀNH VI CHẾ NHẠO CƠ THỂ CẦN NGĂN CHẶN Tập trung vào chế độ ăn uống thay vì dinh dưỡng. Cơ thể chúng ta vận hành như một bộ máy, điều này đồng nghĩa với việc nó cần một lượng chất đốt đều đặn mỗi ngày để chạy hết công suất. Một trong những nguồn năng lượng đó là thức ăn. Vì thực tế này, chế độ dinh dưỡng nên đa dạng, chức nhiều vitamin, chất xơ, chất béo và thậm chí cả đường. Khi chúng ta chạy theo những trào lưu ăn uống như đếm calo hay cắt giảm tinh bột, chúng ta đang hành động theo một mong muốn về ngoại hình nhất định hơn là tập trung hỗ trợ cơ thể hoạt động theo cách chúng ta cần. Từ chối việc hưởng thụ: Nấu một bữa ăn ngon, uống chung một cốc (hoặc chai!) rượu, ăn một bữa tráng miệng thịnh soạn – đây là những cách chúng ta chiều chuộng cơ thể mình. Mặc dù sự lựa chọn này đem lại cho bạn niềm vui, chúng ta đã chấp nhận rằng chiều chuộng bản thân quá mức quá nhiều và quá thường xuyên là một điều xấu. Đã bao nhiêu lần bạn nghe thấy câu “Bạn thật khôn ngoan khi từ chối món tráng miệng này” hay “Sướng miệng hại thân”. Có một điều quan trọng cần nhớ là có nhiều cách lành mạnh để chiều chuộng bản thân (ví dụ như một chút sô cô la phủ hạnh nhân sau bữa tối). Một khía cạnh của việc trở nên lành mạnh là hãy chấp nhận những thú vui này. &nbsp;Tôn thờ những hình mẫu cơ thể đăng trên phương tiện truyền thông. Theo như Tổ chức Renfrew về chứng rối loạn ăn uống, khoảng 5% người Bắc Mỹ có ngoại hình cơ thể giống với hình mẫu được phác họa trên phương tiện truyền thông. Tuy nghe có vẻ sáo rỗng nhưng ngăn chặn việc chế nhạo cơ thể bắt đầu từ nhận thức rằng mọi cơ thể có đủ các kích cỡ và hình dạng, và hầu như không có loại cơ thể nào cụ thể chiếm sóng áp đảo trên các phương tiện truyền thông. &nbsp;Chế nhạo người “da bọc xương” Tự nhiên đã tạo ra nhiều loại cơ thể khác nhau, vì vậy trông chờ vào việc tất cả mọi người trông giống nhau là điều phi thực tế. Có một thực tế bị lờ đi là những người gầy hơn thường bị nhạo báng rằng “da bọc xương”, “ám ảnh về cơ thể” hay “nhìn như chết đói”. Cũng giống như những người khác, họ được quyền lờ đi những phán xét của người khác về cách cơ thể họ vận hành hoặc những gì nó yêu cầu. &nbsp;Phán xét sự đa dạng về dáng người trong phòng tập gym. Tôi từng biết một giáo viên dạy yoga có ngoại hình mập mạp, và cô đã nhắc đến điều này trong mọi lớp học để chúng tôi nhận thức được những áp lực và phán xét cô đã phải chịu trong cộng đồng yoga. Những suy nghĩ như “Nếu bạn to béo hơn, bạn không thể tập yoga” hoặc “Bạn không thể trở thành một giáo viên yoga với một thân hình phì nhiêu được.” Những tư tưởng này ngăn không cho chúng ta nhận ra rằng sự cân đối đến từ nhiều hình dáng, kích cỡ và tập luyện đem lại hiệu quả giống nhau bất kể dáng người. &nbsp;Phán xét người khác vì những hành vi hòa nhập bất chấp những áp lực từ xã hội mà họ phải đối mặt. Áp lực để thích nghi với vẻ đẹp chuẩn mực của xã hội tấn công tất cả chúng ta một cách khó nhận ra. Thật khó để tháo gỡ những gì chúng ta phải làm để đáp ứng chuẩn mực xã hội và từ đó để những thứ tự nhiên khiến chúng ta xinh đẹp hơn. Học cách chấp nhận và yêu thương cơ thể mình có thể là một hành trình rất dài, không phải tự nhiên mà đạt được thành công. Khi chúng ta phán xét người khác vì phẫu thuật thẩm mỹ hoặc đi giày cao gót, chúng ta đang tham gia vào một loại chế nhạo cơ thể. Thậm chí khi phán xét là một sự loại trừ của quan điểm xã hội, nó có thể có kết quả đối lập, dẫn đến một bước lùi hơn là một bước tiến. &nbsp;&nbsp;Phán xét những biểu hiện của xu hướng tình dục. Xu hướng tình dục có thể được thể hiện bằng cách chúng ta chọn biểu lộ cơ thể mình thông qua quần áo hoặc sự di chuyển. Phán xét cách người khác làm điều này, hay không cho một số người quyền thể hiện xu hướng tình dục dựa trên dáng người họ là một loại chế nhạo cơ thể. Biểu lộ xu hướng tình dục là một nhu cầu tự nhiên của tất cả mọi người. Mỗi người đều có quyền được biểu lộ thiên hướng tình dục theo cách tự nhiên nhất mà không bị phán xét vì điều đó. Không hiểu rõ về cơ thể mình. Nếu chúng ta muốn hiểu hành vi chế nhạo cơ thể đã ăn sâu thế nào, chúng ta phải nhìn lại cách mình giao tiếp với cơ thể của mình. Chúng ta đã học cách sợ tấm gương, hoặc thỉnh thoảng cảm thấy không thoải mái khi nhìn hình ảnh bản thân mình lúc khỏa thân. Chúng ta kiềm chế việc dành thời gian khám phá cơ thể mình và từ đó giới hạn sự hiểu biết của bản thân. &nbsp;Định nghĩa vẻ đẹp là ngoại hình thay vì trí tuệ. Khi chúng ta nhận ra mình có nhiều thứ hơn chỉ là cơ thể, chúng ta bắt đầu vứt bỏ những áp lực mình tự đặt lên nó. Cơ thể là một phương tiện mạnh mẽ, nhưng không phải là phương tiện trung gian duy nhất giúp ta trải nghiệm cuộc sống. Vẻ đẹp còn đến từ những yếu tố như sự hài hước, lòng trắc ẩn, lòng vị tha và tính thông minh. *Bài dịch độc quyền tại Tâm lý học ứng dụng CTV Eira&nbsp;', 1, 0, 0, 0, NULL, 0, 'admin'),
(12, 'vi', 'Body Shaming - Đã Đến Lúc Bạn Đối Mặt Với Cảm Giác Tự Ti Ngoại Hình', 'Bạn có bao giờ bị người khác bình phẩm về vóc dáng, làn da hay mái tóc không đẹp của mình? Nếu đã từng thì chắc hẳn Body Shaming không còn là khái niệm xa lạ đối với bạn', 'uploads/images/no-body-shaming-lam-gi-de-vuot-qua-body-shaming.jpg', '2021-06-02', 3, 'Bạn có bao giờ bị người khác bình phẩm về vóc dáng, làn da hay mái tóc không đẹp của mình? Nếu đã từng thì chắc hẳn Body Shaming không còn là khái niệm xa lạ đối với bạn. Những lời nói về ngoại hình của một ai đó, đôi khi chỉ là lời nói đùa nhưng lại có tính sát thương ghê gớm. Vì vậy bạn cần hiểu rõ và đối mặt với sự mặc cảm ngoại hình để những lời nhận xét thiếu thiện ý không ảnh hưởng đến sự tự tin cũng như tinh thần của bạn.Body shaming được dịch đúng nghĩa là “miệt thị ngoại hình”, một hình thức dùng ngôn ngữ để chê bai, chế giễu ngoại hình của người khác. Điều này khiến cho người đó cảm thấy khó chịu hoặc bị xúc phạm. Thậm chí chính bạn cảm thấy tự tin về bản thân mình thì đó cũng chính là một dạng miệt thị.DẤU HIỆU NHẬN BIẾT BODY-SHAMING&nbsp;&nbsp;&nbsp;Body Shaming gồm các hành vi khác nhau bao gồm trực tiếp và gián tiếp chỉ trích ngoại hình, đó có thể là lời nói vu vơ hoặc miệt thị gay gắt. Body Shaming có hai hình thức là chế giễu bản thân hoặc chế giễu người khác.Ví dụ như:“Cô gái ấy xấu quá”“Con trai gì mà lùn quá vậy?”“Đi thi người đẹp gì mà mặt mụn quá trời”“Gầy quá, chẳng đẹp gì mà cũng thi hoa hậu!”.Ngoài ra, chính bạn cũng có thể đưa ra những lời tiêu cực về chính mình như:“Mình béo như con heo”“Mình gầy như bộ xương di động”“Mình cảm thấy không ai xấu xí như mìnhCÁC TRƯỜNG HỢP BODY SHAMING PHỔ BIẾNCó nhiều loại miệt thị cơ thể như miệt thị thân hình, miệt thị làn da, miệt thị màu da… Trường hợp Body Shaming phổ biến có thể kể đến fat-shaming, đó là sự chế giễu vì cân nặng như bị chê mập, béo phì. Điều này dễ gây ra sự mặc cảm, suy sụp, rối loạn ăn uống và thậm chí tăng cân cho người bị chỉ trích.Ngoài ra, những người có thể tạng quá gầy, ốm yếu cũng dễ trở thành đối tượng bị mỉa mai ngoại hình. Vóc dáng là nội dung tiêu biểu trong vấn đề chỉ trích ngoại hình. Hiện nay, tình trạng miệt thị ngoại hình qua mạng xã hội rất phổ biến, mọi người vẫn thường nói người khác quá ốm, quá mập, quá cao, quá thấp trên mạng.Thậm chí, có những trường hợp Body Shaming chỉ đơn giản là “vạch lá tìm sâu”, bạn sẽ bị chỉ trích ở bất cứ khuyết điểm nhỏ nào trên cơ thể, dù nhìn chung bạn là một cô gái rất xinh đẹp.TÁC HẠI CỦA BODY SHAMING&nbsp;Đối với một số người thì những lời nhận xét không hay về ngoại hình người khác đôi khi chỉ nhằm đùa vui. Tuy nhiên, người nhận những lời đó thì sẽ dễ cảm thấy mặc cảm, buồn bã, thậm chí gây ảnh hưởng đến sức khỏe. Họ phải nhịn ăn, uống thuốc, tìm đủ mọi cách để đạt được cân nặng, ngoại hình hoàn hảo hơn.&nbsp;1. Cảm giác tự tiNhìn chung, có rất nhiều người đã không thể nào gạt bỏ tâm trạng tự ti sau khi bị chỉ trích ngoại hình. Họ có thể từ một người vui vẻ, hoạt bát chuyển sang nhút nhát, tránh né người khác.Đặc biệt, các em ở lứa tuổi dậy thì sẽ rất dễ để tâm quá mức vào Body Shaming. Thậm chí đã có người tìm đến cái chết vì không chịu nổi áp lực từ việc bị chê bai ngoại hình.2. Làm đẹp phản khoa họcNgười bị mặc cảm ngoại hình dễ áp dụng các phương pháp kiểm soát cân nặng không lành mạnh. Từ việc cảm thấy tự ti một chút thì sau đó họ có thể dần chuyển sang nhịn ăn, kiêng khem quá đà hoặc dùng đến các loại thuốc gây hại sức khỏe.&nbsp; Chế độ ăn kiêng kham khổ có thể gây nguy hiểm cho sức khỏe, nhất là với thanh thiếu niên – độ tuổi trong quá trình phát triển và thay đổi cơ thể. Đối tượng này dễ rơi vào rối loạn ăn uống như biếng ăn hoặc háu ăn quá mức.3. Suy sụp tinh thầnBan đầu, nạn nhân của Body Shaming chỉ cảm thấy buồn. Sau đó, nếu những lời chỉ trích ngoại hình tăng dần thì họ có thể bị ám ảnh đến mức “chỉ muốn chết đi”. Thực tế, những gì mà người khác nói về ngoại hình của bạn cũng chính là những gì mà bạn có thể dễ dàng cảm nhận được như “béo quá” hay “gầy thế”.Sự thiếu tự tin vốn có cộng thêm những lời chỉ trích sẽ làm gia tăng cảm giác thất vọng về chính cơ thể của người bị Body Shaming. Có nhiều người suy sụp tinh thần đến mức đã dùng đến cách tệ nhất là tự tử.VƯỢT QUA BODY SHAMING&nbsp;Nếu bạn biết được rằng mỗi người đều có quan điểm riêng về cái đẹp thì có lẽ bạn sẽ thấy vui vẻ, tự tin hơn. Chuẩn mực về cái đẹp luôn thay đổi theo thời gian. Ví dụ như ngày xưa da trắng, môi trái tim sẽ được coi là đẹp thì ngày nay có thể da nâu, môi dày mới được xem là hợp mốt.&nbsp;1. Nhận thức được không ai hoàn hảoTheo nghiên cứu thì cứ hai người lại có một người không hài lòng với cơ thể mình, nghĩa là một nửa nhân loại không hề tự tin về ngoại hình. Thực tế thì chính những người hay chỉ trích người khác cũng là những người thường xuyên tự ti về diện mạo của chính họ.Bạn không nên để tâm đến những lời Body Shaming từ bất kỳ ai. Thay vào đó, bạn nên cố gắng tập luyện thể dục và chăm sóc bản thân để tôn lên các nét đẹp riêng của mình.2. Học cách yêu thương bản thânCó lẽ rất khó để bạn có thể bỏ hết ngoài tai những lời nhận xét ngoại hình tiêu cực. Tuy nhiên, nếu bạn học cách yêu thương chính mình thì bạn sẽ dễ tiếp nhận mọi thứ nhẹ nhàng hơn. Dù bạn là người dễ tăng cân hay khó trở nên đầy đặn, điều đó cũng không sao cả, miễn là bạn đã cố gắng để hoàn thiện chính mình. Khi bạn đầu tư chăm sóc cho bản thân thật nhiều thì cũng là lúc sự tự tin và trân trọng bản thân được gia tăng.3. Nói rõ cảm giác của bạnôi khi, những người nhận xét không hay về ngoại hình của bạn chỉ để mua vui. Đối với người thân hay bạn bè thì bạn nên nói rõ cảm giác của bạn. Có thể là họ không biết được những lời đùa giỡn đó sẽ làm bạn cảm thấy tệ hại về ngoại hình.Bạn hãy nói rõ rằng bạn cảm thấy không vui khi nghe những lời chế giễu ngoại hình. Khi ấy, những người thực sự yêu thương bạn sẽ hiểu và không lặp lại điều đó nữa.Body Shaming ngày càng phổ biến và có thể gây nguy hiểm đến sức khỏe lẫn tâm lý cho những người có tính cách nhạy cảm và tự ti. Bất kì ai cũng có thể trở thành nạn nhân của Body Shaming. Vì vậy, bạn cần suy nghĩ kỹ trước khi bình luận về ngoại hình người khác. Ngoài ra, chính bạn hãy thật mạnh mẽ đối mặt với mặc cảm ngoại hình, đừng để Body Shaming khiến bạn tổn thương nhé.-----------------------------------Nguồn:&nbsp; website Hellobacsi&nbsp;(*) Tâm Lý Học Tuổi Trẻ do YBOX.VN sáng lập với mục tiêu chia sẻ và góp phần nâng cao kiến thức tâm lý cho những người trẻ, chúng tôi mong nhận được sự ủng hộ và đóng góp của các bạn. Trở thành Cộng tác viên hoặc thực tập sinh để rèn luyện ngoại ngữ và đóng góp tri thức chuyên sâu về tâm lý cho cộng đồng tại đây&nbsp; &nbsp;https://bom.to/AxK6nj&nbsp;', 1, 0, 0, 0, NULL, 0, 'admin');

